INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28365, 'nvi', '1 Coríntios', '1co', 1, 1, 'Paulo, chamado para ser apóstolo de Cristo Jesus pela vontade de Deus, e o irmão Sóstenes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28366, 'nvi', '1 Coríntios', '1co', 1, 2, 'à igreja de Deus que está em Corinto, aos santificados em Cristo Jesus e chamados para serem santos, juntamente com todos os que, em toda parte, invocam o nome de nosso Senhor Jesus Cristo, Senhor deles e nosso:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28367, 'nvi', '1 Coríntios', '1co', 1, 3, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28368, 'nvi', '1 Coríntios', '1co', 1, 4, 'Sempre dou graças a meu Deus por vocês, por causa da graça que lhes foi dada por ele em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28369, 'nvi', '1 Coríntios', '1co', 1, 5, 'Pois nele vocês foram enriquecidos em tudo, em toda palavra e em todo conhecimento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28370, 'nvi', '1 Coríntios', '1co', 1, 6, 'porque o testemunho de Cristo foi confirmado entre vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28371, 'nvi', '1 Coríntios', '1co', 1, 7, 'de modo que não lhes falta nenhum dom espiritual, enquanto vocês aguardam que o nosso Senhor Jesus Cristo seja revelado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28372, 'nvi', '1 Coríntios', '1co', 1, 8, 'Ele os manterá firmes até o fim, de modo que vocês serão irrepreensíveis no dia de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28373, 'nvi', '1 Coríntios', '1co', 1, 9, 'Fiel é Deus, o qual os chamou à comunhão com seu Filho Jesus Cristo, nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28374, 'nvi', '1 Coríntios', '1co', 1, 10, 'Irmãos, em nome de nosso Senhor Jesus Cristo suplico a todos vocês que concordem uns com os outros no que falam, para que não haja divisões entre vocês, e, sim, que todos estejam unidos num só pensamento e num só parecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28375, 'nvi', '1 Coríntios', '1co', 1, 11, 'Meus irmãos, fui informado por alguns da casa de Cloe de que há divisões entre vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28376, 'nvi', '1 Coríntios', '1co', 1, 12, 'Com isso quero dizer que cada um de vocês afirma: "Eu sou de Paulo"; "eu de Apolo"; "eu de Pedro"; e "eu de Cristo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28377, 'nvi', '1 Coríntios', '1co', 1, 13, 'Acaso Cristo está dividido? Foi Paulo crucificado em favor de vocês? Foram vocês batizados em nome de Paulo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28378, 'nvi', '1 Coríntios', '1co', 1, 14, 'Dou graças a Deus por não ter batizado nenhum de vocês, exceto Crispo e Gaio;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28379, 'nvi', '1 Coríntios', '1co', 1, 15, 'de modo que ninguém pode dizer que foi batizado em meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28380, 'nvi', '1 Coríntios', '1co', 1, 16, '( Batizei também os da casa de Estéfanas; além destes, não me lembro se batizei alguém mais. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28381, 'nvi', '1 Coríntios', '1co', 1, 17, 'Pois Cristo não me enviou para batizar, mas para pregar o evangelho, não com palavras de sabedoria humana, para que a cruz de Cristo não seja esvaziada. Cristo, Sabedoria e Poder de Deus');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28382, 'nvi', '1 Coríntios', '1co', 1, 18, 'Pois a mensagem da cruz é loucura para os que estão perecendo, mas para nós, que estamos sendo salvos, é o poder de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28383, 'nvi', '1 Coríntios', '1co', 1, 19, 'Pois está escrito: "Destruirei a sabedoria dos sábios e rejeitarei a inteligência dos inteligentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28384, 'nvi', '1 Coríntios', '1co', 1, 20, 'Onde está o sábio? Onde está o erudito? Onde está o questionador desta era? Acaso não tornou Deus louca a sabedoria deste mundo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28385, 'nvi', '1 Coríntios', '1co', 1, 21, 'Visto que, na sabedoria de Deus, o mundo não o conheceu por meio da sabedoria humana, agradou a Deus salvar aqueles que crêem por meio da loucura da pregação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28386, 'nvi', '1 Coríntios', '1co', 1, 22, 'Os judeus pedem sinais miraculosos, e os gregos procuram sabedoria;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28387, 'nvi', '1 Coríntios', '1co', 1, 23, 'nós, porém, pregamos a Cristo crucificado, o qual, de fato, é escândalo para os judeus e loucura para os gentios');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28388, 'nvi', '1 Coríntios', '1co', 1, 24, 'mas para os que foram chamados, tanto judeus como gregos, Cristo é o poder de Deus e a sabedoria de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28389, 'nvi', '1 Coríntios', '1co', 1, 25, 'Porque a loucura de Deus é mais sábia que a sabedoria humana, e a fraqueza de Deus é mais forte que a força do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28390, 'nvi', '1 Coríntios', '1co', 1, 26, 'Irmãos, pensem no que vocês eram quando foram chamados. Poucos eram sábios segundo os padrões humanos; poucos eram poderosos; poucos eram de nobre nascimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28391, 'nvi', '1 Coríntios', '1co', 1, 27, 'Mas Deus escolheu as coisas loucas do mundo para envergonhar os sábios, e escolheu as coisas fracas do mundo para envergonhar as fortes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28392, 'nvi', '1 Coríntios', '1co', 1, 28, 'Ele escolheu as coisas insignificantes do mundo, as desprezadas e as que nada são, para reduzir a nada as que são,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28393, 'nvi', '1 Coríntios', '1co', 1, 29, 'para que ninguém se vanglorie diante dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28394, 'nvi', '1 Coríntios', '1co', 1, 30, 'É, porém, por iniciativa dele que vocês estão em Cristo Jesus, o qual se tornou sabedoria de Deus para nós, isto é, justiça, santidade e redenção,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28395, 'nvi', '1 Coríntios', '1co', 1, 31, 'para que, como está escrito: "Quem se gloriar, glorie-se no Senhor".');
