INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28569, 'nvi', '1 Coríntios', '1co', 10, 1, 'Porque não quero, irmãos, que vocês ignorem o fato de que todos os nossos antepassados estiveram sob a nuvem e todos passaram pelo mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28570, 'nvi', '1 Coríntios', '1co', 10, 2, 'Em Moisés, todos eles foram batizados na nuvem e no mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28571, 'nvi', '1 Coríntios', '1co', 10, 3, 'Todos comeram do mesmo alimento espiritual');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28572, 'nvi', '1 Coríntios', '1co', 10, 4, 'e beberam da mesma bebida espiritual; pois bebiam da rocha espiritual que os acompanhava, e essa rocha era Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28573, 'nvi', '1 Coríntios', '1co', 10, 5, 'Contudo, Deus não se agradou da maioria deles; por isso os seus corpos ficaram espalhados no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28574, 'nvi', '1 Coríntios', '1co', 10, 6, 'Essas coisas ocorreram como exemplos para nós, para que não cobicemos coisas más, como eles fizeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28575, 'nvi', '1 Coríntios', '1co', 10, 7, 'Não sejam idólatras, como alguns deles foram, conforme está escrito: "O povo se assentou para comer e beber, e levantou-se para se entregar à farra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28576, 'nvi', '1 Coríntios', '1co', 10, 8, 'Não pratiquemos imoralidade, como alguns deles fizeram — e num só dia morreram vinte e três mil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28577, 'nvi', '1 Coríntios', '1co', 10, 9, 'Não devemos pôr o Senhor à prova, como alguns deles fizeram — e foram mortos por serpentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28578, 'nvi', '1 Coríntios', '1co', 10, 10, 'E não se queixem, como alguns deles se queixaram — e foram mortos pelo anjo destruidor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28579, 'nvi', '1 Coríntios', '1co', 10, 11, 'Essas coisas aconteceram a eles como exemplos e foram escritas como advertência para nós, sobre quem tem chegado o fim dos tempos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28580, 'nvi', '1 Coríntios', '1co', 10, 12, 'Assim, aquele que julga estar firme, cuide-se para que não caia!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28581, 'nvi', '1 Coríntios', '1co', 10, 13, 'Não sobreveio a vocês tentação que não fosse comum aos homens. E Deus é fiel; ele não permitirá que vocês sejam tentados além do que podem suportar. Mas, quando forem tentados, ele lhes providenciará um escape, para que o possam suportar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28582, 'nvi', '1 Coríntios', '1co', 10, 14, 'Por isso, meus amados irmãos, fujam da idolatria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28583, 'nvi', '1 Coríntios', '1co', 10, 15, 'Estou falando a pessoas sensatas; julguem vocês mesmos o que estou dizendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28584, 'nvi', '1 Coríntios', '1co', 10, 16, 'Não é verdade que o cálice da bênção que abençoamos é uma participação no sangue de Cristo, e que o pão que partimos é uma participação no corpo de Cristo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28585, 'nvi', '1 Coríntios', '1co', 10, 17, 'Por haver um único pão, nós, que somos muitos, somos um só corpo, pois todos participamos de um único pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28586, 'nvi', '1 Coríntios', '1co', 10, 18, 'Considerem o povo de Israel: os que comem dos sacrifícios não participam do altar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28587, 'nvi', '1 Coríntios', '1co', 10, 19, 'Portanto, que estou querendo dizer? Será que o sacrifício oferecido a um ídolo é alguma coisa? Ou o ídolo é alguma coisa?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28588, 'nvi', '1 Coríntios', '1co', 10, 20, 'Não! Quero dizer que o que os pagãos sacrificam é oferecido aos demônios e não a Deus, e não quero que vocês tenham comunhão com os demônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28589, 'nvi', '1 Coríntios', '1co', 10, 21, 'Vocês não podem beber do cálice do Senhor e do cálice dos demônios; não podem participar da mesa do Senhor e da mesa dos demônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28590, 'nvi', '1 Coríntios', '1co', 10, 22, 'Porventura provocaremos o ciúme do Senhor? Somos mais fortes do que ele?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28591, 'nvi', '1 Coríntios', '1co', 10, 23, '"Tudo é permitido", mas nem tudo convém. "Tudo é permitido", mas nem tudo edifica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28592, 'nvi', '1 Coríntios', '1co', 10, 24, 'Ninguém deve buscar o seu próprio bem, mas sim o dos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28593, 'nvi', '1 Coríntios', '1co', 10, 25, 'Comam de tudo o que se vende no mercado, sem fazer perguntas por causa da consciência,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28594, 'nvi', '1 Coríntios', '1co', 10, 26, 'pois "do Senhor é a terra e tudo o que nela existe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28595, 'nvi', '1 Coríntios', '1co', 10, 27, 'Se algum descrente o convidar para uma refeição e você quiser ir, coma de tudo o que lhe for apresentado, sem nada perguntar por causa da consciência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28596, 'nvi', '1 Coríntios', '1co', 10, 28, 'Mas se alguém lhe disser: "Isto foi oferecido em sacrifício", não coma, tanto por causa da pessoa que o comentou, como da consciência,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28597, 'nvi', '1 Coríntios', '1co', 10, 29, 'isto é, da consciência do outro e não da sua própria. Pois, por que minha liberdade deve ser julgada pela consciência dos outros?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28598, 'nvi', '1 Coríntios', '1co', 10, 30, 'Se participo da refeição com ação de graças, por que sou condenado por algo pelo qual dou graças a Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28599, 'nvi', '1 Coríntios', '1co', 10, 31, 'Assim, quer vocês comam, bebam ou façam qualquer outra coisa, façam tudo para a glória de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28600, 'nvi', '1 Coríntios', '1co', 10, 32, 'Não se tornem motivo de tropeço, nem para judeus, nem para gregos, nem para a igreja de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28601, 'nvi', '1 Coríntios', '1co', 10, 33, 'Também eu procuro agradar a todos de todas as formas. Porque não estou procurando o meu próprio bem, mas o bem de muitos, para que sejam salvos.');
