INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28602, 'nvi', '1 Coríntios', '1co', 11, 1, 'Tornem-se meus imitadores, como eu o sou de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28603, 'nvi', '1 Coríntios', '1co', 11, 2, 'Eu os elogio por se lembrarem de mim em tudo e por se apegarem às tradições, exatamente como eu as transmiti a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28604, 'nvi', '1 Coríntios', '1co', 11, 3, 'Quero, porém, que entendam que o cabeça de todo homem é Cristo, e o cabeça da mulher é o homem, e o cabeça de Cristo é Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28605, 'nvi', '1 Coríntios', '1co', 11, 4, 'Todo homem que ora ou profetiza com a cabeça coberta desonra a sua cabeça;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28606, 'nvi', '1 Coríntios', '1co', 11, 5, 'e toda mulher que ora ou profetiza com a cabeça descoberta desonra a sua cabeça; pois é como se a tivesse rapada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28607, 'nvi', '1 Coríntios', '1co', 11, 6, 'Se a mulher não cobre a cabeça, deve também cortar o cabelo; se, porém, é vergonhoso para a mulher ter o cabelo cortado ou rapado, ela deve cobrir a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28608, 'nvi', '1 Coríntios', '1co', 11, 7, 'O homem não deve cobrir a cabeça, visto que ele é imagem e glória de Deus; mas a mulher é glória do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28609, 'nvi', '1 Coríntios', '1co', 11, 8, 'Pois o homem não se originou da mulher, mas a mulher do homem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28610, 'nvi', '1 Coríntios', '1co', 11, 9, 'além disso, o homem não foi criado por causa da mulher, mas a mulher por causa do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28611, 'nvi', '1 Coríntios', '1co', 11, 10, 'Por essa razão e por causa dos anjos, a mulher deve ter sobre a cabeça um sinal de autoridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28612, 'nvi', '1 Coríntios', '1co', 11, 11, 'No Senhor, todavia, a mulher não é independente do homem, nem o homem independente da mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28613, 'nvi', '1 Coríntios', '1co', 11, 12, 'Pois, assim como a mulher proveio do homem, também o homem nasce da mulher. Mas tudo provém de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28614, 'nvi', '1 Coríntios', '1co', 11, 13, 'Julguem entre vocês mesmos: é apropriado a uma mulher orar a Deus com a cabeça descoberta?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28615, 'nvi', '1 Coríntios', '1co', 11, 14, 'A própria natureza das coisas não lhes ensina que é uma desonra para o homem ter cabelo comprido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28616, 'nvi', '1 Coríntios', '1co', 11, 15, 'e que o cabelo comprido é uma glória para a mulher? Pois o cabelo comprido foi lhe dado como manto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28617, 'nvi', '1 Coríntios', '1co', 11, 16, 'Mas se alguém quiser fazer polêmica a esse respeito, nós não temos esse costume, nem as igrejas de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28618, 'nvi', '1 Coríntios', '1co', 11, 17, 'Entretanto, nisto que lhes vou dizer não os elogio, pois as reuniões de vocês mais fazem mal do que bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28619, 'nvi', '1 Coríntios', '1co', 11, 18, 'Em primeiro lugar, ouço que, quando vocês se reúnem como igreja, há divisões entre vocês, e até certo ponto eu o creio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28620, 'nvi', '1 Coríntios', '1co', 11, 19, 'Pois é necessário que haja divergências entre vocês, para que sejam conhecidos quais dentre vocês são aprovados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28621, 'nvi', '1 Coríntios', '1co', 11, 20, 'Quando vocês se reúnem, não é para comer a ceia do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28622, 'nvi', '1 Coríntios', '1co', 11, 21, 'porque cada um come sua própria ceia sem esperar pelos outros. Assim, enquanto um fica com fome, outro se embriaga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28623, 'nvi', '1 Coríntios', '1co', 11, 22, 'Será que vocês não têm casa onde comer e beber? Ou desprezam a igreja de Deus e humilham os que nada têm? Que lhes direi? Eu os elogiarei por isso? Certamente que não!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28624, 'nvi', '1 Coríntios', '1co', 11, 23, 'Pois recebi do Senhor o que também lhes entreguei: que o Senhor Jesus, na noite em que foi traído, tomou o pão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28625, 'nvi', '1 Coríntios', '1co', 11, 24, 'e, tendo dado graças, partiu-o e disse: "Isto é o meu corpo, que é dado em favor de vocês; façam isto em memória de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28626, 'nvi', '1 Coríntios', '1co', 11, 25, 'Da mesma forma, depois da ceia ele tomou o cálice e disse: "Este cálice é a nova aliança no meu sangue; façam isto, sempre que o beberem, em memória de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28627, 'nvi', '1 Coríntios', '1co', 11, 26, 'Porque, sempre que comerem deste pão e beberem deste cálice, vocês anunciam a morte do Senhor até que ele venha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28628, 'nvi', '1 Coríntios', '1co', 11, 27, 'Portanto, todo aquele que comer o pão ou beber o cálice do Senhor indignamente será culpado de pecar contra o corpo e o sangue do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28629, 'nvi', '1 Coríntios', '1co', 11, 28, 'Examine-se o homem a si mesmo, e então coma do pão e beba do cálice.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28630, 'nvi', '1 Coríntios', '1co', 11, 29, 'Pois quem come e bebe sem discernir o corpo do Senhor, come e bebe para sua própria condenação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28631, 'nvi', '1 Coríntios', '1co', 11, 30, 'Por isso há entre vocês muitos fracos e doentes, e vários já dormiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28632, 'nvi', '1 Coríntios', '1co', 11, 31, 'Mas, se nós nos examinássemos a nós mesmos, não receberíamos juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28633, 'nvi', '1 Coríntios', '1co', 11, 32, 'Quando, porém, somos julgados pelo Senhor, estamos sendo disciplinados para que não sejamos condenados com o mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28634, 'nvi', '1 Coríntios', '1co', 11, 33, 'Portanto, meus irmãos, quando vocês se reunirem para comer, esperem uns pelos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28635, 'nvi', '1 Coríntios', '1co', 11, 34, 'Se alguém estiver com fome, coma em casa, para que, quando vocês se reunirem, isso não resulte em condenação. Quanto ao mais, quando eu for lhes darei instruções.');
