INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28636, 'nvi', '1 Coríntios', '1co', 12, 1, 'Irmãos, quanto aos dons espirituais, não quero que vocês sejam ignorantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28637, 'nvi', '1 Coríntios', '1co', 12, 2, 'Vocês sabem que, quando eram pagãos, de uma forma ou de outra eram fortemente atraídos e levados para os ídolos mudos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28638, 'nvi', '1 Coríntios', '1co', 12, 3, 'Por isso, eu lhes afirmo que ninguém que fala pelo Espírito de Deus diz: "Jesus seja amaldiçoado"; e ninguém pode dizer: "Jesus é Senhor", a não ser pelo Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28639, 'nvi', '1 Coríntios', '1co', 12, 4, 'Há diferentes tipos de dons, mas o Espírito é o mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28640, 'nvi', '1 Coríntios', '1co', 12, 5, 'Há diferentes tipos de ministérios, mas o Senhor é o mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28641, 'nvi', '1 Coríntios', '1co', 12, 6, 'Há diferentes formas de atuação, mas é o mesmo Deus quem efetua tudo em todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28642, 'nvi', '1 Coríntios', '1co', 12, 7, 'A cada um, porém, é dada a manifestação do Espírito, visando ao bem comum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28643, 'nvi', '1 Coríntios', '1co', 12, 8, 'Pelo Espírito, a um é dada a palavra de sabedoria; a outro, a palavra de conhecimento, pelo mesmo Espírito;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28644, 'nvi', '1 Coríntios', '1co', 12, 9, 'a outro, fé, pelo mesmo Espírito; a outro, dons de cura, pelo único Espírito;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28645, 'nvi', '1 Coríntios', '1co', 12, 10, 'a outro, poder para operar milagres; a outro, profecia; a outro, discernimento de espíritos; a outro, variedade de línguas; e ainda a outro, interpretação de línguas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28646, 'nvi', '1 Coríntios', '1co', 12, 11, 'Todas essas coisas, porém, são realizadas pelo mesmo e único Espírito, e ele as distribui individualmente, a cada um, conforme quer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28647, 'nvi', '1 Coríntios', '1co', 12, 12, 'Ora, assim como o corpo é uma unidade, embora tenha muitos membros, e todos os membros, mesmo sendo muitos, formam um só corpo, assim também com respeito a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28648, 'nvi', '1 Coríntios', '1co', 12, 13, 'Pois em um só corpo todos nós fomos batizados em um único Espírito: quer judeus, quer gregos, quer escravos, quer livres. E a todos nós foi dado beber de um único Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28649, 'nvi', '1 Coríntios', '1co', 12, 14, 'O corpo não é composto de um só membro, mas de muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28650, 'nvi', '1 Coríntios', '1co', 12, 15, 'Se o pé disser: "Porque não sou mão, não pertenço ao corpo", nem por isso deixa de fazer parte do corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28651, 'nvi', '1 Coríntios', '1co', 12, 16, 'E se o ouvido disser: "Porque não sou olho, não pertenço ao corpo", nem por isso deixa de fazer parte do corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28652, 'nvi', '1 Coríntios', '1co', 12, 17, 'Se todo o corpo fosse olho, onde estaria a audição? Se todo o corpo fosse ouvido, onde estaria o olfato?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28653, 'nvi', '1 Coríntios', '1co', 12, 18, 'De fato, Deus dispôs cada um dos membros no corpo, segundo a sua vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28654, 'nvi', '1 Coríntios', '1co', 12, 19, 'Se todos fossem um só membro, onde estaria o corpo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28655, 'nvi', '1 Coríntios', '1co', 12, 20, 'Assim, há muitos membros, mas um só corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28656, 'nvi', '1 Coríntios', '1co', 12, 21, 'O olho não pode dizer à mão: "Não preciso de você! " Nem a cabeça pode dizer aos pés: "Não preciso de vocês! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28657, 'nvi', '1 Coríntios', '1co', 12, 22, 'Pelo contrário, os membros do corpo que parecem mais fracos são indispensáveis,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28658, 'nvi', '1 Coríntios', '1co', 12, 23, 'e os membros que pensamos serem menos honrosos, tratamos com especial honra. E os membros que em nós são indecorosos são tratados com decoro especial,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28659, 'nvi', '1 Coríntios', '1co', 12, 24, 'enquanto os que em nós são decorosos não precisam ser tratados de maneira especial. Mas Deus estruturou o corpo dando maior honra aos membros que dela tinham falta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28660, 'nvi', '1 Coríntios', '1co', 12, 25, 'a fim de que não haja divisão no corpo, mas, sim, que todos os membros tenham igual cuidado uns pelos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28661, 'nvi', '1 Coríntios', '1co', 12, 26, 'Quando um membro sofre, todos os outros sofrem com ele; quando um membro é honrado, todos os outros se alegram com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28662, 'nvi', '1 Coríntios', '1co', 12, 27, 'Ora, vocês são o corpo de Cristo, e cada um de vocês, individualmente, é membro desse corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28663, 'nvi', '1 Coríntios', '1co', 12, 28, 'Assim, na igreja, Deus estabeleceu primeiramente apóstolos; em segundo lugar, profetas; em terceiro lugar, mestres; depois os que realizam milagres, os que têm dom de curar, os que têm dom de prestar ajuda, os que têm dons de administração e os que falam diversas línguas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28664, 'nvi', '1 Coríntios', '1co', 12, 29, 'São todos apóstolos? São todos profetas? São todos mestres? Têm todos o dom de realizar milagres?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28665, 'nvi', '1 Coríntios', '1co', 12, 30, 'Têm todos dons de curar? Falam todos em línguas? Todos interpretam?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28666, 'nvi', '1 Coríntios', '1co', 12, 31, 'Entretanto, busquem com dedicação os melhores dons. Passo agora a mostrar-lhes um caminho ainda mais excelente.');
