INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28680, 'nvi', '1 Coríntios', '1co', 14, 1, 'Sigam o caminho do amor e busquem com dedicação os dons espirituais, principalmente o dom de profecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28681, 'nvi', '1 Coríntios', '1co', 14, 2, 'Pois quem fala em língua não fala aos homens, mas a Deus. De fato, ninguém o entende; em espírito fala mistérios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28682, 'nvi', '1 Coríntios', '1co', 14, 3, 'Mas quem profetiza o faz para a edificação, encorajamento e consolação dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28683, 'nvi', '1 Coríntios', '1co', 14, 4, 'Quem fala em língua a si mesmo se edifica, mas quem profetiza edifica a igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28684, 'nvi', '1 Coríntios', '1co', 14, 5, 'Gostaria que todos vocês falassem em línguas, mas prefiro que profetizem. Quem profetiza é maior do que aquele que fala em línguas, a não ser que as interprete, para que a igreja seja edificada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28685, 'nvi', '1 Coríntios', '1co', 14, 6, 'Agora, irmãos, se eu for visitá-los e falar em línguas, em que lhes serei útil, a não ser que lhes leve alguma revelação, ou conhecimento, ou profecia, ou doutrina?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28686, 'nvi', '1 Coríntios', '1co', 14, 7, 'Até no caso de coisas inanimadas que produzem sons, tais como a flauta ou a cítara, como alguém reconhecerá o que está sendo tocado, se os sons não forem distintos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28687, 'nvi', '1 Coríntios', '1co', 14, 8, 'Além disso, se a trombeta não emitir um som claro, quem se preparará para a batalha?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28688, 'nvi', '1 Coríntios', '1co', 14, 9, 'Assim acontece com vocês. Se não proferirem palavras compreensíveis com a língua, como alguém saberá o que está sendo dito? Vocês estarão simplesmente falando ao ar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28689, 'nvi', '1 Coríntios', '1co', 14, 10, 'Sem dúvida, há diversos idiomas no mundo; todavia, nenhum deles é sem sentido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28690, 'nvi', '1 Coríntios', '1co', 14, 11, 'Portanto, se eu não entender o significado do que alguém está falando, serei estrangeiro para quem fala, e ele, estrangeiro para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28691, 'nvi', '1 Coríntios', '1co', 14, 12, 'Assim acontece com vocês. Visto que estão ansiosos por terem dons espirituais, procurem crescer naqueles que trazem a edificação para a igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28692, 'nvi', '1 Coríntios', '1co', 14, 13, 'Por isso, quem fala em língua, ore para que a possa interpretar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28693, 'nvi', '1 Coríntios', '1co', 14, 14, 'Pois, se oro em língua, meu espírito ora, mas a minha mente fica infrutífera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28694, 'nvi', '1 Coríntios', '1co', 14, 15, 'Então, que farei? Orarei com o espírito, mas também orarei com o entendimento; cantarei com o espírito, mas também cantarei com o entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28695, 'nvi', '1 Coríntios', '1co', 14, 16, 'Se você estiver louvando a Deus em espírito, como poderá aquele que está entre os não instruídos dizer o "Amém" à sua ação de graças, visto que não sabe o que você está dizendo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28696, 'nvi', '1 Coríntios', '1co', 14, 17, 'Pode ser que você esteja dando graças muito bem, mas o outro não é edificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28697, 'nvi', '1 Coríntios', '1co', 14, 18, 'Dou graças a Deus por falar em línguas mais do que todos vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28698, 'nvi', '1 Coríntios', '1co', 14, 19, 'Todavia, na igreja prefiro falar cinco palavras compreensíveis para instruir os outros a falar dez mil palavras em língua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28699, 'nvi', '1 Coríntios', '1co', 14, 20, 'Irmãos, deixem de pensar como crianças. Com respeito ao mal, sejam crianças; mas, quanto ao modo de pensar, sejam adultos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28700, 'nvi', '1 Coríntios', '1co', 14, 21, 'Pois está escrito na Lei: "Por meio de homens de outras línguas e por meio de lábios de estrangeiros falarei a este povo, mas, mesmo assim, eles não me ouvirão", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28701, 'nvi', '1 Coríntios', '1co', 14, 22, 'Portanto, as línguas são um sinal para os descrentes, e não para os que crêem; a profecia, porém, é para os que crêem, e não para os descrentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28702, 'nvi', '1 Coríntios', '1co', 14, 23, 'Assim, se toda a igreja se reunir e todos falarem em línguas, e entrarem alguns não instruídos ou descrentes não dirão que vocês estão loucos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28703, 'nvi', '1 Coríntios', '1co', 14, 24, 'Mas se entrar algum descrente ou não instruído quando todos estiverem profetizando, ele por todos será convencido de que é pecador e por todos será julgado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28704, 'nvi', '1 Coríntios', '1co', 14, 25, 'e os segredos do seu coração serão expostos. Assim, ele se prostrará, rosto em terra, e adorará a Deus, exclamando: "Deus realmente está entre vocês! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28705, 'nvi', '1 Coríntios', '1co', 14, 26, 'Portanto, que diremos, irmãos? Quando vocês se reúnem, cada um de vocês tem um salmo, ou uma palavra de instrução, uma revelação, uma palavra em língua ou uma interpretação. Tudo seja feito para a edificação da igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28706, 'nvi', '1 Coríntios', '1co', 14, 27, 'Se, porém, alguém falar em língua, devem falar dois, no máximo três, e alguém deve interpretar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28707, 'nvi', '1 Coríntios', '1co', 14, 28, 'Se não houver intérprete, fique calado na igreja, falando consigo mesmo e com Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28708, 'nvi', '1 Coríntios', '1co', 14, 29, 'Tratando-se de profetas, falem dois ou três, e os outros julguem cuidadosamente o que foi dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28709, 'nvi', '1 Coríntios', '1co', 14, 30, 'Se vier uma revelação a alguém que está sentado, cale-se o primeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28710, 'nvi', '1 Coríntios', '1co', 14, 31, 'Pois vocês todos podem profetizar, cada um por sua vez, de forma que todos sejam instruídos e encorajados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28711, 'nvi', '1 Coríntios', '1co', 14, 32, 'Os espíritos dos profetas estão sujeitos aos profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28712, 'nvi', '1 Coríntios', '1co', 14, 33, 'Pois Deus não é Deus de desordem, mas de paz. Como em todas as congregações dos santos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28713, 'nvi', '1 Coríntios', '1co', 14, 34, 'permaneçam as mulheres em silêncio nas igrejas, pois não lhes é permitido falar; antes permaneçam em submissão, como diz a lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28714, 'nvi', '1 Coríntios', '1co', 14, 35, 'Se quiserem aprender alguma coisa, que perguntem a seus maridos em casa; pois é vergonhoso uma mulher falar na igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28715, 'nvi', '1 Coríntios', '1co', 14, 36, 'Acaso a palavra de Deus originou-se entre vocês? São vocês o único povo que ela alcançou?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28716, 'nvi', '1 Coríntios', '1co', 14, 37, 'Se alguém pensa que é profeta ou espiritual, reconheça que o que lhes estou escrevendo é mandamento do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28717, 'nvi', '1 Coríntios', '1co', 14, 38, 'Se ignorar isso, ele mesmo será ignorado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28718, 'nvi', '1 Coríntios', '1co', 14, 39, 'Portanto, meus irmãos, busquem com dedicação o profetizar e não proíbam o falar em línguas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28719, 'nvi', '1 Coríntios', '1co', 14, 40, 'Mas tudo deve ser feito com decência e ordem.');
