INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28720, 'nvi', '1 Coríntios', '1co', 15, 1, 'Irmãos, quero lembrar-lhes o evangelho que lhes preguei, o qual vocês receberam e no qual estão firmes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28721, 'nvi', '1 Coríntios', '1co', 15, 2, 'Por meio deste evangelho vocês são salvos, desde que se apeguem firmemente à palavra que lhes preguei; caso contrário, vocês têm crido em vão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28722, 'nvi', '1 Coríntios', '1co', 15, 3, 'Pois o que primeiramente lhes transmiti foi o que recebi: que Cristo morreu pelos nossos pecados, segundo as Escrituras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28723, 'nvi', '1 Coríntios', '1co', 15, 4, 'foi sepultado e ressuscitou ao terceiro dia, segundo as Escrituras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28724, 'nvi', '1 Coríntios', '1co', 15, 5, 'e apareceu a Pedro e depois aos Doze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28725, 'nvi', '1 Coríntios', '1co', 15, 6, 'Depois disso apareceu a mais de quinhentos irmãos de uma só vez, a maioria dos quais ainda vive, embora alguns já tenham adormecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28726, 'nvi', '1 Coríntios', '1co', 15, 7, 'Depois apareceu a Tiago e, então, a todos os apóstolos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28727, 'nvi', '1 Coríntios', '1co', 15, 8, 'depois destes apareceu também a mim, como a um que nasceu fora de tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28728, 'nvi', '1 Coríntios', '1co', 15, 9, 'Pois sou o menor dos apóstolos e nem sequer mereço ser chamado apóstolo, porque persegui a igreja de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28729, 'nvi', '1 Coríntios', '1co', 15, 10, 'Mas, pela graça de Deus, sou o que sou, e sua graça para comigo não foi em vão; antes, trabalhei mais do que todos eles; contudo, não eu, mas a graça de Deus comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28730, 'nvi', '1 Coríntios', '1co', 15, 11, 'Portanto, quer tenha sido eu, quer tenham sido eles, é isto que pregamos, e é isto que vocês creram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28731, 'nvi', '1 Coríntios', '1co', 15, 12, 'Ora, se está sendo pregado que Cristo ressuscitou dentre os mortos, como alguns de vocês estão dizendo que não existe ressurreição dos mortos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28732, 'nvi', '1 Coríntios', '1co', 15, 13, 'Se não há ressurreição dos mortos, então nem mesmo Cristo ressuscitou;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28733, 'nvi', '1 Coríntios', '1co', 15, 14, 'e, se Cristo não ressuscitou, é inútil a nossa pregação, como também é inútil a fé que vocês têm.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28734, 'nvi', '1 Coríntios', '1co', 15, 15, 'Mais que isso, seremos considerados falsas testemunhas de Deus, pois contra ele testemunhamos que ressuscitou a Cristo dentre os mortos. Mas se de fato os mortos não ressuscitam, ele também não ressuscitou a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28735, 'nvi', '1 Coríntios', '1co', 15, 16, 'Pois, se os mortos não ressuscitam, nem mesmo Cristo ressuscitou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28736, 'nvi', '1 Coríntios', '1co', 15, 17, 'E, se Cristo não ressuscitou, inútil é a fé que vocês têm, e ainda estão em seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28737, 'nvi', '1 Coríntios', '1co', 15, 18, 'Neste caso, também os que dormiram em Cristo estão perdidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28738, 'nvi', '1 Coríntios', '1co', 15, 19, 'Se é somente para esta vida que temos esperança em Cristo, dentre todos os homens somos os mais dignos de compaixão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28739, 'nvi', '1 Coríntios', '1co', 15, 20, 'Mas de fato Cristo ressuscitou dentre os mortos, sendo as primícias dentre aqueles que dormiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28740, 'nvi', '1 Coríntios', '1co', 15, 21, 'Visto que a morte veio por meio de um só homem, também a ressurreição dos mortos veio por meio de um só homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28741, 'nvi', '1 Coríntios', '1co', 15, 22, 'Pois da mesma forma como em Adão todos morrem, em Cristo todos serão vivificados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28742, 'nvi', '1 Coríntios', '1co', 15, 23, 'Mas cada um por sua vez: Cristo, o primeiro; depois, quando ele vier, os que lhe pertencem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28743, 'nvi', '1 Coríntios', '1co', 15, 24, 'Então virá o fim, quando ele entregar o Reino a Deus, o Pai, depois de ter destruído todo domínio, autoridade e poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28744, 'nvi', '1 Coríntios', '1co', 15, 25, 'Pois é necessário que ele reine até que todos os seus inimigos sejam postos debaixo de seus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28745, 'nvi', '1 Coríntios', '1co', 15, 26, 'O último inimigo a ser destruído é a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28746, 'nvi', '1 Coríntios', '1co', 15, 27, 'Porque ele "tudo sujeitou debaixo de seus pés". Ora, quando se diz que "tudo" lhe foi sujeito, fica claro que isso não inclui o próprio Deus, que tudo submeteu a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28747, 'nvi', '1 Coríntios', '1co', 15, 28, 'Quando, porém, tudo lhe estiver sujeito, então o próprio Filho se sujeitará àquele que todas as coisas lhe sujeitou, a fim de que Deus seja tudo em todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28748, 'nvi', '1 Coríntios', '1co', 15, 29, 'Se não há ressurreição, que farão aqueles que se batizam pelos mortos? Se absolutamente os mortos não ressuscitam, por que se batizam por eles?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28749, 'nvi', '1 Coríntios', '1co', 15, 30, 'Também nós, por que estamos nos expondo a perigos o tempo todo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28750, 'nvi', '1 Coríntios', '1co', 15, 31, 'Todos os dias enfrento a morte, irmãos; isso digo pelo orgulho que tenho de vocês em Cristo Jesus, nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28751, 'nvi', '1 Coríntios', '1co', 15, 32, 'Se foi por meras razões humanas que lutei com feras em Éfeso, que ganhei com isso? Se os mortos não ressuscitam, "comamos e bebamos, porque amanhã morreremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28752, 'nvi', '1 Coríntios', '1co', 15, 33, 'Não se deixem enganar: "as más companhias corrompem os bons costumes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28753, 'nvi', '1 Coríntios', '1co', 15, 34, 'Como justos, recuperem o bom senso e parem de pecar; pois alguns há que não têm conhecimento de Deus; digo isso para vergonha de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28754, 'nvi', '1 Coríntios', '1co', 15, 35, 'Mas alguém pode perguntar: "Como ressuscitam os mortos? Com que espécie de corpo virão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28755, 'nvi', '1 Coríntios', '1co', 15, 36, 'Insensato! O que você semeia não nasce a não ser que morra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28756, 'nvi', '1 Coríntios', '1co', 15, 37, 'Quando você semeia, não semeia o corpo que virá a ser, mas apenas uma simples semente, como de trigo ou de alguma outra coisa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28757, 'nvi', '1 Coríntios', '1co', 15, 38, 'Mas Deus lhe dá um corpo, como determinou, e a cada espécie de semente dá seu corpo apropriado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28758, 'nvi', '1 Coríntios', '1co', 15, 39, 'Nem toda carne é a mesma: os homens têm uma espécie de carne, os animais têm outra, as aves outra, e os peixes outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28759, 'nvi', '1 Coríntios', '1co', 15, 40, 'Há corpos celestes e há também corpos terrestres; mas o esplendor dos corpos celestes é um, e o dos corpos terrestres é outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28760, 'nvi', '1 Coríntios', '1co', 15, 41, 'Um é o esplendor do sol, outro o da lua, e outro o das estrelas; e as estrelas diferem em esplendor umas das outras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28761, 'nvi', '1 Coríntios', '1co', 15, 42, 'Assim será com a ressurreição dos mortos. O corpo que é semeado é perecível e ressuscita imperecível;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28762, 'nvi', '1 Coríntios', '1co', 15, 43, 'é semeado em desonra e ressuscita em glória; é semeado em fraqueza e ressuscita em poder;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28763, 'nvi', '1 Coríntios', '1co', 15, 44, 'é semeado um corpo natural e ressuscita um corpo espiritual. Se há corpo natural, há também corpo espiritual.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28764, 'nvi', '1 Coríntios', '1co', 15, 45, 'Assim está escrito: "O primeiro homem, Adão, tornou-se um ser vivente"; o último Adão, espírito vivificante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28765, 'nvi', '1 Coríntios', '1co', 15, 46, 'Não foi o espiritual que veio antes, mas o natural; depois dele, o espiritual.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28766, 'nvi', '1 Coríntios', '1co', 15, 47, 'O primeiro homem era do pó da terra; o segundo homem, do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28767, 'nvi', '1 Coríntios', '1co', 15, 48, 'Os que são da terra são semelhantes ao homem terreno; os que são do céu, ao homem celestial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28768, 'nvi', '1 Coríntios', '1co', 15, 49, 'Assim como tivemos a imagem do homem terreno, teremos também a imagem do homem celestial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28769, 'nvi', '1 Coríntios', '1co', 15, 50, 'Irmãos, eu lhes declaro que carne e sangue não podem herdar o Reino de Deus, nem o que é perecível pode herdar o imperecível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28770, 'nvi', '1 Coríntios', '1co', 15, 51, 'Eis que eu lhes digo um mistério: nem todos dormiremos, mas todos seremos transformados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28771, 'nvi', '1 Coríntios', '1co', 15, 52, 'num momento, num abrir e fechar de olhos, ao som da última trombeta. Pois a trombeta soará, os mortos ressuscitarão incorruptíveis e nós seremos transformados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28772, 'nvi', '1 Coríntios', '1co', 15, 53, 'Pois é necessário que aquilo que é corruptível se revista de incorruptibilidade, e aquilo que é mortal, se revista de imortalidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28773, 'nvi', '1 Coríntios', '1co', 15, 54, 'Quando, porém, o que é corruptível se revestir de incorruptibilidade, e o que é mortal, de imortalidade, então se cumprirá a palavra que está escrita: "A morte foi destruída pela vitória".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28774, 'nvi', '1 Coríntios', '1co', 15, 55, '"Onde está, ó morte, a sua vitória? Onde está, ó morte, o seu aguilhão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28775, 'nvi', '1 Coríntios', '1co', 15, 56, 'O aguilhão da morte é o pecado, e a força do pecado é a lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28776, 'nvi', '1 Coríntios', '1co', 15, 57, 'Mas graças a Deus, que nos dá a vitória por meio de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28777, 'nvi', '1 Coríntios', '1co', 15, 58, 'Portanto, meus amados irmãos, mantenham-se firmes, e que nada os abale. Sejam sempre dedicados à obra do Senhor, pois vocês sabem que, no Senhor, o trabalho de vocês não será inútil.');
