INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28778, 'nvi', '1 Coríntios', '1co', 16, 1, 'Quanto à coleta para o povo de Deus, façam como ordenei às igrejas da Galácia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28779, 'nvi', '1 Coríntios', '1co', 16, 2, 'No primeiro dia da semana, cada um de vocês separe uma quantia, de acordo com a sua renda, reservando-a para que não seja preciso fazer coletas quando eu chegar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28780, 'nvi', '1 Coríntios', '1co', 16, 3, 'Então, quando eu chegar, entregarei cartas de recomendação aos homens que vocês aprovarem e os mandarei para Jerusalém com a oferta de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28781, 'nvi', '1 Coríntios', '1co', 16, 4, 'Se me parecer conveniente ir também, eles me acompanharão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28782, 'nvi', '1 Coríntios', '1co', 16, 5, 'Depois de passar pela Macedônia irei visitá-los, já que passarei por lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28783, 'nvi', '1 Coríntios', '1co', 16, 6, 'Talvez eu permaneça com vocês durante algum tempo, ou até mesmo passe o inverno com vocês, para que me ajudem na viagem, aonde quer que eu vá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28784, 'nvi', '1 Coríntios', '1co', 16, 7, 'Desta vez não quero apenas vê-los e fazer uma visita de passagem; espero ficar algum tempo com vocês, se o Senhor permitir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28785, 'nvi', '1 Coríntios', '1co', 16, 8, 'Mas permanecerei em Éfeso até o Pentecoste,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28786, 'nvi', '1 Coríntios', '1co', 16, 9, 'porque se abriu para mim uma porta ampla e promissora; e há muitos adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28787, 'nvi', '1 Coríntios', '1co', 16, 10, 'Se Timóteo for, tomem providências para que ele não tenha nada que temer enquanto estiver com vocês, pois ele trabalha na obra do Senhor, assim como eu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28788, 'nvi', '1 Coríntios', '1co', 16, 11, 'Portanto, ninguém o despreze. Ajudem-no a prosseguir viagem em paz, para que ele possa voltar a mim. Eu o estou esperando juntamente com os irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28789, 'nvi', '1 Coríntios', '1co', 16, 12, 'Quanto ao irmão Apolo, insisti que fosse visitar vocês, juntamente com os irmãos. Ele não quis de modo nenhum ir agora, mas irá quando tiver boa oportunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28790, 'nvi', '1 Coríntios', '1co', 16, 13, 'Estejam vigilantes, mantenham-se firmes na fé, sejam homens de coragem, sejam fortes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28791, 'nvi', '1 Coríntios', '1co', 16, 14, 'Façam tudo com amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28792, 'nvi', '1 Coríntios', '1co', 16, 15, 'Vocês sabem que os da casa de Estéfanas foram o primeiro fruto da Acaia e que eles têm se dedicado ao serviço dos santos. Recomendo-lhes, irmãos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28793, 'nvi', '1 Coríntios', '1co', 16, 16, 'que se submetam a pessoas como eles e a todos os que cooperam e trabalham conosco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28794, 'nvi', '1 Coríntios', '1co', 16, 17, 'Alegrei-me com a vinda de Estéfanas, Fortunato e Acaico, porque eles supriram o que estava faltando da parte de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28795, 'nvi', '1 Coríntios', '1co', 16, 18, 'Eles trouxeram alívio ao meu espírito, e ao de vocês também. Valorizem homens como estes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28796, 'nvi', '1 Coríntios', '1co', 16, 19, 'As igrejas da província da Ásia enviam-lhes saudações. Áqüila e Priscila os saúdam afetuosamente no Senhor, e também a igreja que se reúne na casa deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28797, 'nvi', '1 Coríntios', '1co', 16, 20, 'Todos os irmãos daqui lhes enviam saudações. Saúdem-se uns aos outros com beijo santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28798, 'nvi', '1 Coríntios', '1co', 16, 21, 'Eu, Paulo, escrevi esta saudação de próprio punho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28799, 'nvi', '1 Coríntios', '1co', 16, 22, 'Se alguém não ama o Senhor, seja amaldiçoado. Vem, Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28800, 'nvi', '1 Coríntios', '1co', 16, 23, 'A graça do Senhor Jesus seja com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28801, 'nvi', '1 Coríntios', '1co', 16, 24, 'Recebam o amor que tenho por todos vocês em Cristo Jesus. Amém.');
