INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28412, 'nvi', '1 Coríntios', '1co', 3, 1, 'Irmãos, não lhes pude falar como a espirituais, mas como a carnais, como a crianças em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28413, 'nvi', '1 Coríntios', '1co', 3, 2, 'Dei-lhes leite, e não alimento sólido, pois vocês não estavam em condições de recebê-lo. De fato, vocês ainda não estão em condições,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28414, 'nvi', '1 Coríntios', '1co', 3, 3, 'porque ainda são carnais. Porque, visto que há inveja e divisão entre vocês, não estão sendo carnais e agindo como mundanos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28415, 'nvi', '1 Coríntios', '1co', 3, 4, 'Pois quando alguém diz: "Eu sou de Paulo", e outro: "Eu sou de Apolo", não estão sendo mundanos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28416, 'nvi', '1 Coríntios', '1co', 3, 5, 'Afinal de contas, quem é Apolo? Quem é Paulo? Apenas servos por meio dos quais vocês vieram a crer, conforme o ministério que o Senhor atribuiu a cada um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28417, 'nvi', '1 Coríntios', '1co', 3, 6, 'Eu plantei, Apolo regou, mas Deus é quem fazia crescer;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28418, 'nvi', '1 Coríntios', '1co', 3, 7, 'de modo que nem o que planta nem o que rega são alguma coisa, mas unicamente Deus, que efetua o crescimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28419, 'nvi', '1 Coríntios', '1co', 3, 8, 'O que planta e o que rega têm um só propósito, e cada um será recompensado de acordo com o seu próprio trabalho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28420, 'nvi', '1 Coríntios', '1co', 3, 9, 'Pois nós somos cooperadores de Deus; vocês são lavoura de Deus e edifício de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28421, 'nvi', '1 Coríntios', '1co', 3, 10, 'Conforme a graça de Deus que me foi concedida, eu, como sábio construtor, lancei o alicerce, e outro está construindo sobre ele. Contudo, veja cada um como constrói.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28422, 'nvi', '1 Coríntios', '1co', 3, 11, 'Porque ninguém pode colocar outro alicerce além do que já está posto, que é Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28423, 'nvi', '1 Coríntios', '1co', 3, 12, 'Se alguém constrói sobre esse alicerce, usando ouro, prata, pedras preciosas, madeira, feno ou palha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28424, 'nvi', '1 Coríntios', '1co', 3, 13, 'sua obra será mostrada, porque o Dia a trará à luz; pois será revelada pelo fogo, que provará a qualidade da obra de cada um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28425, 'nvi', '1 Coríntios', '1co', 3, 14, 'Se o que alguém construiu permanecer, esse receberá recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28426, 'nvi', '1 Coríntios', '1co', 3, 15, 'Se o que alguém construiu se queimar, esse sofrerá prejuízo; contudo, será salvo como alguém que escapa através do fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28427, 'nvi', '1 Coríntios', '1co', 3, 16, 'Vocês não sabem que são santuário de Deus e que o Espírito de Deus habita em vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28428, 'nvi', '1 Coríntios', '1co', 3, 17, 'Se alguém destruir o santuário de Deus, Deus o destruirá; pois o santuário de Deus, que são vocês, é sagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28429, 'nvi', '1 Coríntios', '1co', 3, 18, 'Não se enganem. Se algum de vocês pensa que é sábio segundo os padrões desta era, deve tornar-se "louco" para que se torne sábio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28430, 'nvi', '1 Coríntios', '1co', 3, 19, 'Porque a sabedoria deste mundo é loucura aos olhos de Deus. Pois está escrito: "Ele apanha os sábios na astúcia deles";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28431, 'nvi', '1 Coríntios', '1co', 3, 20, 'e também: "O Senhor conhece os pensamentos dos sábios e sabe como são fúteis".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28432, 'nvi', '1 Coríntios', '1co', 3, 21, 'Portanto, ninguém se glorie em homens; porque todas as coisas são de vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28433, 'nvi', '1 Coríntios', '1co', 3, 22, 'seja Paulo, seja Apolo, seja Pedro, seja o mundo, a vida, a morte, o presente ou o futuro; tudo é de vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28434, 'nvi', '1 Coríntios', '1co', 3, 23, 'e vocês são de Cristo, e Cristo, de Deus.');
