INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28469, 'nvi', '1 Coríntios', '1co', 6, 1, 'Se algum de vocês tem queixa contra outro irmão, como ousa apresentar a causa para ser julgada pelos ímpios, em vez de levá-la aos santos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28470, 'nvi', '1 Coríntios', '1co', 6, 2, 'Vocês não sabem que os santos hão de julgar o mundo? Se vocês hão de julgar o mundo, acaso não são capazes de julgar as causas de menor importância?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28471, 'nvi', '1 Coríntios', '1co', 6, 3, 'Vocês não sabem que haveremos de julgar os anjos? Quanto mais as coisas desta vida!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28472, 'nvi', '1 Coríntios', '1co', 6, 4, 'Portanto, se vocês têm questões relativas às coisas desta vida, designem para juízes os que são da igreja, mesmo que sejam os menos importantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28473, 'nvi', '1 Coríntios', '1co', 6, 5, 'Digo isso para envergonhá-los. Acaso não há entre vocês alguém suficientemente sábio para julgar uma causa entre irmãos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28474, 'nvi', '1 Coríntios', '1co', 6, 6, 'Mas, ao invés disso, um irmão vai ao tribunal contra outro irmão, e isso diante de descrentes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28475, 'nvi', '1 Coríntios', '1co', 6, 7, 'O fato de haver litígios entre vocês já significa uma completa derrota. Por que não preferem sofrer a injustiça? Por que não preferem sofrer o prejuízo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28476, 'nvi', '1 Coríntios', '1co', 6, 8, 'Em vez disso vocês mesmos causam injustiças e prejuízos, e isso contra irmãos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28477, 'nvi', '1 Coríntios', '1co', 6, 9, 'Vocês não sabem que os perversos não herdarão o Reino de Deus? Não se deixem enganar: nem imorais, nem idólatras, nem adúlteros, nem homossexuais passivos ou ativos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28478, 'nvi', '1 Coríntios', '1co', 6, 10, 'nem ladrões, nem avarentos, nem alcoólatras, nem caluniadores, nem trapaceiros herdarão o Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28479, 'nvi', '1 Coríntios', '1co', 6, 11, 'Assim foram alguns de vocês. Mas vocês foram lavados, foram santificados, foram justificados no nome do Senhor Jesus Cristo e no Espírito de nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28480, 'nvi', '1 Coríntios', '1co', 6, 12, '"Tudo me é permitido", mas nem tudo convém. "Tudo me é permitido", mas eu não deixarei que nada domine.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28481, 'nvi', '1 Coríntios', '1co', 6, 13, '"Os alimentos foram feitos para o estômago e o estômago para os alimentos", mas Deus destruirá ambos. O corpo, porém, não é para a imoralidade, mas para o Senhor, e o Senhor para o corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28482, 'nvi', '1 Coríntios', '1co', 6, 14, 'Por seu poder, Deus ressuscitou o Senhor e também nos ressuscitará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28483, 'nvi', '1 Coríntios', '1co', 6, 15, 'Vocês não sabem que os seus corpos são membros de Cristo? Tomarei eu os membros de Cristo e os unirei a uma prostituta? De modo nenhum!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28484, 'nvi', '1 Coríntios', '1co', 6, 16, 'Vocês não sabem que aquele que se une a uma prostituta é um corpo com ela? Pois, como está escrito: "Os dois serão uma só carne".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28485, 'nvi', '1 Coríntios', '1co', 6, 17, 'Mas aquele que se une ao Senhor é um espírito com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28486, 'nvi', '1 Coríntios', '1co', 6, 18, 'Fujam da imoralidade sexual. Todos os outros pecados que alguém comete, fora do corpo os comete; mas quem peca sexualmente, peca contra o seu próprio corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28487, 'nvi', '1 Coríntios', '1co', 6, 19, 'Acaso não sabem que o corpo de vocês é santuário do Espírito Santo que habita em vocês, que lhes foi dado por Deus, e que vocês não são de si mesmos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28488, 'nvi', '1 Coríntios', '1co', 6, 20, 'Vocês foram comprados por alto preço. Portanto, glorifiquem a Deus com o corpo de vocês.');
