INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10722, 'nvi', '1 Crônicas', '1cr', 12, 1, 'Estes são os que se juntaram a Davi em Ziclague, onde se escondia de Saul, filho de Quis. Eles estavam entre os combatentes que o ajudaram na guerra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10723, 'nvi', '1 Crônicas', '1cr', 12, 2, 'utilizavam arco e flecha e a funda para atirar pedras tanto com a mão direita como com a esquerda; pertenciam à tribo de Benjamim e eram parentes de Saul:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10724, 'nvi', '1 Crônicas', '1cr', 12, 3, 'Aiezer, o chefe deles, e Joás, filhos de Semaá, de Gibeate; Jeziel e Pelete, filhos de Azmavete; Beraca, Jeú, de Anatote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10725, 'nvi', '1 Crônicas', '1cr', 12, 4, 'e Ismaías, de Gibeom, um grande guerreiro do pelotão dos trinta, e chefe deles; Jeremias, Jaaziel, Joanã, Jozabade, de Gederate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10726, 'nvi', '1 Crônicas', '1cr', 12, 5, 'Eluzai, Jeremote, Bealías, Semarias e Sefatias, de Harufe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10727, 'nvi', '1 Crônicas', '1cr', 12, 6, 'os coreítas Elcana, Issias, Azareel, Joezer e Jasobeão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10728, 'nvi', '1 Crônicas', '1cr', 12, 7, 'e Joela e Zebadias, filhos de Jeroão de Gedor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10729, 'nvi', '1 Crônicas', '1cr', 12, 8, 'Da tribo de Gade alguns aliaram-se a Davi em sua fortaleza no deserto. Eram guerreiros corajosos, prontos para o combate, e sabiam lutar com escudo e com lança. Tinham a bravura de um leão, e eram ágeis como gazelas nos montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10730, 'nvi', '1 Crônicas', '1cr', 12, 9, 'Ézer era o primeiro; Obadias, o segundo; Eliabe, o terceiro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10731, 'nvi', '1 Crônicas', '1cr', 12, 10, 'Mismana, o quarto; Jeremias, o quinto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10732, 'nvi', '1 Crônicas', '1cr', 12, 11, 'Atai, o sexto; Eliel, o sétimo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10733, 'nvi', '1 Crônicas', '1cr', 12, 12, 'Joanã, o oitavo; Elzabade, o nono;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10734, 'nvi', '1 Crônicas', '1cr', 12, 13, 'Jeremias, o décimo; e Macbanai era o décimo primeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10735, 'nvi', '1 Crônicas', '1cr', 12, 14, 'Todos esses de Gade eram chefes de exército; o menor valia por cem, e o maior enfrentava mil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10736, 'nvi', '1 Crônicas', '1cr', 12, 15, 'Foram eles que atravessaram o Jordão no primeiro mês do ano, quando o rio transborda em todas as suas margens, e puseram em fuga todos os que moravam nos vales, a leste e a oeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10737, 'nvi', '1 Crônicas', '1cr', 12, 16, 'Alguns outros benjamitas e certos homens de Judá também vieram a Davi em sua fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10738, 'nvi', '1 Crônicas', '1cr', 12, 17, 'Davi saiu ao encontro deles e lhes disse: "Se vocês vieram em paz, para me ajudarem, estou pronto para recebê-los. Mas, se querem trair-me e entregar-me aos meus inimigos quando minhas mãos não cometeram violência, que o Deus de nossos antepassados veja isso e julgue vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10739, 'nvi', '1 Crônicas', '1cr', 12, 18, 'Então o Espírito veio sobre Amasai, chefe do pelotão dos trinta, e ele disse: "Somos teus, ó Davi! Estamos contigo, ó filho de Jessé! Paz, paz seja contigo, e aos teus aliados, pois o teu Deus te ajudará". Davi os recebeu e os nomeou chefes de seus grupos de ataque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10740, 'nvi', '1 Crônicas', '1cr', 12, 19, 'Alguns soldados de Manassés desertaram para Davi quando ele foi com os filisteus guerrear contra Saul. Mas eles não ajudaram os filisteus, porque os seus chefes os aconselharam e os mandaram embora, dizendo: "Pagaremos com a vida, caso Davi deserte e passe para Saul, seu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10741, 'nvi', '1 Crônicas', '1cr', 12, 20, 'Estes foram os homens de Manassés que desertaram para Davi quando ele foi a Ziclague: Adna, Jozabade, Jediael, Micael, Jozabade, Eliú e Ziletai, chefes de batalhões de mil em Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10742, 'nvi', '1 Crônicas', '1cr', 12, 21, 'Eles ajudaram Davi contra grupos de ataque, pois todos eles eram guerreiros valentes, e eram líderes no exército dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10743, 'nvi', '1 Crônicas', '1cr', 12, 22, 'De fato, diariamente chegavam soldados para ajudar Davi, até que seu exército tornou-se tão grande como o exército de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10744, 'nvi', '1 Crônicas', '1cr', 12, 23, 'Este é o número dos soldados armados para a guerra que vieram a Davi em Hebrom para entregar a ele o reino de Saul, conforme o Senhor tinha dito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10745, 'nvi', '1 Crônicas', '1cr', 12, 24, 'Da tribo de Judá, 6. 800 armados para a guerra, com escudo e lança;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10746, 'nvi', '1 Crônicas', '1cr', 12, 25, 'Da tribo de Simeão, 7. 100 guerreiros prontos para o combate;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10747, 'nvi', '1 Crônicas', '1cr', 12, 26, 'Da tribo de Levi, 4. 600,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10748, 'nvi', '1 Crônicas', '1cr', 12, 27, 'inclusive Joiada, líder da família de Arão, com 3. 700 homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10749, 'nvi', '1 Crônicas', '1cr', 12, 28, 'e Zadoque, um jovem e valente guerreiro, com 22 oficiais de sua família;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10750, 'nvi', '1 Crônicas', '1cr', 12, 29, 'Da tribo de Benjamim, parentes de Saul, 3. 000, a maioria dos quais era até então fiel à família de Saul;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10751, 'nvi', '1 Crônicas', '1cr', 12, 30, 'Da tribo de Efraim, 20. 800 soldados valentes, famosos em seus próprios clãs;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10752, 'nvi', '1 Crônicas', '1cr', 12, 31, 'Da metade da tribo de Manassés, 18. 000, indicados por nome para fazerem Davi rei;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10753, 'nvi', '1 Crônicas', '1cr', 12, 32, 'Da tribo de Issacar, 200 chefes que sabiam como Israel devia agir em qualquer circunstância. Comandavam todos os seus parentes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10754, 'nvi', '1 Crônicas', '1cr', 12, 33, 'Da tribo de Zebulom, 50. 000 soldados experientes, preparados para guerrear com qualquer tipo de arma, totalmente decididos a ajudar Davi;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10755, 'nvi', '1 Crônicas', '1cr', 12, 34, 'Da tribo de Naftali, 1. 000 líderes com 37. 000 homens armados de escudos e lanças;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10756, 'nvi', '1 Crônicas', '1cr', 12, 35, 'Da tribo de Dã, 28. 600 prontos para o combate;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10757, 'nvi', '1 Crônicas', '1cr', 12, 36, 'Da tribo de Aser, 40. 000 soldados experientes, preparados para o combate;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10758, 'nvi', '1 Crônicas', '1cr', 12, 37, 'e do leste do Jordão, das tribos de Rúben e de Gade, e da metade da tribo de Manassés, 120. 000 completamente armados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10759, 'nvi', '1 Crônicas', '1cr', 12, 38, 'Todos esses eram homens de combate que se apresentaram voluntariamente para servirem nas fileiras. Foram a Hebrom totalmente decididos a fazerem de Davi rei sobre todo o Israel. E todos os outros israelitas tinham esse mesmo propósito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10760, 'nvi', '1 Crônicas', '1cr', 12, 39, 'Ficaram com Davi três dias, comendo e bebendo, pois as suas famílias haviam fornecido provisões para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10761, 'nvi', '1 Crônicas', '1cr', 12, 40, 'Os habitantes das tribos vizinhas e também de lugares distantes como Issacar, Zebulom e Naftali, trouxeram-lhes muitas provisões em jumentos, camelos, mulas e bois: farinha, bolos de figo, bolos de uvas passas, vinho, azeite, bois e ovelhas, pois havia grande alegria em Israel.');
