INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10793, 'nvi', '1 Crônicas', '1cr', 15, 1, 'Depois de Davi ter construído casas para si na cidade de Davi, ele preparou um lugar para a arca de Deus e armou uma tenda para ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10794, 'nvi', '1 Crônicas', '1cr', 15, 2, 'Então Davi disse: "Somente os levitas poderão carregar a arca de Deus, pois o Senhor os escolheu para transportarem a arca do Senhor e para ficarem sempre ao seu serviço".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10795, 'nvi', '1 Crônicas', '1cr', 15, 3, 'Davi reuniu todo o Israel em Jerusalém para levar a arca do Senhor para o lugar que ele lhe havia preparado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10796, 'nvi', '1 Crônicas', '1cr', 15, 4, 'Reuniu também os descendentes de Arão e os levitas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10797, 'nvi', '1 Crônicas', '1cr', 15, 5, 'Dos descendentes de Coate, Uriel, liderando outros 120;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10798, 'nvi', '1 Crônicas', '1cr', 15, 6, 'dos descendentes de Merari, Asaías, liderando outros 220;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10799, 'nvi', '1 Crônicas', '1cr', 15, 7, 'dos descendentes de Gérson, Joel, liderando outros 130;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10800, 'nvi', '1 Crônicas', '1cr', 15, 8, 'dos descendentes de Elisafã, Semaías, liderando outros 200;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10801, 'nvi', '1 Crônicas', '1cr', 15, 9, 'dos descendentes de Hebrom, Eliel, liderando outros 80;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10802, 'nvi', '1 Crônicas', '1cr', 15, 10, 'dos descendentes de Uziel, Aminadabe, liderando outros 112.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10803, 'nvi', '1 Crônicas', '1cr', 15, 11, 'Então Davi convocou os sacerdotes Zadoque e Abiatar, e os levitas Uriel, Asaías, Joel, Semaías, Eliel e Aminadabe, e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10804, 'nvi', '1 Crônicas', '1cr', 15, 12, 'lhes disse: "Vocês são os chefes das famílias levitas; vocês e seus companheiros levitas deverão consagrar-se e levar a arca do Senhor, o Deus de Israel, para o local que preparei para ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10805, 'nvi', '1 Crônicas', '1cr', 15, 13, 'Pelo fato de vocês não terem carregado a arca na primeira vez, a ira do Senhor nosso Deus causou destruição entre nós. Nós não o tínhamos consultado sobre como proceder".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10806, 'nvi', '1 Crônicas', '1cr', 15, 14, 'Então os sacerdotes e os levitas se consagraram para transportar a arca do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10807, 'nvi', '1 Crônicas', '1cr', 15, 15, 'E os levitas carregaram a arca de Deus apoiando as varas da arca sobre os ombros, conforme Moisés tinha ordenado, de acordo com a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10808, 'nvi', '1 Crônicas', '1cr', 15, 16, 'Davi também ordenou aos líderes dos levitas que encarregassem os músicos que havia entre eles de cantar músicas alegres, acompanhados por instrumentos musicais: liras, harpas e címbalos sonoros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10809, 'nvi', '1 Crônicas', '1cr', 15, 17, 'Assim, os levitas escolheram Hemã, filho de Joel, e Asafe, um parente dele; e dentre os meraritas, seus parentes, escolheram Etã, filho de Cuxaías;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10810, 'nvi', '1 Crônicas', '1cr', 15, 18, 'e com eles seus parentes que estavam no segundo escalão: Zacarias, Jaaziel, Semiramote, Jeiel, Uni, Eliabe, Benaia, Maaséias, Matitias, Elifeleu, Micnéias, Obede-Edom e Jeiel, os porteiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10811, 'nvi', '1 Crônicas', '1cr', 15, 19, 'Os músicos Hemã, Asafe e Etã deviam tocar os címbalos de bronze;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10812, 'nvi', '1 Crônicas', '1cr', 15, 20, 'Zacarias, Aziel, Semiramote, Jeiel, Uni, Eliabe, Maaséias e Benaia deviam tocar as liras, acompanhando o soprano,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10813, 'nvi', '1 Crônicas', '1cr', 15, 21, 'e Matitias, Elifeleu, Micnéias, Obede-Edom, Jeiel e Azazias deviam tocar as harpas em oitava, marcando o ritmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10814, 'nvi', '1 Crônicas', '1cr', 15, 22, 'Quenanias, o chefe dos levitas, ficou encarregado dos cânticos; essa era sua responsabilidade, pois ele era capaz nisso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10815, 'nvi', '1 Crônicas', '1cr', 15, 23, 'Berequias e Elcana seriam porteiros. Eles deveriam proteger a arca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10816, 'nvi', '1 Crônicas', '1cr', 15, 24, 'Os sacerdotes Sebanias, Josafá, Natanael, Amasai, Zacarias, Benaia e Eliézer deviam tocar as cornetas diante da arca de Deus. Obede-Edom e Jeías também deviam ser porteiros, para vigiar a arca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10817, 'nvi', '1 Crônicas', '1cr', 15, 25, 'Assim, com grande festa, foram Davi, as autoridades de Israel e os líderes de batalhões de mil buscar a arca da aliança do Senhor na casa de Obede-Edom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10818, 'nvi', '1 Crônicas', '1cr', 15, 26, 'Como Deus havia poupado os levitas que carregavam a arca da aliança do Senhor, sete novilhos e sete carneiros foram sacrificados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10819, 'nvi', '1 Crônicas', '1cr', 15, 27, 'E Davi vestia um manto de linho fino, assim como também todos os levitas que carregavam a arca, os músicos e Quenanias, chefe dos músicos. E Davi vestia também o colete sacerdotal de linho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10820, 'nvi', '1 Crônicas', '1cr', 15, 28, 'E todo Israel acompanhou a arca da aliança do Senhor alegremente, ao som de trombetas, cornetas e címbalos, ao toque de liras e de harpas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10821, 'nvi', '1 Crônicas', '1cr', 15, 29, 'Aconteceu que, entrando a arca da aliança do Senhor na cidade de Davi, Mical, filha de Saul, observava de uma janela. E, ao ver o rei Davi dançando e comemorando, ela o desprezou em seu coração.');
