INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10822, 'nvi', '1 Crônicas', '1cr', 16, 1, 'Eles trouxeram a arca de Deus e a colocaram na tenda que Davi lhe havia preparado, e ofereceram holocaustos e sacrifícios de comunhão diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10823, 'nvi', '1 Crônicas', '1cr', 16, 2, 'Após oferecer os holocaustos e os sacrifícios de comunhão, Davi abençoou o povo em nome do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10824, 'nvi', '1 Crônicas', '1cr', 16, 3, 'e deu um pão, um bolo de tâmaras e um bolo de uvas passas a cada homem e a cada mulher israelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10825, 'nvi', '1 Crônicas', '1cr', 16, 4, 'Davi nomeou alguns dos levitas para ministrarem diante da arca do Senhor, fazendo petições, dando graças, e louvando o Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10826, 'nvi', '1 Crônicas', '1cr', 16, 5, 'Desses, Asafe era o chefe, Zacarias vinha em seguida, e depois Jeiel, Semiramote, Jeiel, Matitias, Eliabe, Benaia, Obede-Edom e Jeiel. Eles deviam tocar lira e harpa, enquanto Asafe tocava os címbalos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10827, 'nvi', '1 Crônicas', '1cr', 16, 6, 'Os sacerdotes Benaia e Jaaziel deviam tocar diariamente as trombetas diante da arca da aliança de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10828, 'nvi', '1 Crônicas', '1cr', 16, 7, 'Foi naquele dia que pela primeira vez Davi encarregou a Asafe e seus parentes de louvar ao Senhor com salmos de gratidão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10829, 'nvi', '1 Crônicas', '1cr', 16, 8, 'Dêem graças ao Senhor, clamem pelo seu nome, divulguem entre as nações o que ele tem feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10830, 'nvi', '1 Crônicas', '1cr', 16, 9, 'Cantem para ele, louvem-no; contem todos os seus atos maravilhosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10831, 'nvi', '1 Crônicas', '1cr', 16, 10, 'Gloriem-se no seu santo nome; alegrem-se os corações dos que buscam o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10832, 'nvi', '1 Crônicas', '1cr', 16, 11, 'Olhem para o Senhor e para a sua força; busquem sempre a sua face.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10833, 'nvi', '1 Crônicas', '1cr', 16, 12, 'Lembrem-se das maravilhas que ele fez, dos seus prodígios e das ordenanças que pronunciou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10834, 'nvi', '1 Crônicas', '1cr', 16, 13, 'ó descendentes de Israel, seu servo, ó filhos de Jacó, seus escolhidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10835, 'nvi', '1 Crônicas', '1cr', 16, 14, 'Ele é o Senhor, o nosso Deus; seus domínio alcança toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10836, 'nvi', '1 Crônicas', '1cr', 16, 15, 'Para sempre se lembra da sua aliança, da palavra que ordenou para mil gerações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10837, 'nvi', '1 Crônicas', '1cr', 16, 16, 'da aliança que fez com Abraão, do juramento que fez a Isaque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10838, 'nvi', '1 Crônicas', '1cr', 16, 17, 'que confirmou para Jacó como um decreto, e para Israel como uma aliança eterna, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10839, 'nvi', '1 Crônicas', '1cr', 16, 18, '"A vocês darei a terra de Canaã, a herança que possuirão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10840, 'nvi', '1 Crônicas', '1cr', 16, 19, 'Quando eles ainda eram poucos, muito poucos, e estrangeiros nela,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10841, 'nvi', '1 Crônicas', '1cr', 16, 20, 'e vagueavam de nação em nação, de um reino a outro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10842, 'nvi', '1 Crônicas', '1cr', 16, 21, 'ele não permitiu que ninguém os oprimisse; por causa deles repreendeu reis, ordenando:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10843, 'nvi', '1 Crônicas', '1cr', 16, 22, '"Não maltratem os meus ungidos; não façam mal aos meus profetas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10844, 'nvi', '1 Crônicas', '1cr', 16, 23, 'Cantem ao Senhor, todas as terras! Proclamem a sua salvação dia após dia!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10845, 'nvi', '1 Crônicas', '1cr', 16, 24, 'Anunciem a sua glória entre as nações, seus feitos maravilhosos entre todos os povos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10846, 'nvi', '1 Crônicas', '1cr', 16, 25, 'Pois o Senhor é grande e muitíssimo digno de louvor; ele deve ser mais temido que todos os deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10847, 'nvi', '1 Crônicas', '1cr', 16, 26, 'Pois todos os deuses das nações não passam de ídolos, mas o Senhor fez os céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10848, 'nvi', '1 Crônicas', '1cr', 16, 27, 'O esplendor e a majestade estão diante dele; força e alegria na sua habitação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10849, 'nvi', '1 Crônicas', '1cr', 16, 28, 'Dêem ao Senhor, ó famílias das nações, dêem ao Senhor glória e força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10850, 'nvi', '1 Crônicas', '1cr', 16, 29, 'Dêem ao Senhor a glória devida ao seu nome. Tragam ofertas e venham à sua presença. Adorem o Senhor no esplendor da sua santidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10851, 'nvi', '1 Crônicas', '1cr', 16, 30, 'tremam diante dele, todas as nações! Firmou o mundo, que não se abalará!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10852, 'nvi', '1 Crônicas', '1cr', 16, 31, 'Que os céus se alegrem e a terra exulte, e diga-se entre as nações: "O Senhor reina! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10853, 'nvi', '1 Crônicas', '1cr', 16, 32, 'Ressoe o mar, e tudo o que nele existe; exultem os campos, e tudo o que neles há!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10854, 'nvi', '1 Crônicas', '1cr', 16, 33, 'Então as árvores da floresta cantarão de alegria, cantarão diante do Senhor, pois ele vem julgar a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10855, 'nvi', '1 Crônicas', '1cr', 16, 34, 'Rendam graças ao Senhor, pois ele é bom; o seu amor dura para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10856, 'nvi', '1 Crônicas', '1cr', 16, 35, 'Clamem: "Salva-nos, ó Deus, nosso Salvador! Reúne-nos e livra-nos das nações, para que demos graças ao teu santo nome e façamos do teu louvor a nossa glória".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10857, 'nvi', '1 Crônicas', '1cr', 16, 36, 'Bendito seja o Senhor, o Deus de Israel, de eternidade a eternidade. Então todo o povo exclamou: "Amém! " e "Louvado seja o Senhor! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10858, 'nvi', '1 Crônicas', '1cr', 16, 37, 'Então Davi deixou Asafe e seus parentes diante da arca da aliança do Senhor para ali ministrarem regularmente, de acordo com as prescrições para cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10859, 'nvi', '1 Crônicas', '1cr', 16, 38, 'Também deixou Obede-Edom e seus sessenta e oito parentes para ministrarem com eles. Obede-Edom, filho de Jedutum, e também Hosa foram porteiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10860, 'nvi', '1 Crônicas', '1cr', 16, 39, 'Davi deixou o sacerdote Zadoque e seus parentes sacerdotes diante do tabernáculo do Senhor em Gibeom');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10861, 'nvi', '1 Crônicas', '1cr', 16, 40, 'para regularmente, de manhã e à tarde, apresentarem holocaustos no altar de holocaustos, de acordo com tudo o que está escrito na Lei do Senhor, que ele deu a Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10862, 'nvi', '1 Crônicas', '1cr', 16, 41, 'Com eles estavam Hemã e Jedutum e os outros designados para darem graças ao Senhor, exclamando: "O seu amor dura para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10863, 'nvi', '1 Crônicas', '1cr', 16, 42, 'Hemã e Jedutum eram responsáveis pelas trombetas, pelos címbalos e pelos outros instrumentos musicais para o culto. Os filhos de Jedutum foram designados como porteiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10864, 'nvi', '1 Crônicas', '1cr', 16, 43, 'Então todo o povo partiu, cada um para a sua casa, e Davi voltou para casa para abençoar sua família.');
