INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10936, 'nvi', '1 Crônicas', '1cr', 21, 1, 'Satanás levantou-se contra Israel e levou Davi a fazer um recenseamento do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10937, 'nvi', '1 Crônicas', '1cr', 21, 2, 'Davi disse a Joabe e aos outros comandantes do exército: "Vão e contem os israelitas desde Berseba até Dã e tragam-me um relatório para que eu saiba quantos são".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10938, 'nvi', '1 Crônicas', '1cr', 21, 3, 'Joabe, porém, respondeu: "Que o Senhor multiplique o povo dele por cem. Ó rei, meu senhor, não são, porventura, todos eles súditos do meu senhor? Por que o meu senhor deseja fazer isso? Por que deveria trazer culpa sobre Israel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10939, 'nvi', '1 Crônicas', '1cr', 21, 4, 'Mas a palavra do rei prevaleceu, de modo que Joabe partiu, percorreu todo o Israel e então voltou a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10940, 'nvi', '1 Crônicas', '1cr', 21, 5, 'Joabe apresentou a Davi o relatório com o número dos homens de combate: Em todo o Israel havia um milhão e cem mil homens habilitados para o serviço militar, sendo quatrocentos e setenta mil de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10941, 'nvi', '1 Crônicas', '1cr', 21, 6, 'Mas Joabe não incluiu as tribos de Levi e de Benjamim na contagem, pois a ordem do rei lhe parecera absurda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10942, 'nvi', '1 Crônicas', '1cr', 21, 7, 'Essa ordem foi reprovada por Deus, e por isso ele puniu Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10943, 'nvi', '1 Crônicas', '1cr', 21, 8, 'Então Davi disse a Deus: "Pequei gravemente com o que fiz. Agora eu te imploro que perdoes o pecado do teu servo, porque cometi uma grande loucura! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10944, 'nvi', '1 Crônicas', '1cr', 21, 9, 'O Senhor disse a Gade, o vidente de Davi:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10945, 'nvi', '1 Crônicas', '1cr', 21, 10, '"Vá dizer a Davi: ‘Assim diz o Senhor: Estou lhe dando três opções. Escolha uma delas, e eu a executarei contra você’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10946, 'nvi', '1 Crônicas', '1cr', 21, 11, 'Então Gade foi a Davi e lhe disse: "Assim diz o Senhor: ‘Escolha:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10947, 'nvi', '1 Crônicas', '1cr', 21, 12, 'três anos de fome, três meses fugindo de seus adversários, perseguido pela espada deles, ou três dias da espada do Senhor, isto é, três dias de praga, com o anjo do Senhor assolando todas as regiões de Israel’. Decida agora como devo responder àquele que me enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10948, 'nvi', '1 Crônicas', '1cr', 21, 13, 'Davi respondeu: "É grande a minha angustia! Prefiro cair nas mãos do Senhor, pois é grande a sua misericórdia, e não nas mãos dos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10949, 'nvi', '1 Crônicas', '1cr', 21, 14, 'Então o Senhor enviou uma praga sobre Israel, e setenta mil homens de Israel morreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10950, 'nvi', '1 Crônicas', '1cr', 21, 15, 'E Deus enviou um anjo para destruir Jerusalém. Mas, quando o anjo ia fazê-lo, o Senhor olhou e arrependeu-se de trazer a catástrofe, e ele disse ao anjo destruidor: "Pare! Já basta! " Naquele momento o anjo do Senhor estava perto da eira de Araúna, o jebuseu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10951, 'nvi', '1 Crônicas', '1cr', 21, 16, 'Davi olhou para cima e viu o anjo do Senhor entre o céu e a terra, com uma espada na mão erguida sobre Jerusalém. Então Davi e as autoridades de Israel, vestidos de luto, prostraram-se, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10952, 'nvi', '1 Crônicas', '1cr', 21, 17, 'Davi disse a Deus: "Não fui eu que ordenei contar o povo? Fui eu que pequei e fiz o mal. Estes não passam de ovelhas. O que eles fizeram? Ó Senhor meu Deus, que o teu castigo caia sobre mim e sobre a minha família, mas não sobre o teu povo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10953, 'nvi', '1 Crônicas', '1cr', 21, 18, 'Então o anjo do Senhor mandou Gade dizer a Davi que construísse um altar na eira de Araúna, o jebuseu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10954, 'nvi', '1 Crônicas', '1cr', 21, 19, 'Davi foi para lá, em obediência à palavra que Gade havia falado em nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10955, 'nvi', '1 Crônicas', '1cr', 21, 20, 'Araúna estava debulhando o trigo; virando-se, viu o anjo, e ele e seus quatro filhos que estavam com ele se esconderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10956, 'nvi', '1 Crônicas', '1cr', 21, 21, 'Nisso chegou Davi e, quando Araúna o viu, saiu da eira e prostrou-se diante de Davi, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10957, 'nvi', '1 Crônicas', '1cr', 21, 22, 'E Davi lhe pediu: "Ceda-me o terreno da sua eira para eu construir um altar em honra do Senhor, para que cesse a praga sobre o povo. Venda-me o terreno pelo preço justo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10958, 'nvi', '1 Crônicas', '1cr', 21, 23, 'Mas Araúna disse a Davi: "Considera-o teu! Que o meu rei e senhor faça dele o que desejar. Eu darei os bois para os holocaustos, o debulhador para servir de lenha, e o trigo para a oferta de cereal. Tudo isso eu dou a ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10959, 'nvi', '1 Crônicas', '1cr', 21, 24, 'O rei Davi, porém, respondeu a Araúna: "Não! Faço questão de pagar o preço justo. Não darei ao Senhor aquilo que pertence a você, nem oferecerei um holocausto que não me custe nada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10960, 'nvi', '1 Crônicas', '1cr', 21, 25, 'Então Davi pagou a Araúna sete quilos e duzentos gramas de ouro pelo terreno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10961, 'nvi', '1 Crônicas', '1cr', 21, 26, 'E Davi edificou ali um altar ao Senhor e ofereceu holocaustos e sacrifícios de comunhão. Davi invocou o Senhor, e o Senhor lhe respondeu com fogo que veio do céu sobre o altar de holocaustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10962, 'nvi', '1 Crônicas', '1cr', 21, 27, 'E o Senhor ordenou ao anjo que pusesse a espada na bainha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10963, 'nvi', '1 Crônicas', '1cr', 21, 28, 'Nessa ocasião viu Davi que o Senhor lhe havia respondido na eira de Araúna, o jebuseu, e passou a oferecer sacrifícios ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10964, 'nvi', '1 Crônicas', '1cr', 21, 29, 'Naquela época, o tabernáculo do Senhor que Moisés fizera no deserto, e o altar de holocaustos, estavam em Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10965, 'nvi', '1 Crônicas', '1cr', 21, 30, 'Mas Davi não podia consultar a Deus lá, pois tinha medo da espada do anjo do Senhor.');
