INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10985, 'nvi', '1 Crônicas', '1cr', 23, 1, 'Já envelhecido e de idade avançada, Davi fez do seu filho Salomão rei sobre Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10986, 'nvi', '1 Crônicas', '1cr', 23, 2, 'Ele reuniu todos os líderes de Israel, bem como os sacerdotes e os levitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10987, 'nvi', '1 Crônicas', '1cr', 23, 3, 'Os levitas de trinta anos para cima foram contados, e o número total deles chegou a trinta e oito mil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10988, 'nvi', '1 Crônicas', '1cr', 23, 4, 'Davi escolheu vinte e quatro mil desses para supervisionarem o trabalho do templo do Senhor e seis mil para serem oficiais e juízes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10989, 'nvi', '1 Crônicas', '1cr', 23, 5, 'quatro mil para serem guardas das portas e quatro mil para louvarem o Senhor com os instrumentos musicais que Davi preparou com esse propósito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10990, 'nvi', '1 Crônicas', '1cr', 23, 6, 'Davi repartiu os levitas em grupos que descendiam dos filhos de Levi, Gérson, Coate e Merari.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10991, 'nvi', '1 Crônicas', '1cr', 23, 7, 'Dos filhos de Gérson: Ladã e Simei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10992, 'nvi', '1 Crônicas', '1cr', 23, 8, 'Estes foram os filhos de Ladã: Jeiel, o primeiro, Zetã e Joel, três ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10993, 'nvi', '1 Crônicas', '1cr', 23, 9, 'Estes foram os filhos de Simei: Selomote, Haziel e Harã, três ao todo. Esses foram os chefes das famílias de Ladã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10994, 'nvi', '1 Crônicas', '1cr', 23, 10, 'E os filhos de Simei foram: Jaate, Ziza, Jeús e Berias. Esses foram os filhos de Simei, quatro ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10995, 'nvi', '1 Crônicas', '1cr', 23, 11, 'Jaate foi o primeiro e Ziza, o segundo, mas Jeús e Berias não tiveram muitos descendentes, por isso foram contados como uma única família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10996, 'nvi', '1 Crônicas', '1cr', 23, 12, 'Dos filhos de Coate: Anrão, Isar, Hebrom e Uziel, quatro ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10997, 'nvi', '1 Crônicas', '1cr', 23, 13, 'Estes foram os filhos de Anrão: Arão e Moisés. Arão foi separado, ele e seus descendentes para sempre, para consagrar as coisas santíssimas, oferecer sacrifícios ao Senhor, ministrar diante dele e pronunciar bênçãos em seu nome, para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10998, 'nvi', '1 Crônicas', '1cr', 23, 14, 'Os filhos de Moisés, homem de Deus, foram contados como parte da tribo de Levi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10999, 'nvi', '1 Crônicas', '1cr', 23, 15, 'Estes foram os filhos de Moisés: Gérson e Eliézer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11000, 'nvi', '1 Crônicas', '1cr', 23, 16, 'Sebuel foi o chefe dos descendentes de Gérson.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11001, 'nvi', '1 Crônicas', '1cr', 23, 17, 'Reabias foi o chefe dos descendentes de Eliézer. Eliézer não teve nenhum outro filho, mas Reabias teve muitos filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11002, 'nvi', '1 Crônicas', '1cr', 23, 18, 'Selomite foi o chefe dos filhos de Isar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11003, 'nvi', '1 Crônicas', '1cr', 23, 19, 'Estes foram os filhos de Hebrom: Jerias foi o primeiro, Amarias, o segundo, Jaaziel, o terceiro, e Jecameão foi o quarto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11004, 'nvi', '1 Crônicas', '1cr', 23, 20, 'Estes foram os filhos de Uziel: Mica, o primeiro, e Issias, o segundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11005, 'nvi', '1 Crônicas', '1cr', 23, 21, 'Dos filhos de Merari: Mali e Musi. Estes foram os filhos de Mali: Eleazar e Quis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11006, 'nvi', '1 Crônicas', '1cr', 23, 22, 'Eleazar morreu sem ter filhos, pois teve apenas filhas. Os primos delas, os filhos de Quis, casaram-se com elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11007, 'nvi', '1 Crônicas', '1cr', 23, 23, 'Estes foram os filhos de Musi: Mali, Éder e Jeremote, três ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11008, 'nvi', '1 Crônicas', '1cr', 23, 24, 'Esses foram os descendentes de Levi pelas suas famílias: os chefes de famílias conforme registrados por seus nomes e contados individualmente, ou seja, os de vinte anos para cima, que serviam no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11009, 'nvi', '1 Crônicas', '1cr', 23, 25, 'Pois Davi dissera: "Uma vez que o Senhor, o Deus de Israel, concedeu descanso ao seu povo e veio habitar para sempre em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11010, 'nvi', '1 Crônicas', '1cr', 23, 26, 'os levitas não mais precisam carregar o tabernáculo nem os utensílios usados em seu serviço".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11011, 'nvi', '1 Crônicas', '1cr', 23, 27, 'De acordo com as instruções finais de Davi, foram contados os levitas de vinte anos para cima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11012, 'nvi', '1 Crônicas', '1cr', 23, 28, 'O dever dos levitas era ajudar os descendentes de Arão no serviço do templo do Senhor. Encarregavam-se dos pátios, das salas laterais, da purificação de todas as coisas sagradas e dos outros deveres na casa de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11013, 'nvi', '1 Crônicas', '1cr', 23, 29, 'Estavam encarregados do pão consagrado, da farinha para as ofertas de cereal, dos bolos sem fermento, de assar o pão e misturar a massa, e de todos os pesos e medidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11014, 'nvi', '1 Crônicas', '1cr', 23, 30, 'Além disso, deviam se apresentar todas as manhãs e todas as tardes para agradecer e louvar ao Senhor, e fazer o mesmo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11015, 'nvi', '1 Crônicas', '1cr', 23, 31, 'sempre que holocaustos fossem apresentados ao Senhor nos sábados, nas festas da lua nova e nas festas fixas. Deviam servir regularmente diante do Senhor, conforme o número prescrito para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11016, 'nvi', '1 Crônicas', '1cr', 23, 32, 'Dessa maneira os levitas ficaram responsáveis pela Tenda do Encontro, pelo Lugar Santo e, pela assistência aos seus irmãos, os descendentes de Arão, e pelo serviço do templo do Senhor.');
