INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11017, 'nvi', '1 Crônicas', '1cr', 24, 1, 'Os filhos de Arão foram assim agrupados: Os filhos de Arão foram Nadabe, Abiú, Eleazar e Itamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11018, 'nvi', '1 Crônicas', '1cr', 24, 2, 'Mas Nadabe e Abiú morreram antes de seu pai e não tiveram filhos; apenas Eleazar e Itamar serviram como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11019, 'nvi', '1 Crônicas', '1cr', 24, 3, 'Com a ajuda de Zadoque, descendente de Eleazar, e de Aimeleque, descendente de Itamar, Davi os dividiu em grupos para que cumprissem as suas responsabilidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11020, 'nvi', '1 Crônicas', '1cr', 24, 4, 'Havia um número maior de chefes de família entre os descendentes de Eleazar do que entre os de Itamar, e por isso eles foram assim divididos: dezesseis chefes de famílias dentre os descendentes de Eleazar e oito, dentre os descendentes de Itamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11021, 'nvi', '1 Crônicas', '1cr', 24, 5, 'Eles foram divididos de maneira imparcial mediante sorteio, pois havia líderes do santuário e líderes de Deus tanto entre os descendentes de Eleazar como entre os de Itamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11022, 'nvi', '1 Crônicas', '1cr', 24, 6, 'O escriba Semaías, filho do levita Natanael, registrou os nomes deles na presença do rei, dos líderes, dos sacerdotes Zadoque e Aimeleque, filho de Abiatar, e dos chefes de famílias dos sacerdotes e dos levitas; as famílias de Eleazar e de Itamar foram sorteadas alternadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11023, 'nvi', '1 Crônicas', '1cr', 24, 7, 'A primeira sorte caiu para Jeoiaribe, a segunda para Jedaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11024, 'nvi', '1 Crônicas', '1cr', 24, 8, 'a terceira para Harim, a quarta para Seorim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11025, 'nvi', '1 Crônicas', '1cr', 24, 9, 'a quinta para Malquias, a sexta para Miamim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11026, 'nvi', '1 Crônicas', '1cr', 24, 10, 'a sétima para Hacoz, a oitava para Abias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11027, 'nvi', '1 Crônicas', '1cr', 24, 11, 'a nona para Jesua, a décima para Secanias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11028, 'nvi', '1 Crônicas', '1cr', 24, 12, 'a décima primeira para Eliasibe, a décima segunda para Jaquim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11029, 'nvi', '1 Crônicas', '1cr', 24, 13, 'a décima terceira para Hupá, a décima quarta para Jesebeabe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11030, 'nvi', '1 Crônicas', '1cr', 24, 14, 'a décima quinta para Bilga, a décima sexta para Imer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11031, 'nvi', '1 Crônicas', '1cr', 24, 15, 'a décima sétima para Hezir, a décima oitava para Hapises,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11032, 'nvi', '1 Crônicas', '1cr', 24, 16, 'a décima nona para Petaías, a vigésima para Jeezquel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11033, 'nvi', '1 Crônicas', '1cr', 24, 17, 'a vigésima primeira para Jaquim, a vigésima segunda para Gamul,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11034, 'nvi', '1 Crônicas', '1cr', 24, 18, 'a vigésima terceira para Delaías, e a vigésima quarta para Maazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11035, 'nvi', '1 Crônicas', '1cr', 24, 19, 'Conforme essa ordem eles deveriam ministrar quando entrassem no templo do Senhor, de acordo com as prescrições que Arão, antepassado deles, lhes deixou, conforme o Senhor, o Deus de Israel, havia lhe ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11036, 'nvi', '1 Crônicas', '1cr', 24, 20, 'Estes foram os chefes dos outros levitas: dos descendentes de Anrão: Subael; dos descendentes de Subael: Jedias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11037, 'nvi', '1 Crônicas', '1cr', 24, 21, 'Quanto a Reabias, Issias foi o chefe dos seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11038, 'nvi', '1 Crônicas', '1cr', 24, 22, 'Dos descendentes de Isar: Selomote; dos filhos de Selomote: Jaate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11039, 'nvi', '1 Crônicas', '1cr', 24, 23, 'Dos descendentes de Hebrom: Jerias, o primeiro, Amarias, o segundo, Jaaziel, o terceiro, e Jecameão, o quarto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11040, 'nvi', '1 Crônicas', '1cr', 24, 24, 'Dos descendentes de Uziel: Mica; dos filhos de Mica: Samir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11041, 'nvi', '1 Crônicas', '1cr', 24, 25, 'Dos descendentes de Issias, irmão de Mica, Zacarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11042, 'nvi', '1 Crônicas', '1cr', 24, 26, 'Dos filhos de Merari: Mali e Musi. Dos filhos de Jaazias: Beno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11043, 'nvi', '1 Crônicas', '1cr', 24, 27, 'Os descendentes de Merari por Jaazias: Beno, Soão, Zacur e Ibri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11044, 'nvi', '1 Crônicas', '1cr', 24, 28, 'De Mali: Eleazar, que não teve filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11045, 'nvi', '1 Crônicas', '1cr', 24, 29, 'De Quis: Jerameel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11046, 'nvi', '1 Crônicas', '1cr', 24, 30, 'E foram estes os filhos de Musi: Mali, Éder e Jeremote. Esses foram os levitas, de acordo com as suas famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11047, 'nvi', '1 Crônicas', '1cr', 24, 31, 'Eles também tiraram sortes na presença do rei Davi e de Zadoque, de Aimeleque, e dos chefes de famílias dos sacerdotes e dos levitas, assim como fizeram seus irmãos, os descendentes de Arão. As famílias dos irmãos mais velhos foram tratadas da mesma maneira que as dos mais novos.');
