INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11048, 'nvi', '1 Crônicas', '1cr', 25, 1, 'Davi, junto com os comandantes do exército, separou alguns dos filhos de Asafe, de Hemã e de Jedutum para o ministério de profetizar ao som de harpas, liras e címbalos. Esta é a lista dos escolhidos para essa função:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11049, 'nvi', '1 Crônicas', '1cr', 25, 2, 'Dos filhos de Asafe: Zacur, José, Netanias e Asarela. Os filhos de Asafe estavam sob a sua supervisão que, por sua vez, profetizava sob a supervisão do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11050, 'nvi', '1 Crônicas', '1cr', 25, 3, 'Dos filhos de Jedutum: Gedalias, Zeri, Jesaías, Simei, Hasabias e Matitias, seis ao todo, sob a supervisão de seu pai, Jedutum, que profetizava ao som da harpa para agradecer e louvar ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11051, 'nvi', '1 Crônicas', '1cr', 25, 4, 'Dos filhos de Hemã: Buquias, Matanias, Uziel, Sebuel, Jeremote, Hananias, Hanani, Eliata, Gidalti, Romanti-Ézer, Josbecasa, Maloti, Hotir e Maaziote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11052, 'nvi', '1 Crônicas', '1cr', 25, 5, 'Todos esses eram filhos de Hemã, o vidente do rei. Esses lhe nasceram conforme as promessas de que Deus haveria de torná-lo poderoso. E Deus deu a Hemã catorze filhos e três filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11053, 'nvi', '1 Crônicas', '1cr', 25, 6, 'Todos esses homens estavam sob a supervisão de seus pais quando ministravam a música do templo do Senhor, com címbalos, liras e harpas, na casa de Deus. Asafe, Jedutum e Hemã estavam sob a supervisão do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11054, 'nvi', '1 Crônicas', '1cr', 25, 7, 'Junto com seus parentes, todos capazes e preparados para o ministério do louvor do Senhor, totalizavam 288.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11055, 'nvi', '1 Crônicas', '1cr', 25, 8, 'Então tiraram sortes entre jovens e velhos, mestres e discípulos para designar-lhes suas responsabilidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11056, 'nvi', '1 Crônicas', '1cr', 25, 9, 'A primeira sorte, caiu para José, filho de Asafe, com seus filhos e parentes; eram ao todo 12; a segunda, para Gedalias, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11057, 'nvi', '1 Crônicas', '1cr', 25, 10, 'a terceira, para Zacur, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11058, 'nvi', '1 Crônicas', '1cr', 25, 11, 'a quarta, para Izri, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11059, 'nvi', '1 Crônicas', '1cr', 25, 12, 'a quinta, para Netanias, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11060, 'nvi', '1 Crônicas', '1cr', 25, 13, 'a sexta, para Buquias, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11061, 'nvi', '1 Crônicas', '1cr', 25, 14, 'a sétima, para Jesarela, com seus filhos e parentes, eram ao todo 12;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11062, 'nvi', '1 Crônicas', '1cr', 25, 15, 'a oitava, para Jesaías, com seus filhos e parentes, eram ao todo 12;');
