INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11075, 'nvi', '1 Crônicas', '1cr', 25, 28, 'a vigésima primeira, para Hotir, com seus filhos e parentes, eram ao todo 12;');
