INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11079, 'nvi', '1 Crônicas', '1cr', 26, 1, 'Esta é a relação dos grupos dos porteiros: Dos coreítas, Meselemias, filho de Coré, da família de Asafe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11080, 'nvi', '1 Crônicas', '1cr', 26, 2, 'Foram estes os filhos de Meselemias: Zacarias, o primeiro, Jediael, o segundo, Zebadias, o terceiro, Jatniel, o quarto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11081, 'nvi', '1 Crônicas', '1cr', 26, 3, 'Elão, o quinto, Joanã, o sexto, e Elioenai, o sétimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11082, 'nvi', '1 Crônicas', '1cr', 26, 4, 'Foram estes os filhos de Obede-Edom: Semaías, o primeiro, Jeozabade, o segundo, Joá, o terceiro, Sacar, o quarto, Natanael, o quinto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11083, 'nvi', '1 Crônicas', '1cr', 26, 5, 'Amiel, o sexto, Issacar, o sétimo, e Peuletai, o oitavo. Pois Deus havia abençoado Obede-Edom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11084, 'nvi', '1 Crônicas', '1cr', 26, 6, 'Seu filho Semaías também teve filhos, que foram líderes na família do seu pai, pois eram homens capazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11085, 'nvi', '1 Crônicas', '1cr', 26, 7, 'Foram estes os filhos de Semaías: Otni, Rafael, Obede e Elzabade. Os parentes dele, Eliú e Semaquias, também foram homens capazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11086, 'nvi', '1 Crônicas', '1cr', 26, 8, 'Todos esses foram descendentes de Obede-Edom; eles e os seus filhos e parentes eram capazes e aptos para a obra. Eram ao todo 62 descendentes de Obede-Edom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11087, 'nvi', '1 Crônicas', '1cr', 26, 9, 'Meselemias teve 18 filhos e parentes chegados, todos eles homens capazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11088, 'nvi', '1 Crônicas', '1cr', 26, 10, 'Foram estes os filhos de Hosa, o merarita: Sinri, que foi nomeado chefe por seu pai, mesmo não sendo o mais velho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11089, 'nvi', '1 Crônicas', '1cr', 26, 11, 'Hilquias, o segundo, Tebalias, o terceiro, e Zacarias, o quarto. Os filhos e parentes de Hosa foram 13 ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11090, 'nvi', '1 Crônicas', '1cr', 26, 12, 'Essas divisões dos porteiros, feitas pelos chefes deles; eles cumpriam tarefas no serviço do templo do Senhor, assim como seus parentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11091, 'nvi', '1 Crônicas', '1cr', 26, 13, 'Tiraram sortes entre as famílias, incluindo jovens e velhos, para que cuidassem de cada porta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11092, 'nvi', '1 Crônicas', '1cr', 26, 14, 'E a porta leste coube a Selemias. Então tiraram sortes para seu filho Zacarias, sábio conselheiro, e a porta norte foi sorteada para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11093, 'nvi', '1 Crônicas', '1cr', 26, 15, 'A sorte da porta sul saiu para Obede-Edom, e a do depósito, para seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11094, 'nvi', '1 Crônicas', '1cr', 26, 16, 'A sorte da porta oeste e da porta Salequete, na rua de cima, saíram para Supim e Hosa. Os guardas ficavam um ao lado do outro:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11095, 'nvi', '1 Crônicas', '1cr', 26, 17, 'Havia seis levitas por dia no leste, quatro no norte, quatro no sul e dois de cada vez no depósito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11096, 'nvi', '1 Crônicas', '1cr', 26, 18, 'Quanto ao pátio a oeste, havia quatro na rua e dois no próprio pátio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11097, 'nvi', '1 Crônicas', '1cr', 26, 19, 'Foram essas as divisões dos porteiros, descendentes de Coré e Merari.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11098, 'nvi', '1 Crônicas', '1cr', 26, 20, 'Outros dos seus irmãos levitas estavam encarregados dos depósitos dos tesouros do templo de Deus e do depósito das dádivas sagradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11099, 'nvi', '1 Crônicas', '1cr', 26, 21, 'Os gersonitas, descendentes de Ladã, que eram chefes de famílias pertencentes a Ladã, foram Jeieli');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11100, 'nvi', '1 Crônicas', '1cr', 26, 22, 'e seus filhos Zetã e seu irmão Joel. Estavam encarregados da tesouraria do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11101, 'nvi', '1 Crônicas', '1cr', 26, 23, 'Dos filhos de Anrão, de Isar, de Hebrom e de Uziel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11102, 'nvi', '1 Crônicas', '1cr', 26, 24, 'Sebuel, um descendente de Gérson, filho de Moisés, era o oficial encarregado dos depósitos dos tesouros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11103, 'nvi', '1 Crônicas', '1cr', 26, 25, 'Seus parentes por parte de Eliézer foram seu filho Reabias, que foi o pai de Jesaías, o avô de Jorão, o bisavô de Zicri, o tataravô de Selomote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11104, 'nvi', '1 Crônicas', '1cr', 26, 26, 'Selomote e seus parentes estavam encarregados de todos os tesouros consagrados pelo rei Davi, pelos chefes de famílias que eram os comandantes de mil e de cem, e pelos outros líderes do exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11105, 'nvi', '1 Crônicas', '1cr', 26, 27, 'Eles consagravam parte dos despojos tomados em combate para a manutenção do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11106, 'nvi', '1 Crônicas', '1cr', 26, 28, 'E todas as dádivas consagradas pelo vidente Samuel e por Saul, filho de Quis, por Abner, filho de Ner, e por Joabe, filho de Zeruia, e todas as demais dádivas sagradas estavam sob os cuidados de Selomote e seus parentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11107, 'nvi', '1 Crônicas', '1cr', 26, 29, 'Dos filhos de Isar, Quenanias e seus filhos ficaram responsáveis pelos negócios públicos de Israel, atuando como oficiais e juízes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11108, 'nvi', '1 Crônicas', '1cr', 26, 30, 'Dos filhos de Hebrom, Hasabias e seus parentes, que ficaram responsáveis em Israel, a oeste do Jordão, por todo o trabalho do Senhor e pelo serviço do rei; ao todo eram mil e setecentos homens capazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11109, 'nvi', '1 Crônicas', '1cr', 26, 31, 'De acordo com os registros genealógicos das famílias hebronitas, Jerias foi o chefe delas. No ano quarenta do reinado de Davi fez-se uma busca nos registros, e entre os descendentes de Hebrom encontraram-se homens capazes, em Jazar de Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11110, 'nvi', '1 Crônicas', '1cr', 26, 32, 'Jerias tinha dois mil e setecentos parentes, homens capazes e chefes de famílias, que o rei Davi encarregou de todas as questões pertinentes a Deus e aos negócios do rei nas tribos de Rúben e de Gade, e da metade da tribo de Manassés.');
