INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11111, 'nvi', '1 Crônicas', '1cr', 27, 1, 'Esta é a lista dos israelitas, chefes de famílias, comandantes de mil e comandantes de cem; oficiais que serviam o rei na supervisão das divisões do exército que estavam de serviço mês a mês, durante o ano. Cada divisão era constituída por 24. 000 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11112, 'nvi', '1 Crônicas', '1cr', 27, 2, 'Encarregado da primeira divisão de 24. 000 homens, para o primeiro mês, estava Jasobeão, filho de Zabdiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11113, 'nvi', '1 Crônicas', '1cr', 27, 3, 'Ele era descendente de Perez e chefe de todos os oficiais do exército para o primeiro mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11114, 'nvi', '1 Crônicas', '1cr', 27, 4, 'Encarregado da divisão para o segundo mês estava Dodai, descendente de Aoí; Miclote era o líder da sua divisão que contava 24. 000 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11115, 'nvi', '1 Crônicas', '1cr', 27, 5, 'O terceiro comandante do exército, para o terceiro mês, foi Benaia, filho do sacerdote Joiada. Ele era chefe da sua divisão de 24. 000 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11116, 'nvi', '1 Crônicas', '1cr', 27, 6, 'Esse Benaia foi guerreiro, chefe dos Trinta. Seu filho Amizabade estava encarregado da sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11117, 'nvi', '1 Crônicas', '1cr', 27, 7, 'O quarto, para o quarto mês, foi Asael, irmão de Joabe; seu filho Zebadias foi seu sucessor. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11118, 'nvi', '1 Crônicas', '1cr', 27, 8, 'O quinto, para o quinto mês, foi o comandante Samute, o izraíta. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11119, 'nvi', '1 Crônicas', '1cr', 27, 9, 'O sexto, para o sexto mês, foi Ira, filho de Iques, de Tecoa. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11120, 'nvi', '1 Crônicas', '1cr', 27, 10, 'O sétimo, para o sétimo mês, foi Helez, de Pelom, descendente de Efraim. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11121, 'nvi', '1 Crônicas', '1cr', 27, 11, 'O oitavo, para o oitavo mês, foi Sibecai, de Husate, da família de Zerá. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11122, 'nvi', '1 Crônicas', '1cr', 27, 12, 'O nono, para o nono mês, foi Abiezer, de Anatote, da tribo de Benjamim. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11123, 'nvi', '1 Crônicas', '1cr', 27, 13, 'O décimo, para o décimo mês, foi Maarai, de Netofate, da família de Zerá. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11124, 'nvi', '1 Crônicas', '1cr', 27, 14, 'O décimo primeiro, para o décimo primeiro mês, foi Benaia, de Piratom, descendente de Efraim. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11125, 'nvi', '1 Crônicas', '1cr', 27, 15, 'O décimo segundo, para o décimo segundo mês, foi Heldai, de Netofate, da família de Otoniel. Havia 24. 000 homens em sua divisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11126, 'nvi', '1 Crônicas', '1cr', 27, 16, 'Estes foram os líderes das tribos de Israel: de Rúben: Eliézer, filho de Zicri; de Simeão: Sefatias, filho de Maaca;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11127, 'nvi', '1 Crônicas', '1cr', 27, 17, 'de Levi: Hasabias, filho de Quemuel; de Arão: Zadoque;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11128, 'nvi', '1 Crônicas', '1cr', 27, 18, 'de Judá: Eliú, irmão de Davi; de Issacar: Onri, filho de Micael;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11129, 'nvi', '1 Crônicas', '1cr', 27, 19, 'de Zebulom: Ismaías, filho de Obadias; de Naftali: Jeremote, filho de Azriel;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11130, 'nvi', '1 Crônicas', '1cr', 27, 20, 'dos descendentes de Efraim: Oséias, filho de Azazias; da metade da tribo de Manassés: Joel, filho de Pedaías;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11131, 'nvi', '1 Crônicas', '1cr', 27, 21, 'da outra metade da tribo de Manassés, em Gileade: Ido, filho de Zacarias; de Benjamim: Jaasiel, filho de Abner;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11132, 'nvi', '1 Crônicas', '1cr', 27, 22, 'de Dã: Azareel, filho de Jeroão. Foram esses os líderes das tribos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11133, 'nvi', '1 Crônicas', '1cr', 27, 23, 'Davi não contou os homens com menos de vinte anos, pois o Senhor havia prometido tornar Israel tão numeroso quanto as estrelas do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11134, 'nvi', '1 Crônicas', '1cr', 27, 24, 'Joabe, filho de Zeruia, começou a contar os homens, mas não pôde terminar. A ira divina caiu sobre Israel por causa desse recenseamento, e o resultado não entrou nos registros históricos do rei Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11135, 'nvi', '1 Crônicas', '1cr', 27, 25, 'Azmavete, filho de Adiel, estava encarregado dos tesouros do palácio. Jônatas, filho de Uzias, estava encarregado dos depósitos do rei nos distritos distantes, nas cidades, nos povoados e nas torres de sentinela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11136, 'nvi', '1 Crônicas', '1cr', 27, 26, 'Ezri, filho de Quelube, estava encarregado dos trabalhadores rurais, que cultivavam a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11137, 'nvi', '1 Crônicas', '1cr', 27, 27, 'Simei, de Ramá, estava encarregado das vinhas. Zabdi, de Sifá, estava encarregado do vinho que era armazenado em tonéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11138, 'nvi', '1 Crônicas', '1cr', 27, 28, 'Baal-Hanã, de Gederá, estava encarregado das oliveiras e das figueiras bravas, na Sefelá. Joás estava encarregado do fornecimento de azeite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11139, 'nvi', '1 Crônicas', '1cr', 27, 29, 'Sitrai, de Sarom, estava encarregado dos rebanhos que pastavam em Sarom. Safate, filho de Adlai, estava encarregado dos rebanhos nos vales.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11140, 'nvi', '1 Crônicas', '1cr', 27, 30, 'O ismaelita Obil estava encarregado dos camelos. Jedias, de Meronote, estava encarregado dos jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11141, 'nvi', '1 Crônicas', '1cr', 27, 31, 'O hagareno Jaziz estava encarregado das ovelhas. Todos esses eram encarregados de cuidar dos bens do rei Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11142, 'nvi', '1 Crônicas', '1cr', 27, 32, 'Jônatas, tio de Davi, era conselheiro; homem sábio e também escriba. Jeiel, filho de Hacmoni, cuidava dos filhos do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11143, 'nvi', '1 Crônicas', '1cr', 27, 33, 'Aitofel era conselheiro do rei. Husai, o arquita, era amigo do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11144, 'nvi', '1 Crônicas', '1cr', 27, 34, 'Aitofel foi sucedido por Joiada, filho de Benaia, e por Abiatar. Joabe era o comandante do exército real.');
