INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10363, 'nvi', '1 Crônicas', '1cr', 3, 1, 'Estes foram os filhos de Davi nascidos em Hebrom: O seu filho mais velho era Amnom, filho de Ainoã de Jezreel; o segundo, Daniel, de Abigail, de Carmelo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10364, 'nvi', '1 Crônicas', '1cr', 3, 2, 'o terceiro, Absalão, de Maaca, filha de Talmai, rei de Gesur; o quarto, Adonias, de Hagite;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10365, 'nvi', '1 Crônicas', '1cr', 3, 3, 'o quinto, Sefatias, de Abital; e o sexto, Itreão, de sua mulher Eglá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10366, 'nvi', '1 Crônicas', '1cr', 3, 4, 'São esses os seis filhos de Davi que nasceram em Hebrom, onde ele reinou sete anos e seis meses. E, em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10367, 'nvi', '1 Crônicas', '1cr', 3, 5, 'onde Davi reinou trinta e três anos, nasceram-lhe os seguintes filhos: Siméia, Sobabe, Natã e Salomão, os quatro filhos que ele teve com Bate-Seba, filha de Amiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10368, 'nvi', '1 Crônicas', '1cr', 3, 6, 'Davi teve ainda mais nove filhos: Ibar, Elisua, Elpalete,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10369, 'nvi', '1 Crônicas', '1cr', 3, 7, 'Nogá, Nefegue, Jafia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10370, 'nvi', '1 Crônicas', '1cr', 3, 8, 'Elisama, Eliada e Elifelete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10371, 'nvi', '1 Crônicas', '1cr', 3, 9, 'Todos esses foram filhos de Davi, sem contar os filhos que teve com suas concubinas, e sua filha Tamar, irmã deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10372, 'nvi', '1 Crônicas', '1cr', 3, 10, 'O filho de Salomão foi Roboão; o filho de Roboão foi Abias; o filho de Abias, Asa; o filho de Asa, Josafá;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10373, 'nvi', '1 Crônicas', '1cr', 3, 11, 'o filho de Josafá, Jeorão; o filho de Jeorão, Acazias; o filho de Acazias, Joás;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10374, 'nvi', '1 Crônicas', '1cr', 3, 12, 'o filho de Joás, Amazias; o filho de Amazias, Azarias; o filho de Azarias, Jotão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10375, 'nvi', '1 Crônicas', '1cr', 3, 13, 'o filho de Jotão, Acaz; o filho de Acaz, Ezequias; o filho de Ezequias, Manassés;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10376, 'nvi', '1 Crônicas', '1cr', 3, 14, 'o filho de Manassés, Amom; o filho de Amom, Josias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10377, 'nvi', '1 Crônicas', '1cr', 3, 15, 'Os filhos de Josias foram: Joanã, o primeiro, Jeoaquim, o segundo, Zedequias, o terceiro, e Salum, o quarto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10378, 'nvi', '1 Crônicas', '1cr', 3, 16, 'Os sucessores de Jeoaquim foram: Joaquim e Zedequias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10379, 'nvi', '1 Crônicas', '1cr', 3, 17, 'Estes foram os filhos de Joaquim, que foi levado para o cativeiro: Sealtiel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10380, 'nvi', '1 Crônicas', '1cr', 3, 18, 'Malquirão, Pedaías, Senazar, Jecamias, Hosama e Nedabias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10381, 'nvi', '1 Crônicas', '1cr', 3, 19, 'Estes foram os filhos de Pedaías: Zorobabel e Simei. Estes foram os filhos de Zorobabel: Mesulão, Hananias e Selomite, irmã deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10382, 'nvi', '1 Crônicas', '1cr', 3, 20, 'Teve ainda mais cinco filhos: Hasubá, Oel, Berequias, Hasadias e Jusabe-Hesede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10383, 'nvi', '1 Crônicas', '1cr', 3, 21, 'Estes foram os descendentes de Hananias: Pelatias e Jesaías, e os filhos de Refaías, de Arnã, de Obadias e de Secanias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10384, 'nvi', '1 Crônicas', '1cr', 3, 22, 'Estes foram os descendentes de Secanias: Semaías e seus filhos Hatus, Igal, Bariá, Nearias e Safate; seis descendentes ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10385, 'nvi', '1 Crônicas', '1cr', 3, 23, 'Estes foram os três filhos de Nearias: Elioenai, Ezequias e Azricão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10386, 'nvi', '1 Crônicas', '1cr', 3, 24, 'Estes foram os sete filhos de Elioenai: Hodavias, Eliasibe, Pelaías, Acube, Joanã, Delaías e Anani.');
