INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10387, 'nvi', '1 Crônicas', '1cr', 4, 1, 'Estes foram os descendentes de Judá: Perez, Hezrom, Carmi, Hur e Sobal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10388, 'nvi', '1 Crônicas', '1cr', 4, 2, 'Reaías, filho de Sobal, gerou Jaate, e Jaate gerou Aumai e Laade. Esses foram os clãs dos zoratitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10389, 'nvi', '1 Crônicas', '1cr', 4, 3, 'Estes foram os filhos de Etã: Jezreel, Isma e Idbás. A irmã deles chamava-se Hazelelponi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10390, 'nvi', '1 Crônicas', '1cr', 4, 4, 'Depois teve Penuel, pai de Gedor, e Ézer, pai de Husá. Esses foram os descendentes de Hur, o filho mais velho de Efrate e pai de Belém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10391, 'nvi', '1 Crônicas', '1cr', 4, 5, 'Asur, fundador de Tecoa, teve duas mulheres: Helá e Naará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10392, 'nvi', '1 Crônicas', '1cr', 4, 6, 'Naará lhe deu Auzã, Héfer, Temeni e Haastari. Esses foram os filhos de Naará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10393, 'nvi', '1 Crônicas', '1cr', 4, 7, 'Estes foram os filhos de Helá: Zerete, Zoar, Etnã');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10394, 'nvi', '1 Crônicas', '1cr', 4, 8, 'e Coz, que gerou Anube e Zobeba e os clãs de Aarel, filho de Harum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10395, 'nvi', '1 Crônicas', '1cr', 4, 9, 'Jabez foi o homem mais respeitado de sua família. Sua mãe lhe deu o nome de Jabez, dizendo: "Com muitas dores o dei à luz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10396, 'nvi', '1 Crônicas', '1cr', 4, 10, 'Jabez orou ao Deus de Israel: "Ah, abençoa-me e aumenta as minhas terras! Que a tua mão esteja comigo, guardando-me de males e livrando-me de dores". E Deus atendeu ao seu pedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10397, 'nvi', '1 Crônicas', '1cr', 4, 11, 'Quelube, irmão de Suá, gerou Meir, pai de Estom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10398, 'nvi', '1 Crônicas', '1cr', 4, 12, 'Estom gerou Bete-Rafa, Paséia e Teína, fundador de Ir-Naás. Esses habitaram em Reca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10399, 'nvi', '1 Crônicas', '1cr', 4, 13, 'Estes foram os filhos de Quenaz: Otoniel e Seraías. Estes foram os filhos de Otoniel: Hatate e Meonotai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10400, 'nvi', '1 Crônicas', '1cr', 4, 14, 'Meonotai gerou Ofra. Seraías gerou Joabe, fundador de Ge-Harasim que recebeu esse nome porque os seus habitantes eram artesãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10401, 'nvi', '1 Crônicas', '1cr', 4, 15, 'Estes foram os filhos de Calebe, filho de Jefoné: Iru, Elá e Naã. O filho de Elá foi Quenaz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10402, 'nvi', '1 Crônicas', '1cr', 4, 16, 'Estes foram os filhos de Jealelel: Zife, Zifa, Tiria e Asareel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10403, 'nvi', '1 Crônicas', '1cr', 4, 17, 'Estes foram os filhos de Ezra: Jéter, Merede, Éfer e Jalom. Merede casou-se com Bitia, filha do faraó, e teve os seguintes filhos: Miriã, Samai e Isbá, fundador de Estemoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10404, 'nvi', '1 Crônicas', '1cr', 4, 18, 'Sua mulher judia deu à luz a Jerede, fundador de Gedor, a Héber, fundador de Socó, e a Jecutiel, fundador de Zanoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10405, 'nvi', '1 Crônicas', '1cr', 4, 19, 'Estes foram os filhos da mulher de Hodias, irmã de Naã: o pai de Queila, o garmita, e Estemoa, o maacatita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10406, 'nvi', '1 Crônicas', '1cr', 4, 20, 'Estes foram os filhos de Simão: Amnom, Rina, Bene-Hanã e Tilom. Estes foram os filhos de Isi: Zoete e Ben-Zoete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10407, 'nvi', '1 Crônicas', '1cr', 4, 21, 'Estes foram os filhos de Selá, filho de Judá: Er, pai de Leca; Lada, pai de Maressa. Selá também foi antepassado dos clãs dos que trabalhavam com linho em Bete-Asbéia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10408, 'nvi', '1 Crônicas', '1cr', 4, 22, 'de Joquim, dos homens de Cozeba, de Joás e de Sarafe, que governavam em Moabe e Jasubi-Leém. ( Estes registros são de épocas antigas. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10409, 'nvi', '1 Crônicas', '1cr', 4, 23, 'Eles eram oleiros e habitavam em Netaim e em Gederá, perto do rei, para quem trabalhavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10410, 'nvi', '1 Crônicas', '1cr', 4, 24, 'Estes foram os filhos de Simeão: Nemuel, Jamim, Jaribe, Zerá e Saul;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10411, 'nvi', '1 Crônicas', '1cr', 4, 25, 'O filho de Saul era Salum, pai de Mibsão, que foi o pai de Misma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10412, 'nvi', '1 Crônicas', '1cr', 4, 26, 'Estes foram os descendentes de Misma: seu filho Hamuel, pai de Zacur, que foi o pai de Simei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10413, 'nvi', '1 Crônicas', '1cr', 4, 27, 'Simei teve dezesseis filhos e seis filhas, mas seus irmãos não tiveram muitos filhos; por isso todos os seus clãs não se igualam em número à tribo de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10414, 'nvi', '1 Crônicas', '1cr', 4, 28, 'Eles viviam em Berseba, Moladá, Hazar-Sual,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10415, 'nvi', '1 Crônicas', '1cr', 4, 29, 'Bila, Ezém, Tolade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10416, 'nvi', '1 Crônicas', '1cr', 4, 30, 'Betuel, Hormá, Ziclague,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10417, 'nvi', '1 Crônicas', '1cr', 4, 31, 'Bete-Marcabote, Hazar-Susim, Bete-Biri e Saaraim. Essas foram as suas cidades até o reinado de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10418, 'nvi', '1 Crônicas', '1cr', 4, 32, 'Tinham também as cinco cidades de Etã, Aim, Rimom, Toquém e Asã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10419, 'nvi', '1 Crônicas', '1cr', 4, 33, 'com todos os povoados ao redor delas até Baalate. Nessas cidades viviam e mantinham um registro genealógico.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10420, 'nvi', '1 Crônicas', '1cr', 4, 34, 'Mesobabe, Janleque, Josa, filho de Amazias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10421, 'nvi', '1 Crônicas', '1cr', 4, 35, 'Joel, Jeú, filho de Josibias, neto de Seraías e bisneto de Asiel;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10422, 'nvi', '1 Crônicas', '1cr', 4, 36, 'também Elioenai, Jaacobá, Jesoaías, Asaías, Adiel, Jesimiel, Benaia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10423, 'nvi', '1 Crônicas', '1cr', 4, 37, 'e Ziza, filho de Sifi, neto de Alom, bisneto de Jedaías, trineto de Sinri e tetraneto de Semaías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10424, 'nvi', '1 Crônicas', '1cr', 4, 38, 'Essa é a lista dos líderes dos seus clãs. Suas famílias cresceram muito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10425, 'nvi', '1 Crônicas', '1cr', 4, 39, 'e por isso foram para os arredores de Gedor, a leste do vale, em busca de pastagens para os seus rebanhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10426, 'nvi', '1 Crônicas', '1cr', 4, 40, 'Encontraram muitas pastagens boas, numa região vasta, pacífica e tranqüila, onde alguns camitas tinham vivido anteriormente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10427, 'nvi', '1 Crônicas', '1cr', 4, 41, 'Durante o reinado de Ezequias, rei de Judá, esses homens aqui alistados chegaram e atacaram os camitas e os meunitas da região e os destruíram totalmente, como até hoje se pode ver. Depois ocuparam o lugar daqueles povos, pois havia pastagens para os seus rebanhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10428, 'nvi', '1 Crônicas', '1cr', 4, 42, 'E quinhentos desses simeonitas, liderados por Pelatias, Nearias, Refaías e Uziel, filhos de Isi, invadiram as colinas de Seir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10429, 'nvi', '1 Crônicas', '1cr', 4, 43, 'Eles mataram o restante dos amalequitas que tinha escapado, e ali vivem até hoje.');
