INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10430, 'nvi', '1 Crônicas', '1cr', 5, 1, 'Estes são os filhos de Rúben, o filho mais velho de Israel. ( De fato, ele era o mais velho, mas, por ter desonrado o leito de seu pai, seus direitos de filho mais velho foram dados aos filhos de José, filho de Israel, de modo que não foi alistado nos registros genealógicos como o primeiro filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10431, 'nvi', '1 Crônicas', '1cr', 5, 2, 'Embora Judá tenha sido o mais poderoso de seus irmãos e dele tenha vindo um líder, os direitos de filho mais velho foram dados a José ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10432, 'nvi', '1 Crônicas', '1cr', 5, 3, 'Os filhos de Rúben, filho mais velho de Israel, foram: Enoque, Palu, Hezrom e Carmi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10433, 'nvi', '1 Crônicas', '1cr', 5, 4, 'Estes foram os descendentes de Joel: Seu filho Semaías, pai de Gogue, que foi o pai de Simei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10434, 'nvi', '1 Crônicas', '1cr', 5, 5, 'pai de Mica, que foi o pai de Reaías, pai de Baal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10435, 'nvi', '1 Crônicas', '1cr', 5, 6, 'que foi o pai de Beera, a quem Tiglate-Pileser, rei da Assíria levou para o exílio. Beera era um líder da tribo de Rúben.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10436, 'nvi', '1 Crônicas', '1cr', 5, 7, 'Estes foram os parentes dele, de acordo com seus clãs, alistados conforme os seus registros genealógicos: Jeiel, o chefe, Zacarias');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10437, 'nvi', '1 Crônicas', '1cr', 5, 8, 'e Belá, filho de Azaz, neto de Sema e bisneto de Joel. Eles foram viver na região que vai desde Aroer até o monte Nebo e Baal-Meom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10438, 'nvi', '1 Crônicas', '1cr', 5, 9, 'A leste ocuparam a terra que vai até o deserto que se estende na direção do rio Eufrates, pois os seus rebanhos tinham aumentado muito em Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10439, 'nvi', '1 Crônicas', '1cr', 5, 10, 'Durante o reinado de Saul eles entraram em guerra contra os hagarenos e os derrotaram, passando a ocupar o acampamento deles por toda a região a leste de Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10440, 'nvi', '1 Crônicas', '1cr', 5, 11, 'Ao lado da tribo de Rúben ficou a tribo de Gade, desde a região de Basã até Salcá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10441, 'nvi', '1 Crônicas', '1cr', 5, 12, 'Joel foi o primeiro chefe de clãs em Basã, Safã, o segundo; os outros foram Janai e Safate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10442, 'nvi', '1 Crônicas', '1cr', 5, 13, 'Estes foram os parentes deles, por famílias: Micael, Mesulão, Seba, Jorai, Jacã, Zia e Héber. Eram sete ao todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10443, 'nvi', '1 Crônicas', '1cr', 5, 14, 'Eles eram descendentes de Abiail, filho de Huri, neto de Jaroa, bisneto de Gileade e trineto de Micael, que foi filho de Jesisai, neto de Jado e bisneto de Buz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10444, 'nvi', '1 Crônicas', '1cr', 5, 15, 'Aí, filho de Abdiel e neto de Guni, foi o chefe dessas famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10445, 'nvi', '1 Crônicas', '1cr', 5, 16, 'A tribo de Gade habitou em Gileade, em Basã e seus povoados, e em toda a extensão das terras de pastagem de Sarom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10446, 'nvi', '1 Crônicas', '1cr', 5, 17, 'Todos esses entraram nos registros genealógicos durante os reinados de Jotão, rei de Judá, e de Jeroboão, rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10447, 'nvi', '1 Crônicas', '1cr', 5, 18, 'As tribos de Rúben, Gade e a metade da tribo de Manassés tinham juntas quarenta e quatro mil e setecentos e sessenta homens de combate, capazes de empunhar escudo e espada, de usar o arco, e treinados para a guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10448, 'nvi', '1 Crônicas', '1cr', 5, 19, 'Eles entraram em guerra contra os hagarenos e seus aliados Jetur, Nafis e Nodabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10449, 'nvi', '1 Crônicas', '1cr', 5, 20, 'Durante a batalha clamaram a Deus, que os ajudou, entregando os hagarenos e todos os seus aliados nas suas mãos. Deus os atendeu, porque confiaram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10450, 'nvi', '1 Crônicas', '1cr', 5, 21, 'Tomaram dos hagarenos o rebanho de cinqüenta mil camelos, duzentas e cinqüenta mil ovelhas e dois mil jumentos. Também fizeram cem mil prisioneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10451, 'nvi', '1 Crônicas', '1cr', 5, 22, 'E muitos foram os inimigos mortos, pois a batalha era de Deus. Eles ocuparam aquela terra até a época do exílio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10452, 'nvi', '1 Crônicas', '1cr', 5, 23, 'A metade da tribo de Manassés era numerosa e se estabeleceu na região que vai de Basã a Baal-Hermom, isto é, até Senir, o monte Hermom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10453, 'nvi', '1 Crônicas', '1cr', 5, 24, 'Estes eram os chefes das famílias dessa tribo: Éfer, Isi, Eliel, Azriel, Jeremias, Hodavias e Jadiel. Eram soldados valentes, homens famosos, e chefes das famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10454, 'nvi', '1 Crônicas', '1cr', 5, 25, 'Mas foram infiéis para com o Deus dos seus antepassados e se prostituíram, seguindo os deuses dos povos que Deus tinha destruído diante deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10455, 'nvi', '1 Crônicas', '1cr', 5, 26, 'Por isso o Deus de Israel incitou Pul, que é Tiglate-Pileser, rei da Assíria, a levar as tribos de Rúben, de Gade e a metade da tribo de Manassés para Hala, para Habor, para Hara e para o rio Gozã, onde estão até hoje.');
