INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10537, 'nvi', '1 Crônicas', '1cr', 7, 1, 'Estes foram os quatro filhos de Issacar: Tolá, Puá, Jasube e Sinrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10538, 'nvi', '1 Crônicas', '1cr', 7, 2, 'Estes foram os filhos de Tolá: Uzi, Refaías, Jeriel, Jamai, Ibsão e Samuel, chefes dos seus clãs. No reinado de Davi, os descendentes de Tolá alistados em suas genealogias como homens de combate chegavam a 22. 600.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10539, 'nvi', '1 Crônicas', '1cr', 7, 3, 'O filho de Uzi foi Israías. Estes foram os filhos de Israías: Micael, Obadias, Joel e Issias. Todos os cinco eram chefes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10540, 'nvi', '1 Crônicas', '1cr', 7, 4, 'que tinham muitas mulheres e filhos. Por isso, conforme a genealogia de sua família, eles contavam com 36. 000 homens prontos para o combate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10541, 'nvi', '1 Crônicas', '1cr', 7, 5, 'Juntamente com seus parentes, os homens de combate de todos os clãs de Issacar, conforme alistados em sua genealogia, chegavam ao todo a 87. 000.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10542, 'nvi', '1 Crônicas', '1cr', 7, 6, 'Estes foram os três filhos de Benjamim: Belá, Bequer e Jediael.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10543, 'nvi', '1 Crônicas', '1cr', 7, 7, 'Estes foram os filhos de Belá: Esbom, Uzi, Uziel, Jeremote e Iri, cinco chefes de famílias. Seu registro genealógico alistava 22. 034 homens de combate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10544, 'nvi', '1 Crônicas', '1cr', 7, 8, 'Estes foram os filhos de Bequer: Zemira, Joás, Eliézer, Elioenai, Onri, Jeremote, Abias, Anatote e Alemete. Todos esses eram filhos de Bequer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10545, 'nvi', '1 Crônicas', '1cr', 7, 9, 'O registro genealógico deles alistava os chefes de famílias e 20. 200 homens de combate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10546, 'nvi', '1 Crônicas', '1cr', 7, 10, 'O filho de Jediael foi Bilã. Estes foram os filhos de Bilã: Jeús, Benjamim, Eúde, Quenaaná, Zetã, Társis e Aisaar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10547, 'nvi', '1 Crônicas', '1cr', 7, 11, 'Todos esses descendentes de Jediael eram chefes de famílias que contavam com 17. 200 homens de combate prontos para a guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10548, 'nvi', '1 Crônicas', '1cr', 7, 12, 'Supim e Hupim eram filhos de Ir; e Husim era filho de Aer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10549, 'nvi', '1 Crônicas', '1cr', 7, 13, 'Estes foram os filhos de Naftali: Jaziel, Guni, Jezer e Silém, netos de Bila.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10550, 'nvi', '1 Crônicas', '1cr', 7, 14, 'Estes foram os descendentes de Manassés: Asriel, filho de sua concubina araméia, que também deu à luz Maquir, pai de Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10551, 'nvi', '1 Crônicas', '1cr', 7, 15, 'Maquir casou-se com Maaca, irmã de Hupim e Supim. Outro descendente de Manassés chamava-se Zelofeade, o qual só teve filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10552, 'nvi', '1 Crônicas', '1cr', 7, 16, 'Maaca, mulher de Maquir, deu à luz um filho, a quem deu o nome de Perez. O nome de seu irmão era Seres, cujos filhos chamavam-se Ulão e Requém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10553, 'nvi', '1 Crônicas', '1cr', 7, 17, 'O filho de Ulão foi Bedã. Esses foram os descendentes de Gileade, filho de Maquir, e neto de Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10554, 'nvi', '1 Crônicas', '1cr', 7, 18, 'Sua irmã Hamolequete deu à luz Isode, Abiezer e Maalá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10555, 'nvi', '1 Crônicas', '1cr', 7, 19, 'Estes foram os filhos de Semida: Aiã, Siquém, Liqui e Anião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10556, 'nvi', '1 Crônicas', '1cr', 7, 20, 'Estes foram os descendentes de Efraim: Sutela, que foi o pai de Berede, pai de Taate, pai de Eleada, que foi o pai de Taate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10557, 'nvi', '1 Crônicas', '1cr', 7, 21, 'pai de Zabade, pai de Sutela. Ézer e Eleade, filhos de Efraim, foram mortos por homens da cidade de Gate, quando tentavam roubar os rebanhos deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10558, 'nvi', '1 Crônicas', '1cr', 7, 22, 'Efraim chorou muitos dias por eles, e seus parentes vieram consolá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10559, 'nvi', '1 Crônicas', '1cr', 7, 23, 'Depois ele se deitou de novo com sua mulher, e ela engravidou e deu à luz um filho. Ele o chamou Berias, pois tinha acontecido uma desgraça em sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10560, 'nvi', '1 Crônicas', '1cr', 7, 24, 'Sua filha chamava-se Seerá. Foi ela que fundou Bete-Horom Alta e Bete-Horom Baixa e também Uzém-Seerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10561, 'nvi', '1 Crônicas', '1cr', 7, 25, 'O filho de Berias foi Refa, pai de Resefe, que foi o pai de Telá, pai de Taã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10562, 'nvi', '1 Crônicas', '1cr', 7, 26, 'pai de Ladã, pai de Amiúde, que foi o pai de Elisama,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10563, 'nvi', '1 Crônicas', '1cr', 7, 27, 'pai de Num, que foi o pai de Josué.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10564, 'nvi', '1 Crônicas', '1cr', 7, 28, 'Suas terras e cidades incluíam Betel e os povoados ao redor, Naarã a leste, Gezer e seus povoados a oeste, e Siquém e Aiá com os seus povoados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10565, 'nvi', '1 Crônicas', '1cr', 7, 29, 'A tribo de Manassés controlava as cidades de Bete-Seã, Taanaque, Megido e Dor, com seus respectivos povoados. Os descendentes de José, filho de Israel, viviam nessas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10566, 'nvi', '1 Crônicas', '1cr', 7, 30, 'Estes foram os filhos de Aser: Imna, Isvá, Isvi e Berias. A irmã deles chamava-se Sera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10567, 'nvi', '1 Crônicas', '1cr', 7, 31, 'Estes foram os filhos de Berias: Héber e Malquiel, que foi o pai de Birzavite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10568, 'nvi', '1 Crônicas', '1cr', 7, 32, 'Héber gerou Jaflete, Somer e Hotão, e da irmã deles, Suá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10569, 'nvi', '1 Crônicas', '1cr', 7, 33, 'Estes foram os filhos de Jaflete: Pasaque, Bimal e Asvate. Esses foram os filhos de Jaflete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10570, 'nvi', '1 Crônicas', '1cr', 7, 34, 'Estes foram os filhos de Somer: Aí, Roga, Jeubá e Arã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10571, 'nvi', '1 Crônicas', '1cr', 7, 35, 'Estes foram os filhos de Helém, irmão de Somer: Zofa, Imna, Seles e Amal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10572, 'nvi', '1 Crônicas', '1cr', 7, 36, 'Estes foram os filhos de Zofa: Suá, Harnefer, Sual, Beri, Inra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10573, 'nvi', '1 Crônicas', '1cr', 7, 37, 'Bezer, Hode, Samá, Silsa, Itrã e Beera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10574, 'nvi', '1 Crônicas', '1cr', 7, 38, 'Estes foram os filhos de Jéter: Jefoné, Pispa e Ara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10575, 'nvi', '1 Crônicas', '1cr', 7, 39, 'Estes foram os filhos de Ula: Ara, Haniel e Rizia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10576, 'nvi', '1 Crônicas', '1cr', 7, 40, 'Todos esses foram descendentes de Aser. Eram chefes de famílias, homens escolhidos, soldados valentes e líderes de destaque. O número dos alistados para combate no exército deles chegou a 26. 000.');
