INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10617, 'nvi', '1 Crônicas', '1cr', 9, 1, 'Todos os israelitas foram alistados nas genealogias dos registros históricos dos reis de Israel. O povo de Judá foi levado prisioneiro para a Babilônia por sua infidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10618, 'nvi', '1 Crônicas', '1cr', 9, 2, 'Os primeiros a voltarem às suas propriedades e às suas cidades foram algumas pessoas do povo e alguns sacerdotes, levitas e servidores do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10619, 'nvi', '1 Crônicas', '1cr', 9, 3, 'Os de Judá, de Benjamim e de Efraim e Manassés que se instalaram em Jerusalém foram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10620, 'nvi', '1 Crônicas', '1cr', 9, 4, 'Utai, filho de Amiúde, neto de Onri, bisneto de Inri e trineto de Bani, um descendente de Perez, filho de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10621, 'nvi', '1 Crônicas', '1cr', 9, 5, 'Dos descendentes de Selá: O primogênito Asaías com seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10622, 'nvi', '1 Crônicas', '1cr', 9, 6, 'Dos descendentes de Zerá: Jeuel. Os de Judá chegaram a 690.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10623, 'nvi', '1 Crônicas', '1cr', 9, 7, 'Dos benjamitas: Salu, filho de Mesulão, neto de Hodavias e bisneto de Hassenua;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10624, 'nvi', '1 Crônicas', '1cr', 9, 8, 'Ibnéias, filho de Jeroão; Elá, filho de Uzi, filho de Micri; e Mesulão, filho de Sefatias, filho de Reuel, filho de Ibnias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10625, 'nvi', '1 Crônicas', '1cr', 9, 9, 'Da tribo de Benjamim, relacionados em sua genealogia, eram 956. Todos esses homens eram chefes de suas famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10626, 'nvi', '1 Crônicas', '1cr', 9, 10, 'Dos sacerdotes: Jedaías, Jeoiaribe, Jaquim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10627, 'nvi', '1 Crônicas', '1cr', 9, 11, 'Azarias, filho de Hilquias, neto de Mesulão, bisneto de Zadoque, trineto de Meraiote e tetraneto de Aitube, o líder encarregado do templo de Deus;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10628, 'nvi', '1 Crônicas', '1cr', 9, 12, 'Adaías, filho de Jeroão, neto de Pasur e bisneto de Malquias; e Masai, filho de Adiel, neto de Jazera, bisneto de Mesulão, trineto de Mesilemite e tetraneto de Imer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10629, 'nvi', '1 Crônicas', '1cr', 9, 13, 'O número de sacerdotes que eram chefes de famílias era 1. 760. Eram homens capazes, e sua responsabilidade era ministrar no templo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10630, 'nvi', '1 Crônicas', '1cr', 9, 14, 'Dos levitas: Semaías, filho de Hassube, neto de Azricão e bisneto de Hasabias, um merarita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10631, 'nvi', '1 Crônicas', '1cr', 9, 15, 'Baquebacar, Heres, Galal e Matanias, filho de Mica, neto de Zicri e bisneto de Asafe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10632, 'nvi', '1 Crônicas', '1cr', 9, 16, 'Obadias, filho de Semaías, neto de Galal e bisneto de Jedutum; e Berequias, filho de Asa e neto de Elcana, que vivia nos povoados dos netofatitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10633, 'nvi', '1 Crônicas', '1cr', 9, 17, 'Os guardas das portas eram: Salum, o chefe, Acube, Talmom, Aimã e os irmãos deles, sendo até hoje');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10634, 'nvi', '1 Crônicas', '1cr', 9, 18, 'os guardas da porta do Rei, a leste. Salum era o chefe. Esses eram os guardas das portas que pertenciam ao acampamento dos levitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10635, 'nvi', '1 Crônicas', '1cr', 9, 19, 'Salum, filho de Coré, neto de Ebiasafe e bisneto de Corá, e seus parentes, os coreítas, guardas das portas, responsáveis por guardar as entradas da Tenda, como os seus antepassados tinham sido responsáveis por guardar a entrada da habitação do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10636, 'nvi', '1 Crônicas', '1cr', 9, 20, 'Naquela época, Finéias, filho de Eleazar, estivera encarregado dos guardas das portas, e o Senhor estava com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10637, 'nvi', '1 Crônicas', '1cr', 9, 21, 'Zacarias, filho de Meselemias, era o guarda das portas da entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10638, 'nvi', '1 Crônicas', '1cr', 9, 22, 'A soma total dos escolhidos para serem guardas das portas, registrados nas genealogias dos seus povoados, era de 212. Eles haviam sido designados para esses postos de confiança por Davi e pelo vidente Samuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10639, 'nvi', '1 Crônicas', '1cr', 9, 23, 'Eles e os seus descendentes foram encarregados de vigiar as portas do templo do Senhor, o templo chamado Tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10640, 'nvi', '1 Crônicas', '1cr', 9, 24, 'Os guardas vigiavam as portas nos quatro lados: norte, sul, leste e oeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10641, 'nvi', '1 Crônicas', '1cr', 9, 25, 'Seus parentes, residentes em seus povoados, tinham que vir de tempos em tempos e trabalhar com eles por períodos de sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10642, 'nvi', '1 Crônicas', '1cr', 9, 26, 'Mas os quatro principais guardas das portas, que eram levitas, receberam a responsabilidade de tomar conta das salas e da tesouraria do templo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10643, 'nvi', '1 Crônicas', '1cr', 9, 27, 'Eles passavam a noite perto do templo de Deus, pois tinham o dever de vigiá-la e de abrir as portas todas as manhãs.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10644, 'nvi', '1 Crônicas', '1cr', 9, 28, 'Alguns levitas estavam encarregados dos utensílios utilizados no culto no templo; eles os contavam quando eram retirados e quando eram devolvidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10645, 'nvi', '1 Crônicas', '1cr', 9, 29, 'Outros eram responsáveis pelos móveis e por todos os demais utensílios do santuário, bem como pela farinha, pelo vinho, pelo óleo, pelo incenso e pelas especiarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10646, 'nvi', '1 Crônicas', '1cr', 9, 30, 'E ainda outros cuidavam da manipulação das especiarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10647, 'nvi', '1 Crônicas', '1cr', 9, 31, 'Um levita chamado Matitias, filho mais velho do coreíta Salum, tinha a responsabilidade de assar os pães para as ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10648, 'nvi', '1 Crônicas', '1cr', 9, 32, 'E dentre os coatitas, seus irmãos, alguns estavam encarregados de preparar os pães que são postos sobre a mesa todo sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10649, 'nvi', '1 Crônicas', '1cr', 9, 33, 'Os cantores, chefes de famílias levitas, permaneciam nas salas do templo e estavam isentos de outros deveres, pois dia e noite se dedicavam à sua própria tarefa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10650, 'nvi', '1 Crônicas', '1cr', 9, 34, 'Todos esses eram chefes de famílias levitas, alistados como líderes em suas genealogias, e moravam em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10651, 'nvi', '1 Crônicas', '1cr', 9, 35, 'Jeiel, pai de Gibeom, morava em Gibeom. O nome de sua mulher era Maaca,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10652, 'nvi', '1 Crônicas', '1cr', 9, 36, 'e o de seu filho mais velho, Abdom. Depois nasceram Zur, Quis, Baal, Ner, Nadabe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10653, 'nvi', '1 Crônicas', '1cr', 9, 37, 'Gedor, Aiô, Zacarias e Miclote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10654, 'nvi', '1 Crônicas', '1cr', 9, 38, 'Miclote gerou Siméia. Eles também moravam perto de seus parentes em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10655, 'nvi', '1 Crônicas', '1cr', 9, 39, 'Ner gerou Quis, Quis gerou Saul, Saul gerou Jônatas, Malquisua, Abinadabe e Esbaal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10656, 'nvi', '1 Crônicas', '1cr', 9, 40, 'Este foi o filho de Jônatas: Meribe-Baal, que gerou Mica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10657, 'nvi', '1 Crônicas', '1cr', 9, 41, 'Estes foram os filhos de Mica: Pitom, Meleque, Taréia e Acaz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10658, 'nvi', '1 Crônicas', '1cr', 9, 42, 'Acaz gerou Jadá, Jadá gerou Alemete, Azmavete e Zinri, e Zinri gerou Mosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10659, 'nvi', '1 Crônicas', '1cr', 9, 43, 'Mosa gerou Bineá, cujo filho foi Refaías, o filho deste foi Eleasa, pai de Azel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10660, 'nvi', '1 Crônicas', '1cr', 9, 44, 'Azel teve seis filhos, e os nomes deles foram: Azricão, Bocru, Ismael, Searias, Obadias e Hanã. Esses foram os filhos de Azel.');
