INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30552, 'nvi', '1 João', '1jo', 2, 1, 'Meus filhinhos, escrevo-lhes estas coisas para que vocês não pequem. Se, porém, alguém pecar, temos um intercessor junto ao Pai, Jesus Cristo, o Justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30553, 'nvi', '1 João', '1jo', 2, 2, 'Ele é a propiciação pelos nossos pecados, e não somente pelos nossos, mas também pelos pecados de todo o mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30554, 'nvi', '1 João', '1jo', 2, 3, 'Sabemos que o conhecemos, se obedecemos aos seus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30555, 'nvi', '1 João', '1jo', 2, 4, 'Aquele que diz: "Eu o conheço", mas não obedece aos seus mandamentos, é mentiroso, e a verdade não está nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30556, 'nvi', '1 João', '1jo', 2, 5, 'Mas, se alguém obedece à sua palavra, nele verdadeiramente o amor de Deus está aperfeiçoado. Desta forma sabemos que estamos nele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30557, 'nvi', '1 João', '1jo', 2, 6, 'aquele que afirma que permanece nele, deve andar como ele andou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30558, 'nvi', '1 João', '1jo', 2, 7, 'Amados, não lhes escrevo um mandamento novo, mas um mandamento antigo, que vocês têm desde o princípio: a mensagem que ouviram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30559, 'nvi', '1 João', '1jo', 2, 8, 'No entanto, eu lhes escrevo um mandamento novo, o qual é verdadeiro nele e em vocês, pois as trevas estão se dissipando e já brilha a verdadeira luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30560, 'nvi', '1 João', '1jo', 2, 9, 'Quem afirma estar na luz mas odeia seu irmão, continua nas trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30561, 'nvi', '1 João', '1jo', 2, 10, 'Quem ama seu irmão permanece na luz, e nele não há causa de tropeço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30562, 'nvi', '1 João', '1jo', 2, 11, 'Mas quem odeia seu irmão está nas trevas e anda nas trevas; não sabe para onde vai, porque as trevas o cegaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30563, 'nvi', '1 João', '1jo', 2, 12, 'Filhinhos, eu lhes escrevo porque os seus pecados foram perdoados, graças ao nome de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30564, 'nvi', '1 João', '1jo', 2, 13, 'Pais, eu lhes escrevo porque vocês conhecem aquele que é desde o princípio. Jovens, eu lhes escrevo porque venceram o Maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30565, 'nvi', '1 João', '1jo', 2, 14, 'Filhinhos, eu lhes escrevi porque vocês conhecem o Pai. Pais, eu lhes escrevi porque vocês conhecem aquele que é desde o princípio. Jovens, eu lhes escrevi, porque vocês são fortes, e em vocês a Palavra de Deus permanece e vocês venceram o Maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30566, 'nvi', '1 João', '1jo', 2, 15, 'Não amem o mundo nem o que nele há. Se alguém amar o mundo, o amor do Pai não está nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30567, 'nvi', '1 João', '1jo', 2, 16, 'Pois tudo o que há no mundo — a cobiça da carne, a cobiça dos olhos e a ostentação dos bens — não provém do Pai, mas do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30568, 'nvi', '1 João', '1jo', 2, 17, 'O mundo e a sua cobiça passam, mas aquele que faz a vontade de Deus permanece para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30569, 'nvi', '1 João', '1jo', 2, 18, 'Filhinhos, esta é a última hora; e, assim como vocês ouviram que o anticristo está vindo, já agora muitos anticristos têm surgido. Por isso sabemos que esta é a última hora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30570, 'nvi', '1 João', '1jo', 2, 19, 'Eles saíram do nosso meio, mas na realidade não eram dos nossos, pois, se fossem dos nossos, teriam permanecido conosco; o fato de terem saído mostra que nenhum deles era dos nossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30571, 'nvi', '1 João', '1jo', 2, 20, 'Mas vocês têm uma unção que procede do Santo, e todos vocês têm conhecimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30572, 'nvi', '1 João', '1jo', 2, 21, 'Não lhes escrevo porque não conhecem a verdade, mas porque vocês a conhecem e porque nenhuma mentira procede da verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30573, 'nvi', '1 João', '1jo', 2, 22, 'Quem é o mentiroso, senão aquele que nega que Jesus é o Cristo? Este é o anticristo: aquele que nega o Pai e o Filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30574, 'nvi', '1 João', '1jo', 2, 23, 'Todo o que nega o Filho também não tem o Pai; quem confessa publicamente o Filho tem também o Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30575, 'nvi', '1 João', '1jo', 2, 24, 'Quanto a vocês, cuidem para que aquilo que ouviram desde o princípio permaneça em vocês. Se o que ouviram desde o princípio permanecer em vocês, vocês também permanecerão no Filho e no Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30576, 'nvi', '1 João', '1jo', 2, 25, 'E esta é a promessa que ele nos fez: a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30577, 'nvi', '1 João', '1jo', 2, 26, 'Escrevo-lhes estas coisas a respeito daqueles que os querem enganar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30578, 'nvi', '1 João', '1jo', 2, 27, 'Quanto a vocês, a unção que receberam dele permanece em vocês, e não precisam que alguém os ensine; mas, como a unção dele recebida, que é verdadeira e não falsa, os ensina acerca de todas as coisas, permaneçam nele como ele os ensinou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30579, 'nvi', '1 João', '1jo', 2, 28, 'Filhinhos, agora permaneçam nele para que, quando ele se manifestar, tenhamos confiança e não sejamos envergonhados diante dele na sua vinda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30580, 'nvi', '1 João', '1jo', 2, 29, 'Se vocês sabem que ele é justo, saibam também que todo aquele que pratica a justiça é nascido dele.');
