INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30581, 'nvi', '1 João', '1jo', 3, 1, 'Vejam como é grande o amor que o Pai nos concedeu: que fôssemos chamados filhos de Deus, o que de fato somos! Por isso o mundo não nos conhece, porque não o conheceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30582, 'nvi', '1 João', '1jo', 3, 2, 'Amados, agora somos filhos de Deus, e ainda não se manifestou o que havemos de ser, mas sabemos que, quando ele se manifestar, seremos semelhantes a ele, pois o veremos como ele é.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30583, 'nvi', '1 João', '1jo', 3, 3, 'Todo aquele que nele tem esta esperança purifica-se a si mesmo, assim como ele é puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30584, 'nvi', '1 João', '1jo', 3, 4, 'Todo aquele que pratica o pecado transgride a Lei; de fato, o pecado é a transgressão da Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30585, 'nvi', '1 João', '1jo', 3, 5, 'Vocês sabem que ele se manifestou para tirar os nossos pecados, e nele não há pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30586, 'nvi', '1 João', '1jo', 3, 6, 'Todo aquele que nele permanece não está no pecado. Todo aquele que está no pecado não o viu nem o conheceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30587, 'nvi', '1 João', '1jo', 3, 7, 'Filhinhos, não deixem que ninguém os engane. Aquele que pratica a justiça é justo, assim como ele é justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30588, 'nvi', '1 João', '1jo', 3, 8, 'Aquele que pratica o pecado é do diabo, porque o diabo vem pecando desde o princípio. Para isso o Filho de Deus se manifestou: para destruir as obras do diabo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30589, 'nvi', '1 João', '1jo', 3, 9, 'Todo aquele que é nascido de Deus não pratica o pecado, porque a semente de Deus permanece nele; ele não pode estar no pecado, porque é nascido de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30590, 'nvi', '1 João', '1jo', 3, 10, 'Desta forma sabemos quem são os filhos de Deus e quem são os filhos do diabo: quem não pratica a justiça não procede de Deus; e também quem não ama seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30591, 'nvi', '1 João', '1jo', 3, 11, 'Esta é a mensagem que vocês ouviram desde o princípio: que nos amemos uns aos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30592, 'nvi', '1 João', '1jo', 3, 12, 'Não sejamos como Caim, que pertencia ao Maligno e matou seu irmão. E por que o matou? Porque suas obras eram más e as de seu irmão eram justas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30593, 'nvi', '1 João', '1jo', 3, 13, 'Meus irmãos, não se admirem se o mundo os odeia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30594, 'nvi', '1 João', '1jo', 3, 14, 'Sabemos que já passamos da morte para a vida porque amamos nossos irmãos. Quem não ama permanece na morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30595, 'nvi', '1 João', '1jo', 3, 15, 'Quem odeia seu irmão é assassino, e vocês sabem que nenhum assassino tem vida eterna em si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30596, 'nvi', '1 João', '1jo', 3, 16, 'Nisto conhecemos o que é o amor: Jesus Cristo deu a sua vida por nós, e devemos dar a nossa vida por nossos irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30597, 'nvi', '1 João', '1jo', 3, 17, 'Se alguém tiver recursos materiais e, vendo seu irmão em necessidade, não se compadecer dele, como pode permanecer nele o amor de Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30598, 'nvi', '1 João', '1jo', 3, 18, 'Filhinhos, não amemos de palavra nem de boca, mas em ação e em verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30599, 'nvi', '1 João', '1jo', 3, 19, 'Assim saberemos que somos da verdade; e tranqüilizaremos o nosso coração diante dele');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30600, 'nvi', '1 João', '1jo', 3, 20, 'quando o nosso coração nos condenar. Porque Deus é maior do que o nosso coração e sabe todas as coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30601, 'nvi', '1 João', '1jo', 3, 21, 'Amados, se o nosso coração não nos condenar, temos confiança diante de Deus');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30602, 'nvi', '1 João', '1jo', 3, 22, 'e recebemos dele tudo o que pedimos, porque obedecemos aos seus mandamentos e fazemos o que lhe agrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30603, 'nvi', '1 João', '1jo', 3, 23, 'E este é o seu mandamento: que creiamos no nome de seu Filho Jesus Cristo e que nos amemos uns aos outros, como ele nos ordenou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30604, 'nvi', '1 João', '1jo', 3, 24, 'Os que obedecem aos seus mandamentos permanecem nele, e ele neles. Deste modo sabemos que ele permanece em nós: pelo Espírito que nos deu.');
