INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30626, 'nvi', '1 João', '1jo', 5, 1, 'Todo aquele que crê que Jesus é o Cristo é nascido de Deus, e todo aquele que ama o Pai ama também ao que dele foi gerado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30627, 'nvi', '1 João', '1jo', 5, 2, 'Assim sabemos que amamos os filhos de Deus: amando a Deus e obedecendo aos seus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30628, 'nvi', '1 João', '1jo', 5, 3, 'Porque nisto consiste o amor a Deus: obedecer aos seus mandamentos. E os seus mandamentos não são pesados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30629, 'nvi', '1 João', '1jo', 5, 4, 'O que é nascido de Deus vence o mundo; e esta é a vitória que vence o mundo: a nossa fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30630, 'nvi', '1 João', '1jo', 5, 5, 'Quem é que vence o mundo? Somente aquele que crê que Jesus é o Filho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30631, 'nvi', '1 João', '1jo', 5, 6, 'Este é aquele que veio por meio de água e sangue, Jesus Cristo: não somente por água, mas por água e sangue. E o Espírito é quem dá testemunho, porque o Espírito é a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30632, 'nvi', '1 João', '1jo', 5, 7, 'Há três que dão testemunho:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30633, 'nvi', '1 João', '1jo', 5, 8, 'o Espírito, a água e o sangue; e os três são unânimes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30634, 'nvi', '1 João', '1jo', 5, 9, 'Nós aceitamos o testemunho dos homens, mas o testemunho de Deus tem maior valor, pois é o testemunho de Deus, que ele dá acerca de seu Filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30635, 'nvi', '1 João', '1jo', 5, 10, 'Quem crê no Filho de Deus tem em si mesmo esse testemunho. Quem não crê em Deus o faz mentiroso, porque não crê no testemunho que Deus dá acerca de seu Filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30636, 'nvi', '1 João', '1jo', 5, 11, 'E este é o testemunho: Deus nos deu a vida eterna, e essa vida está em seu Filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30637, 'nvi', '1 João', '1jo', 5, 12, 'Quem tem o Filho, tem a vida; quem não tem o Filho de Deus, não tem a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30638, 'nvi', '1 João', '1jo', 5, 13, 'Escrevi-lhes estas coisas, a vocês que crêem no nome do Filho de Deus, para que vocês saibam que têm a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30639, 'nvi', '1 João', '1jo', 5, 14, 'Esta é a confiança que temos ao nos aproximarmos de Deus: se pedirmos alguma coisa de acordo com a sua vontade, ele nos ouve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30640, 'nvi', '1 João', '1jo', 5, 15, 'E se sabemos que ele nos ouve em tudo o que pedimos, sabemos que temos o que dele pedimos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30641, 'nvi', '1 João', '1jo', 5, 16, 'Se alguém vir seu irmão cometer pecado que não leva à morte, ore, e Deus lhe dará vida. Refiro-me àqueles cujo pecado não leva à morte. Há pecado que leva à morte; não estou dizendo que se deva orar por este.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30642, 'nvi', '1 João', '1jo', 5, 17, 'Toda injustiça é pecado, mas há pecado que não leva à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30643, 'nvi', '1 João', '1jo', 5, 18, 'Sabemos que todo aquele que é nascido de Deus não está no pecado; aquele que nasceu de Deus o protege, e o Maligno não o atinge.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30644, 'nvi', '1 João', '1jo', 5, 19, 'Sabemos que somos de Deus e que o mundo todo está sob o poder do Maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30645, 'nvi', '1 João', '1jo', 5, 20, 'Sabemos também que o Filho de Deus veio e nos deu entendimento, para que conheçamos aquele que é o Verdadeiro. E nós estamos naquele que é o Verdadeiro, em seu Filho Jesus Cristo. Este é o verdadeiro Deus e a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30646, 'nvi', '1 João', '1jo', 5, 21, 'Filhinhos, guardem-se dos ídolos.');
