INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30401, 'nvi', '1 Pedro', '1pe', 2, 1, 'Livrem-se, pois, de toda maldade e de todo engano, hipocrisia, inveja e toda espécie de maledicência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30402, 'nvi', '1 Pedro', '1pe', 2, 2, 'Como crianças recém-nascidas, desejem de coração o leite espiritual puro, para que por meio dele cresçam para a salvação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30403, 'nvi', '1 Pedro', '1pe', 2, 3, 'agora que provaram que o Senhor é bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30404, 'nvi', '1 Pedro', '1pe', 2, 4, 'À medida que se aproximam dele, a pedra viva — rejeitada pelos homens, mas escolhida por Deus e preciosa para ele —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30405, 'nvi', '1 Pedro', '1pe', 2, 5, 'vocês também estão sendo utilizados como pedras vivas na edificação de uma casa espiritual para serem sacerdócio santo, oferecendo sacrifícios espirituais aceitáveis a Deus, por meio de Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30406, 'nvi', '1 Pedro', '1pe', 2, 6, 'Pois assim é dito na Escritura: "Eis que ponho em Sião uma pedra angular, escolhida e preciosa, e aquele que nela confia jamais será envergonhado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30407, 'nvi', '1 Pedro', '1pe', 2, 7, 'Portanto, para vocês, os que crêem, esta pedra é preciosa; mas para os que não crêem, "a pedra que os construtores rejeitaram tornou-se a pedra angular",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30408, 'nvi', '1 Pedro', '1pe', 2, 8, 'e, "pedra de tropeço e rocha que faz cair". Os que não crêem tropeçam, porque desobedecem à mensagem; para o que também foram destinados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30409, 'nvi', '1 Pedro', '1pe', 2, 9, 'Vocês, porém, são geração eleita, sacerdócio real, nação santa, povo exclusivo de Deus, para anunciar as grandezas daquele que os chamou das trevas para a sua maravilhosa luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30410, 'nvi', '1 Pedro', '1pe', 2, 10, 'Antes vocês nem sequer eram povo, mas agora são povo de Deus; não haviam recebido misericórdia, mas agora a receberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30411, 'nvi', '1 Pedro', '1pe', 2, 11, 'Amados, insisto em que, como estrangeiros e peregrinos no mundo, vocês se abstenham dos desejos carnais que guerreiam contra a alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30412, 'nvi', '1 Pedro', '1pe', 2, 12, 'Vivam entre os pagãos de maneira exemplar para que, naquilo em que eles os acusam de praticarem o mal, observem as boas obras que vocês praticam e glorifiquem a Deus no dia da sua intervenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30413, 'nvi', '1 Pedro', '1pe', 2, 13, 'Por causa do Senhor, sujeitem-se a toda autoridade constituída entre os homens; seja ao rei, como autoridade suprema,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30414, 'nvi', '1 Pedro', '1pe', 2, 14, 'seja aos governantes, como por ele enviados para punir os que praticam o mal e honrar os que praticam o bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30415, 'nvi', '1 Pedro', '1pe', 2, 15, 'Pois é da vontade de Deus que, praticando o bem, vocês silenciem a ignorância dos insensatos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30416, 'nvi', '1 Pedro', '1pe', 2, 16, 'Vivam como pessoas livres, mas não usem a liberdade como desculpa para fazer o mal; vivam como servos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30417, 'nvi', '1 Pedro', '1pe', 2, 17, 'Tratem a todos com o devido respeito: amem os irmãos, temam a Deus e honrem o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30418, 'nvi', '1 Pedro', '1pe', 2, 18, 'Escravos, sujeitem-se a seus senhores com todo o respeito, não apenas aos bons e amáveis, mas também aos maus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30419, 'nvi', '1 Pedro', '1pe', 2, 19, 'Porque é louvável que, por motivo de sua consciência para com Deus, alguém suporte aflições sofrendo injustamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30420, 'nvi', '1 Pedro', '1pe', 2, 20, 'Pois que vantagem há em suportar açoites recebidos por terem cometido o mal? Mas se vocês suportam o sofrimento por terem feito o bem, isso é louvável diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30421, 'nvi', '1 Pedro', '1pe', 2, 21, 'Para isso vocês foram chamados, pois também Cristo sofreu no lugar de vocês, deixando-lhes exemplo, para que sigam os seus passos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30422, 'nvi', '1 Pedro', '1pe', 2, 22, '"Ele não cometeu pecado algum, e nenhum engano foi encontrado em sua boca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30423, 'nvi', '1 Pedro', '1pe', 2, 23, 'Quando insultado, não revidava; quando sofria, não fazia ameaças, mas entregava-se àquele que julga com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30424, 'nvi', '1 Pedro', '1pe', 2, 24, 'Ele mesmo levou em seu corpo os nossos pecados sobre o madeiro, a fim de que morrêssemos para os pecados e vivêssemos para a justiça; por suas feridas vocês foram curados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30425, 'nvi', '1 Pedro', '1pe', 2, 25, 'Pois vocês eram como ovelhas desgarradas, mas agora se converteram ao Pastor e Bispo de suas almas.');
