INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30467, 'nvi', '1 Pedro', '1pe', 5, 1, 'Portanto, apelo para os presbíteros que há entre vocês, e o faço na qualidade de presbítero como eles e testemunha dos sofrimentos de Cristo, como alguém que participará da glória a ser revelada:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30468, 'nvi', '1 Pedro', '1pe', 5, 2, 'Pastoreiem o rebanho de Deus que está aos seus cuidados. Olhem por ele, não por obrigação, mas de livre vontade, como Deus quer. Não façam isso por ganância, mas com o desejo de servir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30469, 'nvi', '1 Pedro', '1pe', 5, 3, 'Não ajam como dominadores dos que lhes foram confiados, mas como exemplos para o rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30470, 'nvi', '1 Pedro', '1pe', 5, 4, 'Quando se manifestar o Supremo Pastor, vocês receberão a imperecível coroa da glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30471, 'nvi', '1 Pedro', '1pe', 5, 5, 'Da mesma forma jovens, sujeitem-se aos mais velhos. Sejam todos humildes uns para com os outros, porque "Deus se opõe aos orgulhosos, mas concede graça aos humildes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30472, 'nvi', '1 Pedro', '1pe', 5, 6, 'Portanto, humilhem-se debaixo da poderosa mão de Deus, para que ele os exalte no tempo devido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30473, 'nvi', '1 Pedro', '1pe', 5, 7, 'Lancem sobre ele toda a sua ansiedade, porque ele tem cuidado de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30474, 'nvi', '1 Pedro', '1pe', 5, 8, 'Sejam sóbrios e vigiem. O diabo, o inimigo de vocês, anda ao redor como leão, rugindo e procurando a quem possa devorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30475, 'nvi', '1 Pedro', '1pe', 5, 9, 'Resistam-lhe, permanecendo firmes na fé, sabendo que os irmãos que vocês têm em todo o mundo estão passando pelos mesmos sofrimentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30476, 'nvi', '1 Pedro', '1pe', 5, 10, 'O Deus de toda a graça, que os chamou para a sua glória eterna em Cristo Jesus, depois de terem sofrido durante pouco de tempo, os restaurará, os confirmará, lhes dará forças e os porá sobre firmes alicerces.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30477, 'nvi', '1 Pedro', '1pe', 5, 11, 'A ele seja o poder para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30478, 'nvi', '1 Pedro', '1pe', 5, 12, 'Com a ajuda de Silvano, a quem considero irmão fiel, eu lhes escrevi resumidamente, encorajando-os e testemunhando que esta é a verdadeira graça de Deus. Mantenham-se firmes na graça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30479, 'nvi', '1 Pedro', '1pe', 5, 13, 'Aquela que está em Babilônia, também eleita, envia-lhes saudações, e também Marcos, meu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30480, 'nvi', '1 Pedro', '1pe', 5, 14, 'Saúdem uns aos outros com beijo de santo amor. Paz a todos vocês que estão em Cristo.');
