INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8719, 'nvi', '1 Reis', '1rs', 1, 1, 'Quando o rei Davi envelheceu, já de idade bem avançada, cobriam-no de cobertores, mas ele não se aquecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8720, 'nvi', '1 Reis', '1rs', 1, 2, 'Por isso os seus servos lhe propuseram: "Nós vamos procurar uma jovem virgem para servir e cuidar do rei. Ela se deitará ao seu lado, afim de aquecer o rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8721, 'nvi', '1 Reis', '1rs', 1, 3, 'Então procuraram em todo o território de Israel uma jovem que fosse bonita e encontraram Abisague, uma sunamita, e a levaram ao rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8722, 'nvi', '1 Reis', '1rs', 1, 4, 'A jovem, muito bonita, cuidava do rei e o servia, mas o rei não teve relações com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8723, 'nvi', '1 Reis', '1rs', 1, 5, 'Ora, Adonias, cuja mãe se chamava Hagite, tomou a dianteira e disse: "Eu serei o rei". Providenciou uma carruagem e cavalos, além de cinqüenta homens para correrem à sua frente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8724, 'nvi', '1 Reis', '1rs', 1, 6, 'Seu pai nunca o havia contrariado; nunca lhe perguntava: "Por que você age assim? " Adonias também tinha boa aparência e havia nascido depois de Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8725, 'nvi', '1 Reis', '1rs', 1, 7, 'Adonias fez acordo com Joabe, filho de Zeruia, e com o sacerdote Abiatar, e eles, o seguiram e o apoiaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8726, 'nvi', '1 Reis', '1rs', 1, 8, 'Mas o sacerdote Zadoque, Benaia, filho de Joiada, o profeta Natã, Simei, Reí e a guarda especial de Davi não deram apoio a Adonias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8727, 'nvi', '1 Reis', '1rs', 1, 9, 'Então Adonias sacrificou ovelhas, bois e novilhos gordos junto à pedra de Zoelete, próximo a En-Rogel. Convidou todos os seus irmãos, filhos do rei, e todos os homens de Judá que eram conselheiros do rei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8728, 'nvi', '1 Reis', '1rs', 1, 10, 'mas não convidou o profeta Natã nem Benaia nem a guarda especial nem o seu irmão Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8729, 'nvi', '1 Reis', '1rs', 1, 11, 'Natã perguntou então a Bate-Seba, mãe de Salomão: "Você ainda não sabe que Adonias, o filho de Hagite, tornou-se rei, sem que o nosso senhor Davi ficasse sabendo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8730, 'nvi', '1 Reis', '1rs', 1, 12, 'Agora, vou dar-lhe um conselho para salvar a sua vida e também a vida do seu filho Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8731, 'nvi', '1 Reis', '1rs', 1, 13, 'Vá perguntar ao rei Davi: Ó rei, meu senhor, não juraste a esta tua serva, prometendo: ‘Tenha certeza que o seu filho Salomão me sucederá como rei, e se assentará no meu trono? ’ Por que foi então, que Adonias se tornou rei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8732, 'nvi', '1 Reis', '1rs', 1, 14, 'Enquanto você ainda estiver conversando com o rei, eu entrarei e confirmarei as suas palavras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8733, 'nvi', '1 Reis', '1rs', 1, 15, 'Então Bate-Seba foi até o quarto do rei, já idoso, onde a sunamita Abisague cuidava dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8734, 'nvi', '1 Reis', '1rs', 1, 16, 'Bate-Seba ajoelhou-se e prostrou-se diante do rei. "O que você quer? ", o rei perguntou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8735, 'nvi', '1 Reis', '1rs', 1, 17, 'Ela respondeu: "Meu senhor, tu mesmo juraste a esta tua serva, pelo Senhor o teu Deus: ‘Seu filho Salomão o sucederá como rei e se assentará no meu trono’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8736, 'nvi', '1 Reis', '1rs', 1, 18, 'Mas agora Adonias se tornou rei, sem que o rei meu senhor o soubesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8737, 'nvi', '1 Reis', '1rs', 1, 19, 'Ele sacrificou muitos bois, novilhos gordos e ovelhas, e convidou todos os filhos do rei, o sacerdote Abiatar, e Joabe, o comandante do exército, mas não convidou o teu servo Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8738, 'nvi', '1 Reis', '1rs', 1, 20, 'Agora, ó rei, meu senhor, os olhos de todo o Israel estão sobre ti para saber de tua parte, quem sucederá ao rei, meu senhor, no trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8739, 'nvi', '1 Reis', '1rs', 1, 21, 'De outro modo, tão logo o rei, meu senhor descanse com os seus antepassados, eu e o meu filho Salomão seremos tratados como traidores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8740, 'nvi', '1 Reis', '1rs', 1, 22, 'Ela ainda conversava com o rei, quando o profeta Natã chegou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8741, 'nvi', '1 Reis', '1rs', 1, 23, 'Assim que informaram o rei que o profeta Natã havia chegado, ele entrou e prostrou-se, rosto em terra, diante do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8742, 'nvi', '1 Reis', '1rs', 1, 24, 'E Natã lhe perguntou: "Ó rei, meu senhor, por acaso declaraste que Adonias te sucederia como rei e que ele se assentaria no teu trono?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8743, 'nvi', '1 Reis', '1rs', 1, 25, 'Hoje ele foi matar muitos bois, novilhos gordos e ovelhas. Convidou todos os filhos do rei, os comandantes do exército e o sacerdote Abiatar. Agora eles estão comendo e bebendo com ele e comemorando: ‘Viva o rei Adonias! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8744, 'nvi', '1 Reis', '1rs', 1, 26, 'Mas ele não convidou a mim, que sou teu servo, nem ao sacerdote Zadoque nem a Benaia, filho de Joiada, nem a teu servo Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8745, 'nvi', '1 Reis', '1rs', 1, 27, 'Seria isto algo que o rei, meu senhor, fez sem deixar que os seus conselheiros soubessem quem sucederia ao rei, meu senhor, no trono? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8746, 'nvi', '1 Reis', '1rs', 1, 28, 'Então o rei Davi ordenou: "Chamem Bate-Seba". Ela entrou e ficou de pé diante dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8747, 'nvi', '1 Reis', '1rs', 1, 29, 'O rei fez então um juramento: "Juro pelo nome do Senhor, o qual me livrou de todas as adversidades,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8748, 'nvi', '1 Reis', '1rs', 1, 30, 'que sem dúvida hoje mesmo vou executar o que jurei pelo Senhor, o Deus de Israel. O seu filho Salomão me sucederá como rei e se assentará no meu trono em meu lugar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8749, 'nvi', '1 Reis', '1rs', 1, 31, 'Então Bate-Seba prostrou-se, rosto em terra, e, ajoelhando-se diante do rei, disse: "Que o rei Davi, meu senhor, viva para sempre! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8750, 'nvi', '1 Reis', '1rs', 1, 32, 'O rei Davi ordenou: "Chamem o sacerdote Zadoque, o profeta Natã e Benaia, filho de Joiada". Quando chegaram à presença do rei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8751, 'nvi', '1 Reis', '1rs', 1, 33, 'ele os instruiu: "Levem os conselheiros do seu senhor com vocês e ponham o meu filho Salomão sobre a minha mula e levem-no a Giom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8752, 'nvi', '1 Reis', '1rs', 1, 34, 'Ali o sacerdote Zadoque e o profeta Natã o ungirão rei sobre Israel. Então toquem a trombeta e gritem: Viva o rei Salomão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8753, 'nvi', '1 Reis', '1rs', 1, 35, 'Depois acompanhem-no, e ele virá assentar-se no meu trono e reinará em meu lugar. Eu o designei para governar Israel e Judá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8754, 'nvi', '1 Reis', '1rs', 1, 36, 'Benaia, filho de Joiada, respondeu ao rei: "Assim se fará! Que o Senhor, o Deus do rei, meu senhor, o confirme.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8755, 'nvi', '1 Reis', '1rs', 1, 37, 'Assim como o Senhor esteve com o rei, meu senhor, também esteja ele com Salomão para que ele tenha um reinado ainda mais glorioso do que o meu senhor, o rei Davi! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8756, 'nvi', '1 Reis', '1rs', 1, 38, 'Então o sacerdote Zadoque, o profeta Natã, Benaia, filho de Joiada, os queretitas e os peletitas fizeram Salomão montar a mula do rei Davi e o escoltaram até Giom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8757, 'nvi', '1 Reis', '1rs', 1, 39, 'O sacerdote Zadoque pegou na Tenda o chifre com óleo e ungiu Salomão. Então tocaram a trombeta e todo o povo gritou: "Viva o rei Salomão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8758, 'nvi', '1 Reis', '1rs', 1, 40, 'E todo o povo o acompanhou, tocando flautas e comemorando, de tal forma que o chão tremia com o barulho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8759, 'nvi', '1 Reis', '1rs', 1, 41, 'Adonias e todos os seus convidados souberam disso quando estavam terminando o banquete. Ao ouvir o toque da trombeta, Joabe perguntou: "O que significa esta gritaria, esse alvoroço na cidade? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8760, 'nvi', '1 Reis', '1rs', 1, 42, 'Falava ele ainda, quando chegou Jônatas, filho do sacerdote Abiatar. E Adonias lhe disse: "Entre, pois um homem digno como você deve estar trazendo boas notícias! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8761, 'nvi', '1 Reis', '1rs', 1, 43, '"De modo algum", respondeu Jônatas a Adonias. "Davi, o nosso rei e senhor, constituiu rei a Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8762, 'nvi', '1 Reis', '1rs', 1, 44, 'O rei enviou com ele o sacerdote Zadoque, o profeta Natã, Benaia, filho de Joiada, os queretitas e os peletitas, e eles o fizeram montar a mula do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8763, 'nvi', '1 Reis', '1rs', 1, 45, 'Depois o sacerdote Zadoque e o profeta Natã o ungiram rei em Giom. De lá eles saíram comemorando, e a cidade está alvoroçada. É esse o barulho que vocês ouvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8764, 'nvi', '1 Reis', '1rs', 1, 46, 'Além disso, Salomão já se assentou no trono real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8765, 'nvi', '1 Reis', '1rs', 1, 47, 'Até mesmo os oficiais do rei foram cumprimentar Davi, o nosso rei e senhor, dizendo: ‘Que o teu Deus torne o nome de Salomão mais famoso que o teu, e o seu reinado mais glorioso do que o teu! ’ E o rei curvou-se reverentemente em sua cama,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8766, 'nvi', '1 Reis', '1rs', 1, 48, 'e disse: ‘Bendito seja o Senhor, o Deus de Israel, que permitiu que os meus olhos vissem hoje um sucessor em meu trono’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8767, 'nvi', '1 Reis', '1rs', 1, 49, 'Diante disso, todos os convidados de Adonias entraram em pânico e se dispersaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8768, 'nvi', '1 Reis', '1rs', 1, 50, 'Mas Adonias, com medo de Salomão, foi agarrar-se às pontas do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8769, 'nvi', '1 Reis', '1rs', 1, 51, 'Então informaram a Salomão: "Adonias está com medo do rei Salomão e está agarrado às pontas do altar. Ele diz: ‘Que o rei Salomão me jure que não matará o seu servo pela espada’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8770, 'nvi', '1 Reis', '1rs', 1, 52, 'Salomão respondeu: "Se ele se mostrar confiável, não cairá nem um só fio de cabelo da sua cabeça; mas se nele se descobrir alguma maldade, ele morrerá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8771, 'nvi', '1 Reis', '1rs', 1, 53, 'Então o rei enviou alguns soldados, e eles o fizeram descer do altar. E Adonias veio e se curvou solenemente perante o rei Salomão, que lhe disse: "Vá para casa".');
