INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9081, 'nvi', '1 Reis', '1rs', 10, 1, 'A rainha de Sabá soube da fama que Salomão tinha alcançado, graças ao nome do Senhor, e foi a Jerusalém para pô-lo à prova com perguntas difíceis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9082, 'nvi', '1 Reis', '1rs', 10, 2, 'Quando chegou, acompanhada de uma enorme caravana, com camelos carregados de especiarias, grande quantidade de ouro e pedras preciosas, foi até Salomão e lhe fez todas as perguntas que tinha em mente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9083, 'nvi', '1 Reis', '1rs', 10, 3, 'Salomão respondeu a todas; nenhuma lhe foi tão difícil que não pudesse responder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9084, 'nvi', '1 Reis', '1rs', 10, 4, 'Vendo toda a sabedoria de Salomão, bem como o palácio que ele havia construído,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9085, 'nvi', '1 Reis', '1rs', 10, 5, 'o que era servido em sua mesa, o lugar de seus oficiais, os criados e copeiros, todos uniformizados, e os holocaustos que ele fazia no templo do Senhor, ela ficou impressionada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9086, 'nvi', '1 Reis', '1rs', 10, 6, 'Disse ela então ao rei: "Tudo o que ouvi em meu país acerca de tuas realizações e de tua sabedoria era verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9087, 'nvi', '1 Reis', '1rs', 10, 7, 'Mas eu não acreditava no que diziam, até ver com os meus próprios olhos. Na realidade, não me contaram nem a metade; tu ultrapassas em muito o que ouvi, tanto em sabedoria como em riqueza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9088, 'nvi', '1 Reis', '1rs', 10, 8, 'Como devem ser felizes os homens da tua corte, que continuamente estão diante de ti e ouvem a tua sabedoria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9089, 'nvi', '1 Reis', '1rs', 10, 9, 'Bendito seja o Senhor, o teu Deus, que se agradou de ti e te colocou no trono de Israel. Por causa do amor eterno do Senhor para com Israel, ele te fez rei, para manter a justiça e a retidão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9090, 'nvi', '1 Reis', '1rs', 10, 10, 'E ela deu ao rei quatro toneladas e duzentos quilos de ouro e grande quantidade de especiarias e pedras preciosas. E nunca mais foram trazidas tantas especiarias quanto as que a rainha de Sabá deu ao rei Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9091, 'nvi', '1 Reis', '1rs', 10, 11, '( Os navios de Hirão, que carregavam ouro de Ofir, também trouxeram de lá grande quantidade de madeira de junípero e pedras preciosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9092, 'nvi', '1 Reis', '1rs', 10, 12, 'O rei utilizou a madeira para fazer a escadaria do templo do Senhor e a do palácio real, além de harpas e liras para os músicos. Nunca mais foi importada nem se viu tanta madeira de junípero. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9093, 'nvi', '1 Reis', '1rs', 10, 13, 'O rei Salomão deu à rainha de Sabá tudo o que ela desejou e pediu, além do que já lhe tinha dado por sua generosidade real. Então ela e os seus servos voltaram para o seu país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9094, 'nvi', '1 Reis', '1rs', 10, 14, 'O peso do ouro que Salomão recebia anualmente era de vinte e três toneladas e trezentos quilos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9095, 'nvi', '1 Reis', '1rs', 10, 15, 'fora os impostos pagos por mercadores e comerciantes e por todos os reis da Arábia e pelos governadores do país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9096, 'nvi', '1 Reis', '1rs', 10, 16, 'O rei Salomão fez duzentos escudos grandes de ouro batido, utilizando três quilos e seiscentos gramas de ouro em cada um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9097, 'nvi', '1 Reis', '1rs', 10, 17, 'Também fez trezentos escudos pequenos de ouro batido, com um quilo e oitocentos gramas de ouro em cada um. O rei os colocou no Palácio da Floresta do Líbano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9098, 'nvi', '1 Reis', '1rs', 10, 18, 'O rei mandou fazer ainda um grande trono de marfim revestido de ouro puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9099, 'nvi', '1 Reis', '1rs', 10, 19, 'O trono tinha seis degraus, e o seu encosto tinha a parte alta arredondada. Nos dois lados do assento havia braços, com um leão junto a cada braço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9100, 'nvi', '1 Reis', '1rs', 10, 20, 'Doze leões ficavam nos seis degraus, um de cada lado. Nada igual havia sido feito em nenhum outro reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9101, 'nvi', '1 Reis', '1rs', 10, 21, 'Todas as taças do rei Salomão eram de ouro, bem como todos os utensílios do Palácio da Floresta do Líbano. Não havia nada de prata, pois a prata quase não tinha valor nos dias de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9102, 'nvi', '1 Reis', '1rs', 10, 22, 'O rei tinha no mar uma frota de navios mercantes junto com os navios de Hirão. Cada três anos a frota voltava, trazendo ouro, prata, marfim, macacos e pavões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9103, 'nvi', '1 Reis', '1rs', 10, 23, 'O rei Salomão era o mais rico e o mais sábio de todos os reis da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9104, 'nvi', '1 Reis', '1rs', 10, 24, 'Gente de todo o mundo pedia audiência a Salomão para ouvir a sabedoria que Deus lhe tinha dado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9105, 'nvi', '1 Reis', '1rs', 10, 25, 'Ano após ano, todos os que vinham traziam algum presente: utensílios de prata e de ouro, mantos, armas e especiarias, cavalos e mulas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9106, 'nvi', '1 Reis', '1rs', 10, 26, 'Salomão juntou carros e cavalos; possuía mil e quatrocentos carros e doze mil cavalos, dos quais mantinha uma parte nas guarnições de algumas cidades e a outra perto dele, em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9107, 'nvi', '1 Reis', '1rs', 10, 27, 'O rei tornou a prata tão comum em Jerusalém quanto as pedras, e o cedro tão numeroso quanto as figueiras bravas da Sefelá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9108, 'nvi', '1 Reis', '1rs', 10, 28, 'Os cavalos de Salomão eram importados do Egito e da Cilícia, onde os fornecedores do rei os compravam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9109, 'nvi', '1 Reis', '1rs', 10, 29, 'Importavam do Egito um carro por sete quilos e duzentos gramas de prata, e um cavalo por um quilo e oitocentos gramas, e os exportavam para todos os reis dos hititas e dos arameus.');
