INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9186, 'nvi', '1 Reis', '1rs', 13, 1, 'Por ordem do Senhor um homem de Deus foi de Judá a Betel, quando Jeroboão estava de pé junto ao altar para queimar incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9187, 'nvi', '1 Reis', '1rs', 13, 2, 'Ele clamou contra o altar, segundo a ordem do Senhor: "Ó altar, ó altar! Assim diz o Senhor: ‘Um filho nascerá na família de Davi e se chamará Josias. Sobre você ele sacrificará os sacerdotes dos altares idólatras que agora queimam incenso aqui, e ossos humanos serão queimados sobre você’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9188, 'nvi', '1 Reis', '1rs', 13, 3, 'Naquele mesmo dia o homem de Deus deu um sinal: "Este é o sinal que o Senhor declarou: O altar se fenderá, e as cinzas que estão sobre ele se derramarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9189, 'nvi', '1 Reis', '1rs', 13, 4, 'Quando o rei Jeroboão ouviu o que o homem de Deus proclamava contra o altar de Betel, apontou para ele e ordenou: "Prendam-no! " Mas o braço que ele tinha estendido ficou paralisado, e não voltava ao normal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9190, 'nvi', '1 Reis', '1rs', 13, 5, 'Além disso, o altar se fendeu, e as suas cinzas se derramaram, conforme o sinal dado pelo homem de Deus por ordem do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9191, 'nvi', '1 Reis', '1rs', 13, 6, 'Então o rei disse ao homem de Deus: "Interceda junto ao Senhor, o seu Deus, e ore por mim para que meu braço se recupere". O homem de Deus intercedeu junto ao Senhor, e o braço do rei recuperou-se e voltou ao normal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9192, 'nvi', '1 Reis', '1rs', 13, 7, 'O rei disse ao homem de Deus: "Venha à minha casa e coma algo, e eu o recompensarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9193, 'nvi', '1 Reis', '1rs', 13, 8, 'Mas o homem de Deus respondeu ao rei: "Mesmo que me desse a metade dos seus bens, eu não iria com você, nem comeria nem beberia nada neste lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9194, 'nvi', '1 Reis', '1rs', 13, 9, 'Pois recebi estas ordens pela palavra do Senhor: ‘Não coma pão nem beba água nem volte pelo mesmo caminho por onde veio’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9195, 'nvi', '1 Reis', '1rs', 13, 10, 'Por isso, quando ele voltou, não foi pelo caminho pelo qual tinha vindo a Betel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9196, 'nvi', '1 Reis', '1rs', 13, 11, 'Ora, havia um certo profeta, já idoso, morando em Betel. Seus filhos lhe contaram tudo o que o homem de Deus havia feito naquele dia e também o que ele dissera ao rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9197, 'nvi', '1 Reis', '1rs', 13, 12, 'O pai lhes perguntou: "Por qual caminho ele foi? " E os seus filhos lhe mostraram por onde tinha ido o homem de Deus que viera de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9198, 'nvi', '1 Reis', '1rs', 13, 13, 'Então disse aos filhos: "Selem o jumento para mim". E, depois de selarem o jumento, ele montou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9199, 'nvi', '1 Reis', '1rs', 13, 14, 'e cavalgou à procura do homem de Deus, até que o encontrou sentado embaixo da Grande Árvore. E lhe perguntou: "Você é o homem de Deus que veio de Judá? " "Sou", respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9200, 'nvi', '1 Reis', '1rs', 13, 15, 'Então o profeta lhe disse: "Venha à minha casa, comer alguma coisa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9201, 'nvi', '1 Reis', '1rs', 13, 16, 'O homem de Deus disse: "Não posso ir com você, nem posso comer pão ou beber água neste lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9202, 'nvi', '1 Reis', '1rs', 13, 17, 'A palavra do Senhor deu-me esta ordem: ‘Não coma pão nem beba água lá, nem volte pelo mesmo caminho por onde você foi’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9203, 'nvi', '1 Reis', '1rs', 13, 18, 'O profeta idoso respondeu: "Eu também sou profeta como você. E um anjo me disse por ordem do Senhor: ‘Faça-o voltar com você para a sua casa para que coma pão e beba água’ ". Mas ele estava mentindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9204, 'nvi', '1 Reis', '1rs', 13, 19, 'E o homem de Deus voltou com ele e foi comer e beber em sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9205, 'nvi', '1 Reis', '1rs', 13, 20, 'Enquanto ainda estavam sentados à mesa, a palavra do Senhor veio ao profeta idoso que o havia feito voltar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9206, 'nvi', '1 Reis', '1rs', 13, 21, 'Ele bradou ao homem de Deus que tinha vindo de Judá: "Assim diz o Senhor: ‘Você desafiou a palavra do Senhor e não obedeceu à ordem que o Senhor, o seu Deus, lhe deu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9207, 'nvi', '1 Reis', '1rs', 13, 22, 'Você voltou e comeu pão e bebeu água no lugar onde ele lhe falou que não comesse nem bebesse. Por isso o seu corpo não será sepultado no túmulo dos seus antepassados’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9208, 'nvi', '1 Reis', '1rs', 13, 23, 'Quando o homem de Deus acabou de comer e beber, o outro profeta selou seu jumento para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9209, 'nvi', '1 Reis', '1rs', 13, 24, 'No caminho, um leão o atacou e o matou, e o seu corpo ficou estendido no chão, ao lado do leão e do jumento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9210, 'nvi', '1 Reis', '1rs', 13, 25, 'Algumas pessoas que passaram viram o cadáver estendido ali, com o leão ao lado, e foram dar a notícia na cidade onde o profeta idoso vivia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9211, 'nvi', '1 Reis', '1rs', 13, 26, 'Quando o outro profeta soube disso, exclamou: "É o homem de Deus que desafiou a palavra do Senhor! O Senhor o entregou ao leão, que o feriu e o matou, conforme a palavra do Senhor o tinha advertido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9212, 'nvi', '1 Reis', '1rs', 13, 27, 'O profeta disse aos seus filhos: "Selem o jumento para mim", e eles o fizeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9213, 'nvi', '1 Reis', '1rs', 13, 28, 'Ele foi e encontrou o cadáver caído no caminho, com o jumento e o leão ao seu lado. O leão não tinha comido o corpo nem ferido o jumento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9214, 'nvi', '1 Reis', '1rs', 13, 29, 'O profeta apanhou o corpo do homem de Deus, colocou-o sobre o jumento, e o levou de volta para Betel, a fim de chorar por ele e sepultá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9215, 'nvi', '1 Reis', '1rs', 13, 30, 'Ele o pôs no seu próprio túmulo, e lamentaram por ele, exclamando: "Ah, meu irmão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9216, 'nvi', '1 Reis', '1rs', 13, 31, 'Depois de sepultá-lo, disse aos seus filhos: "Quando eu morrer, enterrem-me no túmulo onde está sepultado o homem de Deus; ponham os meus ossos ao lado dos ossos dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9217, 'nvi', '1 Reis', '1rs', 13, 32, 'Pois a mensagem que declarou por ordem do Senhor contra o altar de Betel e contra todos os altares idólatras das cidades de Samaria certamente se cumprirá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9218, 'nvi', '1 Reis', '1rs', 13, 33, 'Mesmo depois disso Jeroboão não mudou o seu mau procedimento, mas continuou a nomear dentre o povo sacerdotes para os altares idólatras. Ele consagrava para esses altares todo aquele que quisesse tornar-se sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9219, 'nvi', '1 Reis', '1rs', 13, 34, 'Esse foi o pecado da família de Jeroboão, que levou à sua queda e à sua eliminação da face da terra.');
