INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9220, 'nvi', '1 Reis', '1rs', 14, 1, 'Naquela época, Abias, filho de Jeroboão, ficou doente,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9221, 'nvi', '1 Reis', '1rs', 14, 2, 'e este disse à sua mulher: "Use um disfarce para não ser reconhecida como a mulher de Jeroboão, e vá a Siló, onde vive o profeta Aías, aquele que me disse que eu seria rei sobre este povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9222, 'nvi', '1 Reis', '1rs', 14, 3, 'Leve para ele dez pães, alguns bolos e uma garrafa de mel. Ele lhe dirá o que vai acontecer com o menino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9223, 'nvi', '1 Reis', '1rs', 14, 4, 'A mulher de Jeroboão atendeu o seu pedido e foi à casa de Aías, em Siló. Ora, Aías já não conseguia enxergar; tinha ficado cego por causa da idade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9224, 'nvi', '1 Reis', '1rs', 14, 5, 'Mas o Senhor lhe tinha dito: "A mulher de Jeroboão está vindo para lhe perguntar acerca do filho dela, pois ele está doente, e você deve responder-lhe assim e assim. Quando ela chegar, vai fingir que é outra pessoa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9225, 'nvi', '1 Reis', '1rs', 14, 6, 'Quando Aías ouviu o som dos passos junto da porta, disse: "Entre, mulher de Jeroboão. Por que esse fingimento? Fui encarregado de lhe dar más notícias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9226, 'nvi', '1 Reis', '1rs', 14, 7, 'Vá dizer a Jeroboão que é isto o que o Senhor, o Deus de Israel, diz: ‘Tirei-o dentre o povo e o tornei líder sobre o meu povo Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9227, 'nvi', '1 Reis', '1rs', 14, 8, 'Tirei o reino da família de Davi e o dei a você, mas você não tem sido como o meu servo Davi, que obedecia aos meus mandamentos e me seguia de todo o coração, fazendo apenas o que eu aprovo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9228, 'nvi', '1 Reis', '1rs', 14, 9, 'Você tem feito mais mal do que todos os que viveram antes de você, pois fez para si outros deuses, ídolos de metal; você provocou a minha ira e voltou as costas para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9229, 'nvi', '1 Reis', '1rs', 14, 10, '" ‘Por isso, trarei desgraça à família de Jeroboão. Matarei de Jeroboão até o último indivíduo do sexo masculino em Israel, seja escravo ou livre. Queimarei a família de Jeroboão até o fim como quem queima esterco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9230, 'nvi', '1 Reis', '1rs', 14, 11, 'Dos que pertencem a Jeroboão, os cães comerão os que morrerem na cidade, e as aves do céu se alimentarão dos que morrerem no campo. O Senhor falou! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9231, 'nvi', '1 Reis', '1rs', 14, 12, '"Quanto a você, volte para casa. Quando você puser os pés na cidade, o menino morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9232, 'nvi', '1 Reis', '1rs', 14, 13, 'Todo o Israel chorará por ele e o sepultará. Ele é o único da família de Jeroboão que será sepultado, pois é o único da família de Jeroboão em quem o Senhor, o Deus de Israel, encontrou alguma coisa boa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9233, 'nvi', '1 Reis', '1rs', 14, 14, '"O Senhor levantará para si um rei sobre Israel que eliminará a família de Jeroboão. Este é o dia! Como? Sim, agora mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9234, 'nvi', '1 Reis', '1rs', 14, 15, 'E o Senhor ferirá Israel, de maneira que ficará como um junco balançando na água. Ele desarraigará Israel desta boa terra que deu aos seus antepassados e os espalhará para além do Rio, pois provocaram a ira do Senhor com os postes sagrados que fizeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9235, 'nvi', '1 Reis', '1rs', 14, 16, 'E ele abandonará Israel por causa dos pecados que Jeroboão cometeu e tem feito Israel cometer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9236, 'nvi', '1 Reis', '1rs', 14, 17, 'Então a mulher de Jeroboão levantou-se e voltou para Tirza. Assim que entrou em casa, o menino morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9237, 'nvi', '1 Reis', '1rs', 14, 18, 'Eles o sepultaram, e todo o Israel chorou por ele, conforme o Senhor predissera por meio do seu servo, o profeta Aías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9238, 'nvi', '1 Reis', '1rs', 14, 19, 'Os demais acontecimentos do reinado de Jeroboão, suas guerras e como governou, estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9239, 'nvi', '1 Reis', '1rs', 14, 20, 'Ele reinou durante vinte e dois anos, e então descansou com os seus antepassados. E o seu filho Nadabe foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9240, 'nvi', '1 Reis', '1rs', 14, 21, 'Roboão, filho de Salomão, foi rei de Judá. Tinha quarenta e um anos de idade quando começou a reinar, e reinou dezessete anos em Jerusalém, cidade que o Senhor havia escolhido dentre todas as tribos de Israel para nela pôr o seu nome. Sua mãe, uma amonita, chamava-se Naamá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9241, 'nvi', '1 Reis', '1rs', 14, 22, 'Judá fez o que o Senhor reprova. Pelos pecados que cometeram, eles despertaram a sua ira zelosa mais do que os seus antepassados o tinham feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9242, 'nvi', '1 Reis', '1rs', 14, 23, 'Também construíram para si altares idólatras, colunas sagradas e postes sagrados sobre todos os montes e debaixo de todas as árvores frondosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9243, 'nvi', '1 Reis', '1rs', 14, 24, 'Havia no país até prostitutos cultuais; o povo se envolvia em todas as práticas detestáveis das nações que o Senhor havia expulsado de diante dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9244, 'nvi', '1 Reis', '1rs', 14, 25, 'No quinto ano do reinado de Roboão, Sisaque, rei do Egito, atacou Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9245, 'nvi', '1 Reis', '1rs', 14, 26, 'Levou embora todos os tesouros do templo do Senhor e do palácio real, inclusive os escudos de ouro que Salomão havia feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9246, 'nvi', '1 Reis', '1rs', 14, 27, 'Por isso o rei Roboão mandou fazer escudos de bronze para substituí-los, e os entregou aos chefes da guarda da entrada do palácio real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9247, 'nvi', '1 Reis', '1rs', 14, 28, 'Sempre que o rei ia ao templo do Senhor, os guardas empunhavam os escudos, e, em seguida, os devolviam à sala da guarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9248, 'nvi', '1 Reis', '1rs', 14, 29, 'Os demais acontecimentos do reinado de Roboão, e tudo o que fez, estão escritos nos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9249, 'nvi', '1 Reis', '1rs', 14, 30, 'Houve guerra constante entre Roboão e Jeroboão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9250, 'nvi', '1 Reis', '1rs', 14, 31, 'Roboão descansou com os seus antepassados e foi sepultado com eles na cidade de Davi. Sua mãe, uma amonita, chamava-se Naamá. E o seu filho Abias foi o seu sucessor.');
