INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9251, 'nvi', '1 Reis', '1rs', 15, 1, 'No décimo oitavo ano do reinado de Jeroboão, filho de Nebate, Abias tornou-se rei de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9252, 'nvi', '1 Reis', '1rs', 15, 2, 'e reinou três anos em Jerusalém. O nome de sua mãe era Maaca, filha de Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9253, 'nvi', '1 Reis', '1rs', 15, 3, 'Ele cometeu todos os pecados que o seu pai tinha cometido; seu coração não era inteiramente consagrado ao Senhor, ao seu Deus, quanto fora o coração de Davi, seu antepassado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9254, 'nvi', '1 Reis', '1rs', 15, 4, 'No entanto, por amor de Davi, o Senhor, o seu Deus, concedeu-lhe uma lâmpada em Jerusalém dando-lhe um filho como sucessor e fortalecendo Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9255, 'nvi', '1 Reis', '1rs', 15, 5, 'Pois Davi fizera o que o Senhor aprova e não deixara de obedecer a nenhum dos mandamentos do Senhor durante todos os dias da sua vida, exceto no caso de Urias, o hitita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9256, 'nvi', '1 Reis', '1rs', 15, 6, 'E houve guerra entre Roboão e Jeroboão durante toda a vida de Abias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9257, 'nvi', '1 Reis', '1rs', 15, 7, 'Os demais acontecimentos do reinado de Abias e todas as suas realizações estão escritos nos registros históricos dos reis de Judá. Também houve guerra entre Abias e Jeroboão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9258, 'nvi', '1 Reis', '1rs', 15, 8, 'E Abias descansou com os seus antepassados e foi sepultado na cidade de Davi. E o seu filho Asa foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9259, 'nvi', '1 Reis', '1rs', 15, 9, 'No vigésimo ano do reinado de Jeroboão, rei de Israel, Asa tornou-se rei de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9260, 'nvi', '1 Reis', '1rs', 15, 10, 'e reinou quarenta e um anos em Jerusalém. O nome da sua avó era Maaca, filha de Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9261, 'nvi', '1 Reis', '1rs', 15, 11, 'Asa fez o que o Senhor aprova, tal como Davi, seu predecessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9262, 'nvi', '1 Reis', '1rs', 15, 12, 'Expulsou do país os prostitutos cultuais e se desfez de todos os ídolos que seu pai havia feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9263, 'nvi', '1 Reis', '1rs', 15, 13, 'Chegou até a depor sua avó Maaca da posição de rainha-mãe, pois ela havia feito um poste sagrado repugnante. Asa derrubou o poste e o queimou no vale do Cedrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9264, 'nvi', '1 Reis', '1rs', 15, 14, 'Embora os altares idólatras não tenham sido eliminados, o coração de Asa foi totalmente dedicado ao Senhor durante toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9265, 'nvi', '1 Reis', '1rs', 15, 15, 'Ele trouxe para o templo do Senhor a prata, o ouro e os utensílios que ele e seu pai haviam consagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9266, 'nvi', '1 Reis', '1rs', 15, 16, 'Houve guerra entre Asa e Baasa, rei de Israel, durante todo o reinado deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9267, 'nvi', '1 Reis', '1rs', 15, 17, 'Baasa, rei de Israel, invadiu Judá e fortificou Ramá, para que ninguém pudesse entrar nem sair do território de Asa, rei de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9268, 'nvi', '1 Reis', '1rs', 15, 18, 'Então Asa ajuntou a prata e o ouro que haviam sobrado no tesouro do templo do Senhor e do seu próprio palácio. Confiou tudo isso a alguns dos seus oficiais e os enviou a Ben-Hadade, filho de Tabriom e neto de Heziom, rei da Síria, que governava em Damasco,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9269, 'nvi', '1 Reis', '1rs', 15, 19, 'com uma mensagem que dizia: "Façamos um tratado, como fizeram meu pai e o teu. Estou te enviando como presente prata e ouro. Agora, rompe o tratado que tens com Baasa, rei de Israel, para que ele saia do meu país".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9270, 'nvi', '1 Reis', '1rs', 15, 20, 'Ben-Hadade aceitou a proposta do rei Asa e ordenou aos comandantes das suas forças que atacassem as cidades de Israel. Ele conquistou Ijom, Dã, Abel-Bete-Maaca e todo o Quinerete, além de Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9271, 'nvi', '1 Reis', '1rs', 15, 21, 'Quando Baasa soube disso, abandonou a construção dos muros de Ramá e foi para Tirza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9272, 'nvi', '1 Reis', '1rs', 15, 22, 'Então o rei Asa reuniu todos homens de Judá — ninguém foi isentado — e eles retiraram de Ramá as pedras e a madeira que Baasa estivera usando. Com esse material Asa fortificou Geba, em Benjamim, e também Mispá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9273, 'nvi', '1 Reis', '1rs', 15, 23, 'Os demais acontecimentos do reinado de Asa, todas as suas realizações, tudo o que fez e todas as cidades que construiu, tudo isso está escrito nos registros históricos dos reis de Judá. Na velhice Asa sofreu uma doença nos pés,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9274, 'nvi', '1 Reis', '1rs', 15, 24, 'e quando descansou com os seus antepassados, foi sepultado com eles na cidade de Davi, seu pai. E seu filho Josafá foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9275, 'nvi', '1 Reis', '1rs', 15, 25, 'Nadabe, filho de Jeroboão, tornou-se rei de Israel no segundo ano do reinado de Asa, rei de Judá, e reinou dois anos sobre Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9276, 'nvi', '1 Reis', '1rs', 15, 26, 'Fez o que o Senhor reprova, andando nos caminhos do seu pai e no pecado que ele tinha levado Israel a cometer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9277, 'nvi', '1 Reis', '1rs', 15, 27, 'Baasa, filho de Aías, da tribo de Issacar, conspirou contra ele, e o matou na cidade filistéia de Gibetom, enquanto Nadabe e todo o exército de Israel a sitiavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9278, 'nvi', '1 Reis', '1rs', 15, 28, 'Baasa matou Nadabe no terceiro ano do reinado de Asa, rei de Judá, e foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9279, 'nvi', '1 Reis', '1rs', 15, 29, 'Assim que começou a reinar, matou toda a família de Jeroboão. Dos pertencentes a Jeroboão não deixou ninguém vivo, mas destruiu a todos, de acordo com a palavra do Senhor anunciada por seu servo, o silonita Aías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9280, 'nvi', '1 Reis', '1rs', 15, 30, 'Isso aconteceu por causa dos pecados que Jeroboão havia cometido e havia feito Israel cometer, e porque ele tinha provocado a ira do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9281, 'nvi', '1 Reis', '1rs', 15, 31, 'Os demais acontecimentos do reinado de Nadabe e tudo o que fez, estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9282, 'nvi', '1 Reis', '1rs', 15, 32, 'Houve guerra entre Asa e Baasa, rei de Israel, durante todo o reinado deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9283, 'nvi', '1 Reis', '1rs', 15, 33, 'No terceiro ano do reinado de Asa, rei de Judá, Baasa, filho de Aías, tornou-se rei de todo o Israel, em Tirza, e reinou vinte e quatro anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9284, 'nvi', '1 Reis', '1rs', 15, 34, 'Fez o que o Senhor reprova, andando nos caminhos de Jeroboão e nos pecados que ele tinha levado Israel a cometer.');
