INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9343, 'nvi', '1 Reis', '1rs', 18, 1, 'Depois de um longo tempo, no terceiro ano da seca, a palavra do Senhor veio a Elias: "Vá apresentar-se a Acabe, pois enviarei chuva sobre a terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9344, 'nvi', '1 Reis', '1rs', 18, 2, 'E Elias foi. Como a fome era grande em Samaria,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9345, 'nvi', '1 Reis', '1rs', 18, 3, 'Acabe convocou Obadias, o responsável por seu palácio, homem que temia muito ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9346, 'nvi', '1 Reis', '1rs', 18, 4, 'Enquanto Jezabel estava eliminando os profetas do Senhor, Obadias reuniu cem profetas e os escondeu em duas cavernas, cinqüenta em cada uma, e lhes forneceu comida e água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9347, 'nvi', '1 Reis', '1rs', 18, 5, 'Certa vez Acabe disse a Obadias: "Vamos a todas as fontes e vales do país. Talvez consigamos achar um pouco de capim para manter vivos os cavalos e as mulas e assim não será preciso matar nenhum animal".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9348, 'nvi', '1 Reis', '1rs', 18, 6, 'De modo que dividiram o território que iam percorrer; Acabe foi numa direção e Obadias noutra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9349, 'nvi', '1 Reis', '1rs', 18, 7, 'Quando Obadias estava a caminho, Elias o encontrou. Obadias o reconheceu, inclinou-se até o chão e perguntou: "És tu mesmo, meu senhor Elias? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9350, 'nvi', '1 Reis', '1rs', 18, 8, '"Sou", respondeu Elias. "Vá dizer ao seu senhor: Elias está aqui. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9351, 'nvi', '1 Reis', '1rs', 18, 9, '"O que eu fiz de errado", perguntou Obadias, "para que entregues o teu servo a Acabe para ser morto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9352, 'nvi', '1 Reis', '1rs', 18, 10, 'Juro pelo nome do Senhor, o teu Deus, que não há uma só nação ou reino aonde o rei, meu senhor, não enviou alguém para procurar por ti. E, sempre que uma nação ou reino afirmava que tu não estavas lá, ele os fazia jurar que não conseguiram encontrar-te.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9353, 'nvi', '1 Reis', '1rs', 18, 11, 'Mas agora me dizes para ir até o meu senhor e dizer-lhe: ‘Elias está aqui’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9354, 'nvi', '1 Reis', '1rs', 18, 12, 'Não sei para onde o Espírito do Senhor poderá levar-te quando eu te deixar. Se eu for dizer a Acabe e ele não te encontrar, ele me matará. E eu, que sou teu servo, tenho adorado o Senhor desde a minha juventude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9355, 'nvi', '1 Reis', '1rs', 18, 13, 'Por acaso não ouviste, meu senhor, o que eu fiz enquanto Jezabel estava matando os profetas do Senhor? Escondi cem dos profetas do Senhor em duas cavernas, cinqüenta em cada uma, e os abasteci de comida e água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9356, 'nvi', '1 Reis', '1rs', 18, 14, 'E agora me dizes para ir ao meu senhor e dizer-lhe: ‘Elias está aqui’. Ele vai me matar! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9357, 'nvi', '1 Reis', '1rs', 18, 15, 'E disse Elias: "Juro pelo nome do Senhor dos Exércitos, a quem eu sirvo, que hoje eu me apresentarei a Acabe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9358, 'nvi', '1 Reis', '1rs', 18, 16, 'Então Obadias dirigiu-se a Acabe e passou-lhe a informação, e Acabe foi ao encontro de Elias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9359, 'nvi', '1 Reis', '1rs', 18, 17, 'Quando viu Elias, disse-lhe: "É você mesmo, perturbador de Israel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9360, 'nvi', '1 Reis', '1rs', 18, 18, '"Não tenho perturbado Israel", Elias respondeu. "Mas você e a família do seu pai têm. Vocês abandonaram os mandamentos do Senhor e seguiram os baalins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9361, 'nvi', '1 Reis', '1rs', 18, 19, 'Agora convoque todo o povo de Israel para encontrar-se comigo no monte Carmelo. E traga os quatrocentos e cinqüenta profetas de Baal e os quatrocentos profetas de Aserá, que comem à mesa de Jezabel. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9362, 'nvi', '1 Reis', '1rs', 18, 20, 'Acabe convocou então todo o Israel e reuniu os profetas no monte Carmelo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9363, 'nvi', '1 Reis', '1rs', 18, 21, 'Elias dirigiu-se ao povo e disse: "Até quando vocês vão oscilar entre duas opiniões? Se o Senhor é Deus, sigam-no; mas, se Baal é Deus, sigam-no". O povo, porém, nada respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9364, 'nvi', '1 Reis', '1rs', 18, 22, 'Disse então Elias: "Eu sou o único que restou dos profetas do Senhor, mas Baal tem quatrocentos e cinqüenta profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9365, 'nvi', '1 Reis', '1rs', 18, 23, 'Tragam dois novilhos. Escolham eles um, e cortem-no em pedaços e o ponham sobre a lenha, mas não acendam fogo. Eu prepararei o outro novilho e o colocarei sobre a lenha, e também não acenderei fogo nela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9366, 'nvi', '1 Reis', '1rs', 18, 24, 'Então vocês invocarão o nome do seu deus, e eu invocarei o nome do Senhor. O deus que responder por meio do fogo, esse é Deus". Então todo o povo disse: "O que você disse é bom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9367, 'nvi', '1 Reis', '1rs', 18, 25, 'Elias disse aos profetas de Baal: "Escolham um dos novilhos e preparem-no primeiro, visto que vocês são tantos. Clamem pelo nome do seu deus, mas não acendam o fogo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9368, 'nvi', '1 Reis', '1rs', 18, 26, 'Então pegaram o novilho que lhes foi dado e o prepararam. E clamaram pelo nome de Baal desde a manhã até o meio-dia. "Ó Baal, responde-nos! ", gritavam. E dançavam em volta do altar que haviam feito. Mas não houve nenhuma resposta; ninguém respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9369, 'nvi', '1 Reis', '1rs', 18, 27, 'Ao meio-dia Elias começou a zombar deles. "Gritem mais alto! ", dizia, "já que ele é um deus. Quem sabe está meditando, ou ocupado, ou viajando. Talvez esteja dormindo e precise ser despertado. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9370, 'nvi', '1 Reis', '1rs', 18, 28, 'Então passaram a gritar ainda mais alto e a ferir-se com espadas e lanças, de acordo com o costume deles, até sangrarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9371, 'nvi', '1 Reis', '1rs', 18, 29, 'Passou o meio-dia, e eles continuaram profetizando em transe até a hora do sacrifício da tarde. Mas não houve resposta alguma; ninguém respondeu, ninguém deu atenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9372, 'nvi', '1 Reis', '1rs', 18, 30, 'Então Elias disse a todo o povo: "Aproximem-se de mim". O povo aproximou-se, e Elias reparou o altar do Senhor, que estava em ruínas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9373, 'nvi', '1 Reis', '1rs', 18, 31, 'Depois apanhou doze pedras, uma para cada tribo dos descendentes de Jacó, a quem a palavra do Senhor tinha sido dirigida, e disse: "Seu nome será Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9374, 'nvi', '1 Reis', '1rs', 18, 32, 'Com as pedras construiu um altar em honra do nome do Senhor, e cavou ao redor do altar uma valeta com capacidade de duas medidas de semente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9375, 'nvi', '1 Reis', '1rs', 18, 33, 'Depois arrumou a lenha, cortou o novilho em pedaços e o pôs sobre a lenha. Então lhes disse: "Encham de água quatro jarras grandes e derramem-na sobre o holocausto e sobre a lenha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9376, 'nvi', '1 Reis', '1rs', 18, 34, '"Façam-no novamente", disse, e eles o fizeram de novo. "Façam-no pela terceira vez", ordenou, e eles o fizeram pela terceira vez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9377, 'nvi', '1 Reis', '1rs', 18, 35, 'A água escorria do altar, chegando a encher a valeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9378, 'nvi', '1 Reis', '1rs', 18, 36, 'À hora do sacrifício, o profeta Elias colocou-se à frente e orou: "Ó Senhor, Deus de Abraão, de Isaque e de Israel, que hoje fique conhecido que tu és Deus em Israel e que sou o teu servo e que fiz todas estas coisas por ordem tua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9379, 'nvi', '1 Reis', '1rs', 18, 37, 'Responde-me, ó Senhor, responde-me, para que este povo saiba que tu, ó Senhor, és Deus, e que fazes o coração deles voltar para ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9380, 'nvi', '1 Reis', '1rs', 18, 38, 'Então o fogo do Senhor caiu e queimou completamente o holocausto, a lenha, as pedras e o chão, e também secou totalmente a água na valeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9381, 'nvi', '1 Reis', '1rs', 18, 39, 'Quando o povo viu isso, todos caíram prostrados e gritaram: "O Senhor é Deus! O Senhor é Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9382, 'nvi', '1 Reis', '1rs', 18, 40, 'Então Elias ordenou-lhes: "Prendam os profetas de Baal. Não deixem nenhum escapar! " Eles os prenderam, e Elias os fez descer ao riacho de Quisom e lá os matou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9383, 'nvi', '1 Reis', '1rs', 18, 41, 'E Elias disse a Acabe: "Vá comer e beber, pois já ouço o barulho de chuva pesada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9384, 'nvi', '1 Reis', '1rs', 18, 42, 'Então Acabe foi comer e beber, mas Elias subiu até o alto do Carmelo, dobrou-se até o chão e pôs o rosto entre os joelhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9385, 'nvi', '1 Reis', '1rs', 18, 43, '"Vá e olhe na direção do mar", disse ao seu servo. E ele foi e olhou. "Não há nada lá", disse ele. Sete vezes Elias mandou: "Volte para ver".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9386, 'nvi', '1 Reis', '1rs', 18, 44, 'Na sétima vez o servo disse: "Uma nuvem tão pequena quanto a mão de um homem está se levantando do mar". Então Elias disse: "Vá dizer a Acabe: Prepare o seu carro e desça, antes que a chuva o impeça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9387, 'nvi', '1 Reis', '1rs', 18, 45, 'Enquanto isso, nuvens escuras apareceram no céu, começou a ventar e começou a chover forte, e Acabe partiu de carro para Jezreel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9388, 'nvi', '1 Reis', '1rs', 18, 46, 'O poder do Senhor veio sobre Elias, e este, prendendo a capa com o cinto, correu à frente de Acabe por todo o caminho até Jezreel.');
