INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8772, 'nvi', '1 Reis', '1rs', 2, 1, 'Quando se aproximava o dia de sua morte, Davi deu instruções ao seu filho Salomão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8773, 'nvi', '1 Reis', '1rs', 2, 2, '"Estou para seguir o caminho de toda a terra. Por isso, seja forte e seja homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8774, 'nvi', '1 Reis', '1rs', 2, 3, 'Obedeça ao que o Senhor, o seu Deus, exige: Ande nos seus caminhos e obedeça aos seus decretos, aos seus mandamentos, às suas ordenanças e aos seus testemunhos, conforme se acham escritos na Lei de Moisés; assim você prosperará em tudo o que fizer e por onde quer que for,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8775, 'nvi', '1 Reis', '1rs', 2, 4, 'e o Senhor manterá a promessa que me fez: ‘Se os seus descendentes cuidarem de sua conduta, e se me seguirem fielmente de todo o coração e de toda a alma, você jamais ficará sem descendente no trono de Israel’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8776, 'nvi', '1 Reis', '1rs', 2, 5, '"Você sabe muito bem o que Joabe, filho de Zeruia, me fez; o que fez com os dois comandantes dos exércitos de Israel, Abner, filho de Ner, e Amasa, filho de Jéter. Ele os matou, derramando sangue em tempos de paz; agiu como se estivesse em guerra, e com aquele sangue manchou o seu cinto e as suas sandálias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8777, 'nvi', '1 Reis', '1rs', 2, 6, 'Proceda com a sabedoria que você tem, e não o deixe envelhecer e descer em paz à sepultura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8778, 'nvi', '1 Reis', '1rs', 2, 7, '"Mas seja bondoso com os filhos de Barzilai, de Gileade, admita-os entre os que comem à mesa com você, pois eles me apoiaram quando fugi do seu irmão Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8779, 'nvi', '1 Reis', '1rs', 2, 8, '"Saiba que também está com você Simei, filho de Gera, o benjamita de Baurim. Ele lançou terríveis maldições contra mim no dia em que fui a Maanaim. Mas depois desceu ao meu encontro no Jordão e lhe prometi jurando pelo Senhor que não o mataria à espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8780, 'nvi', '1 Reis', '1rs', 2, 9, 'Mas, agora, não o considere inocente. Você é um homem sábio e saberá o que fazer com ele; apesar de ele já ser idoso, faça-o descer ensangüentado à sepultura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8781, 'nvi', '1 Reis', '1rs', 2, 10, 'Então Davi descansou com os seus antepassados e foi sepultado na cidade de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8782, 'nvi', '1 Reis', '1rs', 2, 11, 'Ele reinou quarenta anos em Israel: sete anos em Hebrom e trinta e três em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8783, 'nvi', '1 Reis', '1rs', 2, 12, 'Salomão assentou-se no trono de Davi, seu pai, e o seu reinado foi firmemente estabelecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8784, 'nvi', '1 Reis', '1rs', 2, 13, 'Adonias, o filho de Hagite, foi até Bate-Seba, mãe de Salomão. E esta lhe perguntou: "Você vem em paz? " Ele respondeu: "Sim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8785, 'nvi', '1 Reis', '1rs', 2, 14, 'E acrescentou: "Tenho algo para lhe dizer". Ela disse: Fale!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8786, 'nvi', '1 Reis', '1rs', 2, 15, '"Você sabe", disse ele, "que o reino era meu. Todo o Israel me via como o seu rei. Mas as circunstâncias mudaram, e o reino foi para o meu irmão; pois o Senhor o concedeu a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8787, 'nvi', '1 Reis', '1rs', 2, 16, 'Agora, quero fazer-lhe um pedido e espero que não me seja negado. " Ela disse: "Fale! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8788, 'nvi', '1 Reis', '1rs', 2, 17, 'Então ele prosseguiu: "Peça, por favor, ao rei Salomão que me dê a sunamita Abisague por mulher, pois ele não deixará de atender você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8789, 'nvi', '1 Reis', '1rs', 2, 18, '"Está bem", respondeu Bate-Seba, "falarei com o rei em seu favor. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8790, 'nvi', '1 Reis', '1rs', 2, 19, 'Quando Bate-Seba foi falar ao rei em favor de Adonias, Salomão levantou-se para recebê-la e inclinou-se diante dela. Depois assentou-se no seu trono, mandou que trouxessem um trono para a sua mãe, e ela se assentou à sua direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8791, 'nvi', '1 Reis', '1rs', 2, 20, '"Tenho um pequeno pedido para lhe fazer", disse ela. "Não deixe de me atender. " O rei respondeu: "Faça o pedido, minha mãe; não deixarei de atendê-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8792, 'nvi', '1 Reis', '1rs', 2, 21, 'Então ela disse: "Dê a sunamita Abisague por mulher a seu irmão Adonias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8793, 'nvi', '1 Reis', '1rs', 2, 22, 'O rei Salomão perguntou à sua mãe: "Por que você pede somente a sunamita Abisague para Adonias? Peça logo o reino para ele, para o sacerdote Abiatar e para Joabe, filho de Zeruia; afinal ele é o meu irmão mais velho! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8794, 'nvi', '1 Reis', '1rs', 2, 23, 'Então o rei Salomão jurou pelo Senhor: "Que Deus me castigue com todo o rigor, se isso que Adonias falou não lhe custar a sua própria vida!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8795, 'nvi', '1 Reis', '1rs', 2, 24, 'E agora eu juro pelo nome do Senhor, que me estabeleceu no trono de meu pai Davi, e, conforme prometeu, fundou uma dinastia para mim, que hoje mesmo Adonias será morto! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8796, 'nvi', '1 Reis', '1rs', 2, 25, 'E o rei Salomão deu ordem a Benaia, filho de Joiada, e este feriu e matou Adonias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8797, 'nvi', '1 Reis', '1rs', 2, 26, 'Ao sacerdote Abiatar o rei ordenou: "Vá para Anatote, para as suas terras! Você merece morrer, mas hoje eu não o matarei, pois você carregou a arca do Soberano Senhor diante de Davi, meu pai, e partilhou de todas as aflições dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8798, 'nvi', '1 Reis', '1rs', 2, 27, 'Então Salomão expulsou Abiatar do sacerdócio do Senhor, cumprindo a palavra que o Senhor tinha dito em Siló a respeito da família de Eli.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8799, 'nvi', '1 Reis', '1rs', 2, 28, 'Quando a notícia chegou a Joabe, que havia conspirado com Adonias, ainda que não com Absalão, ele fugiu para a Tenda do Senhor e agarrou-se às pontas do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8800, 'nvi', '1 Reis', '1rs', 2, 29, 'Foi dito ao rei Salomão que Joabe havia se refugiado na Tenda do Senhor e estava ao lado do altar. Então Salomão ordenou a Benaia, filho de Joiada: "Vá matá-lo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8801, 'nvi', '1 Reis', '1rs', 2, 30, 'Então Benaia entrou na Tenda do Senhor e disse a Joabe: "O rei lhe ordena que saia". "Não", respondeu ele, "Vou morrer aqui". Benaia relatou ao rei a resposta de Joabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8802, 'nvi', '1 Reis', '1rs', 2, 31, 'Então o rei ordenou a Benaia: "Faça o que ele diz. Mate-o e sepulte-o, e assim você retirará de mim e da minha família a culpa do sangue inocente que Joabe derramou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8803, 'nvi', '1 Reis', '1rs', 2, 32, 'O Senhor fará recair sobre a cabeça dele o sangue que derramou: ele atacou dois homens mais justos e melhores do que ele, sem o conhecimento de meu pai Davi, e os matou à espada. Os dois homens eram Abner, filho de Ner, comandante do exército de Israel, e Amasa, filho de Jéter, comandante do exército de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8804, 'nvi', '1 Reis', '1rs', 2, 33, 'Que o sangue deles recaia sobre a cabeça de Joabe e sobre a dos seus descendentes para sempre. Mas que a paz do Senhor esteja para sempre sobre Davi, sobre os seus descendentes, sobre a sua dinastia e sobre o seu trono".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8805, 'nvi', '1 Reis', '1rs', 2, 34, 'Então Benaia, filho de Joiada, foi e atacou Joabe e o matou, e ele foi sepultado em sua casa no campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8806, 'nvi', '1 Reis', '1rs', 2, 35, 'No lugar dele o rei nomeou Benaia, filho de Joiada, no comando do exército, e o sacerdote Zadoque no lugar de Abiatar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8807, 'nvi', '1 Reis', '1rs', 2, 36, 'Depois o rei mandou chamar Simei e lhe ordenou: "Construa para você uma casa em Jerusalém. Você morará nela e não poderá ir para nenhum outro lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8808, 'nvi', '1 Reis', '1rs', 2, 37, 'Esteja certo de que no dia em que sair e atravessar o vale de Cedrom, você será morto; e você será responsável por sua própria morte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8809, 'nvi', '1 Reis', '1rs', 2, 38, 'Simei respondeu ao rei: "A ordem do rei é boa! O teu servo lhe obedecerá". E Simei permaneceu em Jerusalém por muito tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8810, 'nvi', '1 Reis', '1rs', 2, 39, 'Mas três anos depois, dois escravos de Simei fugiram para a casa de Aquis, filho de Maaca, rei de Gate. Alguém contou a Simei: "Seus escravos estão em Gate".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8811, 'nvi', '1 Reis', '1rs', 2, 40, 'Então Simei selou um jumento e foi até Aquis, em Gate, procurar os seus escravos. E de lá Simei trouxe os escravos de volta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8812, 'nvi', '1 Reis', '1rs', 2, 41, 'Quando Salomão soube que Simei tinha ido a Gate e voltado a Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8813, 'nvi', '1 Reis', '1rs', 2, 42, 'mandou chamá-lo e lhe perguntou: "Eu não fiz você jurar pelo Senhor e não o adverti: No dia em que você for para qualquer outro lugar, esteja certo que você morrerá? E você me respondeu: ‘Esta ordem é boa! Obedecerei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8814, 'nvi', '1 Reis', '1rs', 2, 43, 'Por que não manteve o juramento ao Senhor e não obedeceu à ordem que lhe dei? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8815, 'nvi', '1 Reis', '1rs', 2, 44, 'E acrescentou: "No seu coração você sabe como você prejudicou o meu pai Davi. Agora o Senhor faz recair sua maldade sobre a sua cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8816, 'nvi', '1 Reis', '1rs', 2, 45, 'Mas o rei Salomão será abençoado, e o trono de Davi será estabelecido perante o Senhor para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8817, 'nvi', '1 Reis', '1rs', 2, 46, 'Então o rei deu ordem a Benaia, filho de Joiada, e este atacou Simei e o matou. Assim o reino ficou bem estabelecido nas mãos de Salomão.');
