INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8818, 'nvi', '1 Reis', '1rs', 3, 1, 'Salomão aliou-se ao faraó, rei do Egito, casando-se com a filha dele. Ele a trouxe à cidade de Davi até terminar a construção do seu palácio e do templo do Senhor, e do muro em torno de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8819, 'nvi', '1 Reis', '1rs', 3, 2, 'O povo, porém, sacrificava nos lugares sagrados, pois ainda não tinha sido construído um templo em honra do nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8820, 'nvi', '1 Reis', '1rs', 3, 3, 'Salomão amava o Senhor, pois andava de acordo com os decretos do seu pai Davi; todavia oferecia sacrifícios e queimava incenso nos lugares sagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8821, 'nvi', '1 Reis', '1rs', 3, 4, 'O rei Salomão foi a Gibeom para oferecer sacrifícios, pois ali ficava o principal lugar sagrado, e ofereceu naquele altar mil holocaustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8822, 'nvi', '1 Reis', '1rs', 3, 5, 'Em Gibeom o Senhor apareceu a Salomão num sonho, à noite, e lhe disse: "Peça-me o que quiser, e eu lhe darei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8823, 'nvi', '1 Reis', '1rs', 3, 6, 'Salomão respondeu: "Tu foste muito bondoso para com o teu servo, o meu pai Davi, pois ele foi fiel a ti, e foi justo e reto de coração. Tu sustentaste grande bondade para com ele e lhe deste um filho que hoje se assenta no seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8824, 'nvi', '1 Reis', '1rs', 3, 7, '"Agora, Senhor meu Deus, fizeste o teu servo reinar em lugar de meu pai Davi. Mas eu não passo de um jovem e não sei o que fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8825, 'nvi', '1 Reis', '1rs', 3, 8, 'Teu servo está aqui entre o povo que escolheste, um povo tão grande que nem se pode contar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8826, 'nvi', '1 Reis', '1rs', 3, 9, 'Dá, pois, ao teu servo um coração cheio de discernimento para governar o teu povo e capaz de distinguir entre o bem e o mal. Pois, quem pode governar este teu grande povo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8827, 'nvi', '1 Reis', '1rs', 3, 10, 'O pedido que Salomão fez agradou ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8828, 'nvi', '1 Reis', '1rs', 3, 11, 'Por isso Deus lhe disse: "Já que você pediu isto e não uma vida longa nem riqueza, nem pediu a morte dos seus inimigos, mas discernimento para ministrar a justiça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8829, 'nvi', '1 Reis', '1rs', 3, 12, 'farei o que você pediu. Eu lhe darei um coração sábio e capaz de discernir, de modo que nunca houve nem haverá ninguém como você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8830, 'nvi', '1 Reis', '1rs', 3, 13, 'Também lhe darei o que você não pediu: riquezas e fama; de forma que não haverá rei igual a você durante toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8831, 'nvi', '1 Reis', '1rs', 3, 14, 'E, se você andar nos meus caminhos e obedecer aos meus decretos e aos meus mandamentos, como o seu pai Davi, eu prolongarei a sua vida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8832, 'nvi', '1 Reis', '1rs', 3, 15, 'Então Salomão acordou e percebeu que tinha sido um sonho. Depois voltou a Jerusalém, pôs-se perante a arca da aliança do Senhor, sacrificou holocaustos e apresentou ofertas de comunhão. Então deu um banquete para toda a sua corte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8833, 'nvi', '1 Reis', '1rs', 3, 16, 'Certo dia duas prostitutas compareceram diante do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8834, 'nvi', '1 Reis', '1rs', 3, 17, 'Uma delas disse: "Ah meu senhor! Esta mulher mora comigo na mesma casa. Eu dei à luz um filho e ela estava comigo na casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8835, 'nvi', '1 Reis', '1rs', 3, 18, 'Três dias depois de nascer o meu filho, esta mulher também deu à luz um filho. Estávamos sozinhas, e não havia mais ninguém na casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8836, 'nvi', '1 Reis', '1rs', 3, 19, '"Certa noite esta mulher se deitou sobre o seu filho, e ele morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8837, 'nvi', '1 Reis', '1rs', 3, 20, 'Então ela se levantou no meio da noite e pegou o meu filho enquanto eu, tua serva, dormia, e o pôs ao seu lado. E pôs o filho dela, morto, ao meu lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8838, 'nvi', '1 Reis', '1rs', 3, 21, 'Ao levantar-me de madrugada para amamentar o meu filho, ele estava morto. Mas quando olhei bem para ele de manhã, vi que não era o filho que eu dera à luz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8839, 'nvi', '1 Reis', '1rs', 3, 22, 'A outra mulher disse: "Não! O que está vivo é meu filho; o morto é seu". Mas a primeira insistia: "Não! O morto é seu; o vivo é meu". Assim elas discutiram diante do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8840, 'nvi', '1 Reis', '1rs', 3, 23, 'O rei disse: "Esta afirma: ‘Meu filho está vivo, e o seu filho está morto’, enquanto aquela diz: ‘Não! Seu filho está morto, e o meu está vivo’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8841, 'nvi', '1 Reis', '1rs', 3, 24, 'Então o rei ordenou: "Tragam-me uma espada". Trouxeram-lhe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8842, 'nvi', '1 Reis', '1rs', 3, 25, 'Ele então ordenou: "Cortem a criança viva ao meio e dêem metade a uma e metade à outra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8843, 'nvi', '1 Reis', '1rs', 3, 26, 'A mãe do filho que estava vivo, movida pela compaixão materna, clamou: "Por favor, meu senhor, dê a criança viva a ela! Não a mate! " A outra, porém, disse: "Não será nem minha nem sua. Cortem-na ao meio! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8844, 'nvi', '1 Reis', '1rs', 3, 27, 'Então o rei deu o seu veredicto: "Não matem a criança! Dêem-na à primeira mulher. Ela é a mãe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8845, 'nvi', '1 Reis', '1rs', 3, 28, 'Quando todo o Israel ouviu o veredicto do rei, passou a respeitá-lo profundamente, pois viu que a sabedoria de Deus estava nele para fazer justiça.');
