INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8846, 'nvi', '1 Reis', '1rs', 4, 1, 'E assim o rei Salomão tornou-se rei sobre todo o Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8847, 'nvi', '1 Reis', '1rs', 4, 2, 'E estes foram os seus principais assessores: Azarias, filho de Zadoque: o sacerdote;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8848, 'nvi', '1 Reis', '1rs', 4, 3, 'Eliorefe e Aías, filhos de Sisa: secretários; Josafá, filho de Ailude: arquivista real;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8849, 'nvi', '1 Reis', '1rs', 4, 4, 'Benaia, filho de Joiada: comandante do exército; Zadoque e Abiatar: sacerdotes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8850, 'nvi', '1 Reis', '1rs', 4, 5, 'Azarias, filho de Natã: responsável pelos governadores distritais; Zabude, filho de Natã: sacerdote e conselheiro pessoal do rei;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8851, 'nvi', '1 Reis', '1rs', 4, 6, 'Aisar: responsável pelo palácio; Adonirão, filho de Abda: chefe de trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8852, 'nvi', '1 Reis', '1rs', 4, 7, 'Salomão tinha também, doze governadores distritais em todo o Israel, que forneciam provisões para o rei e para o palácio real. Cada um deles tinha que fornecer suprimentos durante um mês do ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8853, 'nvi', '1 Reis', '1rs', 4, 8, 'Estes são os seus nomes: Ben-Hur, nos montes de Efraim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8854, 'nvi', '1 Reis', '1rs', 4, 9, 'Ben-Dequer, em Macaz, Saalbim, Bete-Semes e Elom-Bete-Hanã;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8855, 'nvi', '1 Reis', '1rs', 4, 10, 'Ben-Hesede, em Arubote, Socó e toda a terra de Héfer;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8856, 'nvi', '1 Reis', '1rs', 4, 11, 'Ben-Abinadabe, em Nafote-Dor. Tafate, filha de Salomão, era sua mulher;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8857, 'nvi', '1 Reis', '1rs', 4, 12, 'Baaná, filho de Ailude, em Taanaque e Megido e toda Bete-Seã, próxima de Zaretã, abaixo de Jezreel, desde Bete-Seã até Abel-Meolá, indo além dos limites de Jocmeão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8858, 'nvi', '1 Reis', '1rs', 4, 13, 'Ben-Geder, em Ramote-Gileade e nos povoados de Jair, filho de Manassés, em Gileade, bem como o distrito de Argobe, em Basã, e as suas sessenta grandes cidades muradas com trancas de bronze nos portões;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8859, 'nvi', '1 Reis', '1rs', 4, 14, 'Ainadabe, filho de Ido, em Maanaim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8860, 'nvi', '1 Reis', '1rs', 4, 15, 'Aimaás, em Naftali. Ele se casou com Basemate, filha de Salomão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8861, 'nvi', '1 Reis', '1rs', 4, 16, 'Baaná, filho de Husai, em Aser e em Bealote;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8862, 'nvi', '1 Reis', '1rs', 4, 17, 'Josafá, filho de Parua, em Issacar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8863, 'nvi', '1 Reis', '1rs', 4, 18, 'Simei, filho de Elá, em Benjamim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8864, 'nvi', '1 Reis', '1rs', 4, 19, 'Geber, filho de Uri, em Gileade, a terra de Seom, rei dos amorreus, e de Ogue, rei de Basã. Ele era o único governador desse distrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8865, 'nvi', '1 Reis', '1rs', 4, 20, 'Judá e Israel eram tão numerosos como a areia da praia; eles comiam, bebiam e eram felizes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8866, 'nvi', '1 Reis', '1rs', 4, 21, 'E Salomão governava todos os reinos, desde o Rio até a terra dos filisteus, chegando até a fronteira do Egito. Esses reinos traziam tributos e foram submissos a Salomão durante toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8867, 'nvi', '1 Reis', '1rs', 4, 22, 'As provisões diárias de Salomão eram trinta tonéis da melhor farinha e sessenta tonéis de farinha comum,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8868, 'nvi', '1 Reis', '1rs', 4, 23, 'dez cabeças de gado engordado em cocheiras, vinte de gado engordado no pasto e cem ovelhas e bodes, bem como cervos, gazelas, corças e aves escolhidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8869, 'nvi', '1 Reis', '1rs', 4, 24, 'Ele governava todos os reinos a oeste do Rio, desde Tifsa até Gaza, e tinha paz em todas as fronteiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8870, 'nvi', '1 Reis', '1rs', 4, 25, 'Durante a vida de Salomão, Judá e Israel viveram em segurança, cada homem debaixo da sua videira e da sua figueira, desde Dã até Berseba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8871, 'nvi', '1 Reis', '1rs', 4, 26, 'Salomão possuía quatro mil cocheiras para cavalos de carros de guerra, e doze mil cavalos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8872, 'nvi', '1 Reis', '1rs', 4, 27, 'Todo mês um dos governadores distritais fornecia provisões ao rei Salomão e a todos os que vinham participar de sua mesa. Cuidavam para que não faltasse nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8873, 'nvi', '1 Reis', '1rs', 4, 28, 'Também traziam ao devido lugar suas quotas de cevada e de palha para os cavalos de carros de guerra e para os outros cavalos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8874, 'nvi', '1 Reis', '1rs', 4, 29, 'Deus deu a Salomão sabedoria, discernimento extraordinário e uma abrangência de conhecimento tão imensurável quanto a areia do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8875, 'nvi', '1 Reis', '1rs', 4, 30, 'A sabedoria de Salomão era maior do que a de todos os homens do oriente, bem como de toda a sabedoria do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8876, 'nvi', '1 Reis', '1rs', 4, 31, 'Ele era mais sábio do que qualquer outro homem, mais do que o ezraíta Etã; mais sábio do que Hemã, Calcol e Darda, filhos de Maol. E a sua fama espalhou-se por todas as nações em redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8877, 'nvi', '1 Reis', '1rs', 4, 32, 'Ele compôs três mil provérbios, e os seus cânticos chegaram a mil e cinco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8878, 'nvi', '1 Reis', '1rs', 4, 33, 'Descreveu as plantas, desde o cedro do Líbano até o hissopo que brota nos muros. Também discorreu sobre os quadrúpedes, as aves, os animais que se movem rente ao chão e os peixes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8879, 'nvi', '1 Reis', '1rs', 4, 34, 'Homens de todas as nações vinham ouvir a sabedoria de Salomão. Eram enviados por todos os reis que tinham ouvido falar de sua sabedoria.');
