INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8898, 'nvi', '1 Reis', '1rs', 6, 1, 'Quatrocentos e oitenta anos depois que os israelitas saíram do Egito, no quarto ano do reinado de Salomão em Israel, no mês de zive, o segundo mês, ele começou a construir o templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8899, 'nvi', '1 Reis', '1rs', 6, 2, 'O templo que o rei Salomão construiu para o Senhor media vinte e sete metros de comprimento, nove metros de largura e treze metros e meio de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8900, 'nvi', '1 Reis', '1rs', 6, 3, 'O pórtico da entrada do santuário tinha a largura do templo, que era de nove metros, e avançava quatro metros e meio à frente do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8901, 'nvi', '1 Reis', '1rs', 6, 4, 'Ele fez para o templo, janelas com grades estreitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8902, 'nvi', '1 Reis', '1rs', 6, 5, 'Junto às paredes do átrio principal e do santuário interior, construiu uma estrutura em torno do edifício, na qual havia salas laterais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8903, 'nvi', '1 Reis', '1rs', 6, 6, 'O andar inferior tinha dois metros e vinte e cinco centímetros de largura, o andar intermediário tinha dois metros e setenta centímetros e o terceiro andar tinha três metros e quinze centímetros. Ele fez saliências de apoio nas paredes externas do templo, de modo que não houve necessidade de perfurar as paredes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8904, 'nvi', '1 Reis', '1rs', 6, 7, 'Na construção do templo só foram usados blocos lavrados nas pedreiras, e não se ouviu no templo nenhum barulho de martelo, nem de talhadeira, nem de qualquer outra ferramenta de ferro durante a sua construção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8905, 'nvi', '1 Reis', '1rs', 6, 8, 'A entrada para o andar inferior ficava no lado sul do templo; uma escada conduzia até o andar intermediário e dali ao terceiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8906, 'nvi', '1 Reis', '1rs', 6, 9, 'Assim ele construiu o templo e o terminou, fazendo-lhe um forro com vigas e tábuas de cedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8907, 'nvi', '1 Reis', '1rs', 6, 10, 'E fez as salas laterais ao longo de todo o templo. Cada uma tinha dois metros e vinte e cinco centímetros de altura, e elas estavam ligadas ao templo por vigas de cedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8908, 'nvi', '1 Reis', '1rs', 6, 11, 'E a palavra do Senhor veio a Salomão dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8909, 'nvi', '1 Reis', '1rs', 6, 12, '"Quanto a este templo que você está construindo, se você seguir os meus decretos, executar os meus juízos e obedecer a todos os meus mandamentos, cumprirei por meio de você a promessa que fiz ao seu pai Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8910, 'nvi', '1 Reis', '1rs', 6, 13, 'viverei no meio dos israelitas e não abandonarei Israel, o meu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8911, 'nvi', '1 Reis', '1rs', 6, 14, 'E assim Salomão concluiu a construção do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8912, 'nvi', '1 Reis', '1rs', 6, 15, 'Forrou as paredes do templo por dentro com tábuas de cedro, cobrindo-as desde o chão até o teto, e fez o soalho do templo com tábuas de pinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8913, 'nvi', '1 Reis', '1rs', 6, 16, 'Separou nove metros na parte de trás do templo, fazendo uma divisão com tábuas de cedro, do chão ao teto, para formar dentro do templo o santuário interno, o Lugar Santíssimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8914, 'nvi', '1 Reis', '1rs', 6, 17, 'O átrio principal em frente dessa sala media dezoito metros de comprimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8915, 'nvi', '1 Reis', '1rs', 6, 18, 'O interior do templo era de cedro, com figuras entalhadas de frutos e flores abertas. Tudo era de cedro; não se via pedra alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8916, 'nvi', '1 Reis', '1rs', 6, 19, 'Preparou também o santuário interno no templo para ali colocar a arca da aliança do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8917, 'nvi', '1 Reis', '1rs', 6, 20, 'O santuário interno tinha nove metros de comprimento, nove de largura e nove de altura. Ele revestiu o interior de ouro puro, e também revestiu de ouro o altar de cedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8918, 'nvi', '1 Reis', '1rs', 6, 21, 'Salomão cobriu o interior do templo de ouro puro, e estendeu correntes de ouro em frente do santuário interno, que também foi revestido de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8919, 'nvi', '1 Reis', '1rs', 6, 22, 'Assim, revestiu de ouro todo o interior do templo e também o altar que pertencia ao santuário interno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8920, 'nvi', '1 Reis', '1rs', 6, 23, 'No santuário interno ele esculpiu dois querubins de madeira de oliveira, cada um com quatro metros e meio de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8921, 'nvi', '1 Reis', '1rs', 6, 24, 'As asas abertas dos querubins mediam dois metros e vinte e cinco centímetros: quatro metros e meio da ponta de uma asa à ponta da outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8922, 'nvi', '1 Reis', '1rs', 6, 25, 'Os dois querubins tinham a mesma medida e a mesma forma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8923, 'nvi', '1 Reis', '1rs', 6, 26, 'A altura de cada querubim era de quatro metros e meio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8924, 'nvi', '1 Reis', '1rs', 6, 27, 'Ele colocou os querubins, com as asas abertas, no santuário interno do templo. A asa de um querubim encostava numa parede, e a do outro encostava na outra. As suas outras asas encostavam uma na outra no meio do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8925, 'nvi', '1 Reis', '1rs', 6, 28, 'Ele revestiu os querubins de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8926, 'nvi', '1 Reis', '1rs', 6, 29, 'Nas paredes ao redor do templo, tanto na parte interna como na externa, ele esculpiu querubins, tamareiras e flores abertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8927, 'nvi', '1 Reis', '1rs', 6, 30, 'Também revestiu de ouro os pisos, tanto na parte interna como na externa do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8928, 'nvi', '1 Reis', '1rs', 6, 31, 'Para a entrada do santuário interno fez portas de oliveira com batentes de cinco lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8929, 'nvi', '1 Reis', '1rs', 6, 32, 'E nas duas portas de madeira de oliveira esculpiu querubins, tamareiras e flores abertas, e revestiu os querubins e as tamareiras de ouro batido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8930, 'nvi', '1 Reis', '1rs', 6, 33, 'Também fez pilares de quatro lados, de madeira de oliveira para a entrada do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8931, 'nvi', '1 Reis', '1rs', 6, 34, 'Fez também duas portas de pinho, cada uma com duas folhas que se articulavam por meio de dobradiças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8932, 'nvi', '1 Reis', '1rs', 6, 35, 'Entalhou figuras de querubins, de tamareiras e de flores abertas nas portas e as revestiu todas igualmente de ouro batido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8933, 'nvi', '1 Reis', '1rs', 6, 36, 'E construiu o pátio interno com três camadas de pedra lavrada e uma de vigas de cedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8934, 'nvi', '1 Reis', '1rs', 6, 37, 'O alicerce do templo do Senhor foi lançado no mês de zive do quarto ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8935, 'nvi', '1 Reis', '1rs', 6, 38, 'No mês de bul, o oitavo mês, do décimo primeiro ano, o templo foi terminado em todos os seus detalhes, de acordo com as suas especificações. Ele levou sete anos para construí-lo.');
