INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8987, 'nvi', '1 Reis', '1rs', 8, 1, 'Então o rei Salomão reuniu em Jerusalém as autoridades de Israel, todos os líderes das tribos e os chefes das famílias israelitas, para levarem de Sião, a cidade de Davi, a arca da aliança do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8988, 'nvi', '1 Reis', '1rs', 8, 2, 'E todos os homens de Israel uniram-se ao rei Salomão por ocasião da festa, no mês de etanim, que é o sétimo mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8989, 'nvi', '1 Reis', '1rs', 8, 3, 'Quando todas as autoridades de Israel chegaram, os sacerdotes pegaram');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8990, 'nvi', '1 Reis', '1rs', 8, 4, 'a arca do Senhor e a levaram, com a Tenda do Encontro e com todos os seus utensílios sagrados. Foram os sacerdotes e os levitas que levaram tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8991, 'nvi', '1 Reis', '1rs', 8, 5, 'O rei Salomão e toda a comunidade de Israel que se havia reunido a ele diante da arca, sacrificaram tantas ovelhas e bois que nem era possível contar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8992, 'nvi', '1 Reis', '1rs', 8, 6, 'Os sacerdotes levaram a arca da aliança do Senhor para o seu lugar no santuário interno do templo, no Lugar Santíssimo, e a colocaram debaixo das asas dos querubins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8993, 'nvi', '1 Reis', '1rs', 8, 7, 'Os querubins tinham suas asas estendidas sobre o lugar da arca e cobriam a arca e as varas utilizadas para o transporte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8994, 'nvi', '1 Reis', '1rs', 8, 8, 'Essas varas eram tão compridas que as suas pontas, que se estendiam para fora da arca, podiam ser vistas da frente do santuário interno, mas não de fora dele; e elas estão lá até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8995, 'nvi', '1 Reis', '1rs', 8, 9, 'Na arca havia só as duas tábuas de pedra que Moisés tinha colocado quando estava em Horebe, onde o Senhor fez uma aliança com os israelitas depois que saíram do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8996, 'nvi', '1 Reis', '1rs', 8, 10, 'Quando os sacerdotes se retiraram do Lugar Santo, uma nuvem encheu o templo do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8997, 'nvi', '1 Reis', '1rs', 8, 11, 'de forma que os sacerdotes não podiam desempenhar o seu serviço, pois a glória do Senhor encheu o seu templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8998, 'nvi', '1 Reis', '1rs', 8, 12, 'E Salomão exclamou: "O Senhor disse que habitaria numa nuvem escura!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8999, 'nvi', '1 Reis', '1rs', 8, 13, 'Na realidade construí para ti um templo magnífico, um lugar para nele habitares para sempre! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9000, 'nvi', '1 Reis', '1rs', 8, 14, 'Depois o rei virou-se e abençoou toda a assembléia de Israel, que estava ali de pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9001, 'nvi', '1 Reis', '1rs', 8, 15, 'E disse: "Bendito seja o Senhor, o Deus de Israel, que com a sua mão cumpriu o que prometeu com sua própria boca a meu pai Davi, quando lhe disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9002, 'nvi', '1 Reis', '1rs', 8, 16, '‘Desde o dia em que tirei o meu povo Israel do Egito, não escolhi nenhuma cidade das tribos de Israel para nela construir um templo em honra do meu nome. Mas escolhi Davi para governar Israel, o meu povo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9003, 'nvi', '1 Reis', '1rs', 8, 17, '"Meu pai Davi tinha no coração o propósito de construir um templo em honra do nome do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9004, 'nvi', '1 Reis', '1rs', 8, 18, 'Mas o Senhor lhe disse: ‘Você fez bem em ter no coração o plano de construir um templo em honra do meu nome;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9005, 'nvi', '1 Reis', '1rs', 8, 19, 'no entanto, não será você que o construirá, mas o seu filho, que procederá de você; ele construirá o templo em honra do meu nome’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9006, 'nvi', '1 Reis', '1rs', 8, 20, '"E o Senhor cumpriu a sua promessa: Sou o sucessor de meu pai Davi, e agora ocupo o trono de Israel, como o Senhor tinha prometido, e construí o templo em honra do nome do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9007, 'nvi', '1 Reis', '1rs', 8, 21, 'Providenciei nele um lugar para a arca, na qual estão as tábuas da aliança do Senhor, aliança que fez com os nossos antepassados quando os tirou do Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9008, 'nvi', '1 Reis', '1rs', 8, 22, 'Depois Salomão colocou-se diante do altar do Senhor, diante de toda a assembléia de Israel, levantou as mãos para o céu');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9009, 'nvi', '1 Reis', '1rs', 8, 23, 'e orou: "Senhor, Deus de Israel, não há Deus como tu em cima nos céus nem embaixo na terra! Tu que guardas a tua aliança de amor com os teus servos que, de todo o coração, andam segundo a tua vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9010, 'nvi', '1 Reis', '1rs', 8, 24, 'Cumpriste a tua promessa a teu servo Davi, meu pai; com tua boca prometeste e com tua mão a cumpriste, conforme hoje se vê.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9011, 'nvi', '1 Reis', '1rs', 8, 25, '"Agora, Senhor, Deus de Israel, cumpre a outra promessa que fizeste a teu servo Davi, meu pai, quando disseste: ‘Você nunca deixará de ter, diante de mim, um descendente que se assente no trono de Israel, se tão-somente os seus descendentes tiverem o cuidado de, em tudo, andarem segundo a minha vontade, como você tem feito’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9012, 'nvi', '1 Reis', '1rs', 8, 26, 'Agora, ó Deus de Israel, que se confirme a palavra que falaste a teu servo Davi, meu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9013, 'nvi', '1 Reis', '1rs', 8, 27, '"Mas será possível que Deus habite na terra? Os céus, mesmo os mais altos céus, não podem conter-te. Muito menos este templo que construí!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9014, 'nvi', '1 Reis', '1rs', 8, 28, 'Ainda assim, atende à oração do teu servo e ao seu pedido de misericórdia, ó Senhor, meu Deus. Ouve o clamor e a oração que o teu servo faz hoje na tua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9015, 'nvi', '1 Reis', '1rs', 8, 29, 'Estejam os teus olhos voltados dia e noite para este templo, lugar do qual disseste que nele porias o teu nome, para que ouças a oração que o teu servo fizer voltado para este lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9016, 'nvi', '1 Reis', '1rs', 8, 30, 'Ouve as súplicas do teu servo e de Israel, teu povo, quando orarem voltados para este lugar. Ouve desde os céus, lugar da tua habitação, e quando ouvires, dá-lhes o teu perdão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9017, 'nvi', '1 Reis', '1rs', 8, 31, '"Quando um homem pecar contra seu próximo e tiver que fazer um juramento, e vier jurar diante do teu altar neste templo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9018, 'nvi', '1 Reis', '1rs', 8, 32, 'ouve dos céus e age. Julga os teus servos; condena o culpado, fazendo recair sobre a sua própria cabeça o resultado da sua conduta, e declara sem culpa o inocente, dando-lhe o que a sua inocência merece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9019, 'nvi', '1 Reis', '1rs', 8, 33, '"Quando Israel, o teu povo, for derrotado por um inimigo por ter pecado contra ti, e voltar-se para ti e invocar o teu nome, orando e suplicando a ti neste templo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9020, 'nvi', '1 Reis', '1rs', 8, 34, 'ouve dos céus e perdoa o pecado de Israel, teu povo, e traze-o de volta à terra que deste aos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9021, 'nvi', '1 Reis', '1rs', 8, 35, '"Quando fechar-se o céu, e não houver chuva por haver o teu povo pecado contra ti, e, se o teu povo, voltado para este lugar, invocar o teu nome e afastar-se do seu pecado por o haveres castigado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9022, 'nvi', '1 Reis', '1rs', 8, 36, 'ouve dos céus e perdoa o pecado dos teus servos, do teu povo Israel. Ensina-lhes o caminho certo e envia chuva sobre a tua terra, que deste por herança ao teu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9023, 'nvi', '1 Reis', '1rs', 8, 37, '"Quando houver fome ou praga no país, ferrugem e mofo, gafanhotos peregrinos e gafanhotos devastadores, ou quando inimigos sitiarem suas cidades, quando, em meio a qualquer praga ou epidemia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9024, 'nvi', '1 Reis', '1rs', 8, 38, 'uma oração ou súplica por misericórdia for feita por um israelita ou por todo o teu povo Israel, cada um sentindo as suas próprias aflições e dores, estendendo as mãos na direção deste templo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9025, 'nvi', '1 Reis', '1rs', 8, 39, 'ouve dos céus, o lugar da tua habitação. Perdoa e age; trata cada um de acordo com o que merece, visto que conheces o seu coração. Sim, só tu conheces o coração do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9026, 'nvi', '1 Reis', '1rs', 8, 40, 'Assim eles te temerão durante todo o tempo em que viverem na terra que deste aos nossos antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9027, 'nvi', '1 Reis', '1rs', 8, 41, '"Quanto ao estrangeiro, que não pertence ao teu povo Israel, e que veio de uma terra distante por causa do teu nome —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9028, 'nvi', '1 Reis', '1rs', 8, 42, 'pois ouvirão acerca do teu grande nome, da tua mão poderosa e do teu braço forte — quando ele vier e orar voltado para este templo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9029, 'nvi', '1 Reis', '1rs', 8, 43, 'ouve dos céus, lugar da tua habitação, e atende o pedido do estrangeiro, a fim de que todos os povos da terra conheçam o teu nome e te temam, como faz Israel, teu povo, e saibam que este templo que construí traz o teu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9030, 'nvi', '1 Reis', '1rs', 8, 44, '"Quando o teu povo for à guerra contra os seus inimigos, por onde quer que tu o enviares, e orar ao Senhor voltado para a cidade que escolheste e para o templo que construí em honra do teu nome,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9031, 'nvi', '1 Reis', '1rs', 8, 45, 'então ouve dos céus a sua oração e a sua súplica, e defende a sua causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9032, 'nvi', '1 Reis', '1rs', 8, 46, '"Quando pecarem contra ti, pois não há ninguém que não peque, e ficares irado com eles e os entregares ao inimigo, que os leve prisioneiros para a sua terra, distante ou próxima;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9033, 'nvi', '1 Reis', '1rs', 8, 47, 'se eles caírem em si, na terra para a qual foram deportados, e se arrependerem e lá orarem: ‘Pecamos, praticamos o mal e fomos rebeldes’;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9034, 'nvi', '1 Reis', '1rs', 8, 48, 'e se lá eles se voltarem para ti de todo o coração e de toda a sua alma, na terra dos inimigos que os levaram como prisioneiros, e orarem voltados para a terra que deste aos seus antepassados, para a cidade que escolheste e para o templo que construí em honra do teu nome,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9035, 'nvi', '1 Reis', '1rs', 8, 49, 'então, desde os céus, o lugar da tua habitação, ouve a sua oração e a sua súplica, e defende a sua causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9036, 'nvi', '1 Reis', '1rs', 8, 50, 'Perdoa o teu povo, que pecou contra ti; perdoa todas as transgressões que cometeram contra ti, e faze com que os seus conquistadores tenham misericórdia deles;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9037, 'nvi', '1 Reis', '1rs', 8, 51, 'pois são o teu povo e a tua herança, que tiraste do Egito, da fornalha de fundição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9038, 'nvi', '1 Reis', '1rs', 8, 52, '"Que os teus olhos estejam abertos para a súplica do teu servo e para a súplica do teu povo Israel, e que os ouças sempre que clamarem a ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9039, 'nvi', '1 Reis', '1rs', 8, 53, 'Pois tu os escolheste dentre todos os povos da terra para serem a tua herança, assim como declaraste por meio do teu servo Moisés, quando tu, ó Soberano Senhor, tiraste os nossos antepassados do Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9040, 'nvi', '1 Reis', '1rs', 8, 54, 'Quando Salomão terminou a oração e a súplica ao Senhor, levantou-se diante do altar do Senhor, onde tinha se ajoelhado e estendido as mãos para o céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9041, 'nvi', '1 Reis', '1rs', 8, 55, 'Pôs-se de pé e abençoou em voz alta toda a assembléia de Israel, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9042, 'nvi', '1 Reis', '1rs', 8, 56, '"Bendito seja o Senhor, que deu descanso a Israel, seu povo, como havia prometido. Não ficou sem cumprimento nem uma de todas as boas promessas que ele fez por meio do seu servo Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9043, 'nvi', '1 Reis', '1rs', 8, 57, 'Que o Senhor, o nosso Deus, esteja conosco, assim como esteve com os nossos antepassados. Que ele jamais nos deixe nem nos abandone!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9044, 'nvi', '1 Reis', '1rs', 8, 58, 'E faça com que de coração nos voltemos para ele, a fim de andarmos em todos os seus caminhos e obedecermos aos seus mandamentos, decretos e ordenanças, que deu aos nossos antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9045, 'nvi', '1 Reis', '1rs', 8, 59, 'E que as palavras da minha súplica ao Senhor tenham acesso ao Senhor, ao nosso Deus, dia e noite, para que ele defenda a causa do seu servo e a causa de Israel, seu povo, de acordo com o que precisarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9046, 'nvi', '1 Reis', '1rs', 8, 60, 'Assim, todos os povos da terra saberão que o Senhor é Deus e que não há nenhum outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9047, 'nvi', '1 Reis', '1rs', 8, 61, 'Mas vocês, tenham coração íntegro para com o Senhor, o nosso Deus, para viverem por seus decretos e obedecerem aos seus mandamentos, como acontece hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9048, 'nvi', '1 Reis', '1rs', 8, 62, 'Então o rei Salomão e todo o Israel ofereceram sacrifícios ao Senhor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9049, 'nvi', '1 Reis', '1rs', 8, 63, 'ele ofereceu em sacrifício de comunhão ao Senhor vinte e dois mil bois e cento e vinte mil ovelhas. Assim o rei e todos os israelitas fizeram a dedicação do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9050, 'nvi', '1 Reis', '1rs', 8, 64, 'Naquele mesmo dia o rei consagrou a parte central do pátio, que ficava na frente do templo do Senhor, e ali ofereceu holocaustos, ofertas de cereal e a gordura das ofertas de comunhão, pois o altar de bronze diante do Senhor era pequeno demais para comportar os holocaustos, as ofertas de cereal e a gordura das ofertas de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9051, 'nvi', '1 Reis', '1rs', 8, 65, 'E foi assim que Salomão, junto com todo o Israel, celebrou a festa naquela data; era uma grande multidão, gente vinda desde Lebo-Hamate até o ribeiro do Egito. Comemoraram diante do Senhor, o nosso Deus, durante sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9052, 'nvi', '1 Reis', '1rs', 8, 66, 'No oitavo dia Salomão mandou o povo para casa. Eles abençoaram o rei e foram embora, jubilosos e de coração alegre por todas as coisas boas que o Senhor havia feito por seu servo Davi e por seu povo Israel.');
