INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9053, 'nvi', '1 Reis', '1rs', 9, 1, 'Quando Salomão acabou de construir o templo do Senhor, o palácio real e tudo mais que desejara construir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9054, 'nvi', '1 Reis', '1rs', 9, 2, 'o Senhor lhe apareceu pela segunda vez, como lhe havia aparecido em Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9055, 'nvi', '1 Reis', '1rs', 9, 3, 'O Senhor lhe disse: "Ouvi a oração e a súplica que você fez diante de mim; consagrei este templo que você construiu, para que nele habite o meu nome para sempre. Os meus olhos e o meu coração nele sempre estarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9056, 'nvi', '1 Reis', '1rs', 9, 4, '"E se você andar segundo a minha vontade, com integridade de coração e com retidão, como fez o seu pai Davi, se fizer tudo o que eu lhe ordeno, obedecendo aos meus decretos e às minhas ordenanças,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9057, 'nvi', '1 Reis', '1rs', 9, 5, 'firmarei para sempre sobre Israel o seu trono, conforme prometi a Davi, seu pai, quando lhe disse: Você nunca deixará de ter um descendente para governar Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9058, 'nvi', '1 Reis', '1rs', 9, 6, '"Mas, se vocês ou seus filhos se afastarem de mim e não obedecerem aos mandamentos e aos decretos que lhes dei, e prestarem culto a outros deuses e adorá-los,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9059, 'nvi', '1 Reis', '1rs', 9, 7, 'desarraigarei Israel da terra que lhes dei, e lançarei para longe da minha presença este templo que consagrei ao meu nome. Israel se tornará então motivo de zombaria entre todos os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9060, 'nvi', '1 Reis', '1rs', 9, 8, 'E, embora agora este templo seja imponente, todos os que passarem por ele ficarão espantados e perguntarão: ‘Por que o Senhor fez uma coisa dessas a esta terra e a este templo? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9061, 'nvi', '1 Reis', '1rs', 9, 9, 'E a resposta será: ‘Porque abandonaram o Senhor, o seu Deus, que tirou os seus antepassados do Egito, e se apegaram a outros deuses, adorando-os e prestando-lhes culto; por isso o Senhor trouxe sobre eles toda esta desgraça’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9062, 'nvi', '1 Reis', '1rs', 9, 10, 'Depois de vinte anos, durante os quais construiu estes dois edifícios, o templo do Senhor e o palácio real,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9063, 'nvi', '1 Reis', '1rs', 9, 11, 'o rei Salomão deu vinte cidades da Galiléia a Hirão, rei de Tiro, pois Hirão lhe havia fornecido toda a madeira de cedro e de pinho e o ouro que ele precisou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9064, 'nvi', '1 Reis', '1rs', 9, 12, 'Mas, quando este veio de Tiro para ver as cidades que Salomão lhe dera, não gostou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9065, 'nvi', '1 Reis', '1rs', 9, 13, '"Que cidades são essas que você me deu, meu irmão? ", ele perguntou. E as chamou terra de Cabul, nome que elas têm até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9066, 'nvi', '1 Reis', '1rs', 9, 14, 'Hirão tinha enviado ao rei quatro mil e duzentos quilos de ouro!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9067, 'nvi', '1 Reis', '1rs', 9, 15, 'O rei Salomão impôs trabalhos forçados para que se construísse o templo do Senhor, seu próprio palácio, o Milo, o muro de Jerusalém, como também Hazor, Megido e Gezer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9068, 'nvi', '1 Reis', '1rs', 9, 16, 'O faraó, rei do Egito, havia atacado e conquistado Gezer. Incendiou a cidade e matou os seus habitantes, que eram cananeus, e a deu como presente de casamento à sua filha, mulher de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9069, 'nvi', '1 Reis', '1rs', 9, 17, 'E Salomão reconstruiu Gezer. Ele construiu Bete-Horom Baixa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9070, 'nvi', '1 Reis', '1rs', 9, 18, 'Baalate, e Tadmor, no deserto dessa região,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9071, 'nvi', '1 Reis', '1rs', 9, 19, 'bem como todas as cidades-armazéns e as cidades onde ficavam os seus carros de guerra e os seus cavalos. Construiu tudo o que desejou em Jerusalém, no Líbano e em todo o território que governou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9072, 'nvi', '1 Reis', '1rs', 9, 20, 'Todos os não israelitas, descendentes dos amorreus, dos hititas, dos ferezeus, dos heveus e dos jebuseus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9073, 'nvi', '1 Reis', '1rs', 9, 21, 'que não tinham sido mortos pelos israelitas, Salomão recrutou para o trabalho forçado, como continuam até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9074, 'nvi', '1 Reis', '1rs', 9, 22, 'Mas Salomão não obrigou nenhum israelita a trabalhos forçados; eles eram seus homens de guerra, os seus capitães, e os comandantes dos seus carros de guerra e os condutores de carros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9075, 'nvi', '1 Reis', '1rs', 9, 23, 'Também eram israelitas os principais funcionários encarregados das construções de Salomão: quinhentos e cinqüenta funcionários que supervisionavam os trabalhadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9076, 'nvi', '1 Reis', '1rs', 9, 24, 'Somente depois que a filha do faraó mudou-se da cidade de Davi para o palácio que Salomão havia construído para ela, foi que ele construiu o Milo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9077, 'nvi', '1 Reis', '1rs', 9, 25, 'Três vezes por ano Salomão oferecia holocaustos e sacrifícios de comunhão no altar que havia construído para o Senhor, e ao mesmo tempo queimava incenso diante do Senhor. Assim, ele concluiu o templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9078, 'nvi', '1 Reis', '1rs', 9, 26, 'O rei Salomão também construiu navios em Eziom-Geber, que fica perto de Elate, na terra de Edom, às margens do mar Vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9079, 'nvi', '1 Reis', '1rs', 9, 27, 'E Hirão enviou em navios os seus marinheiros, homens experimentados que conheciam o mar, para trabalharem com os marinheiros de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9080, 'nvi', '1 Reis', '1rs', 9, 28, 'Navegaram até Ofir, e de lá trouxeram catorze toneladas e setecentos quilos de ouro para o rei Salomão.');
