INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7213, 'nvi', '1 Samuel', '1sm', 1, 1, 'Havia certo homem de Ramataim, zufita, dos montes de Efraim, chamado Elcana, filho de Jeroão, neto de Eliú e bisneto de Toú, filho do efraimita Zufe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7214, 'nvi', '1 Samuel', '1sm', 1, 2, 'Ele tinha duas mulheres; uma se chamava Ana, e a outra Penina. Penina tinha filhos, Ana, porém, não tinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7215, 'nvi', '1 Samuel', '1sm', 1, 3, 'Todos os anos esse homem subia de sua cidade a Siló para adorar e sacrificar ao Senhor dos Exércitos. Lá, Hofni e Finéias, os dois filhos de Eli, eram sacerdotes do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7216, 'nvi', '1 Samuel', '1sm', 1, 4, 'No dia em que Elcana oferecia sacrifícios, dava porções à sua mulher Penina e a todos os filhos e filhas dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7217, 'nvi', '1 Samuel', '1sm', 1, 5, 'Mas a Ana dava uma porção dupla, porque a amava, mesmo que o Senhor a houvesse deixado estéril.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7218, 'nvi', '1 Samuel', '1sm', 1, 6, 'E porque o Senhor a tinha deixado estéril, sua rival a provocava continuamente, a fim de irritá-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7219, 'nvi', '1 Samuel', '1sm', 1, 7, 'Isso acontecia ano após ano. Sempre que Ana subia à casa do Senhor, sua rival a provocava e ela chorava e não comia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7220, 'nvi', '1 Samuel', '1sm', 1, 8, 'Elcana, seu marido, lhe perguntava: "Ana, por que você está chorando? Por que não come? Por que está triste? Será que eu não sou melhor para você do que dez filhos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7221, 'nvi', '1 Samuel', '1sm', 1, 9, 'Certa vez quando terminou de comer e beber em Siló, estando o sacerdote Eli sentado numa cadeira junto à entrada do santuário do Senhor, Ana se levantou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7222, 'nvi', '1 Samuel', '1sm', 1, 10, 'e, com a alma amargurada, chorou muito e orou ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7223, 'nvi', '1 Samuel', '1sm', 1, 11, 'E fez um voto, dizendo: "Ó Senhor dos Exércitos, se tu deres atenção à humilhação de tua serva, te lembrares de mim e não te esqueceres de tua serva, mas lhe deres um filho, então eu o dedicarei ao Senhor por todos os dias de sua vida, e o seu cabelo e a sua barba nunca serão cortados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7224, 'nvi', '1 Samuel', '1sm', 1, 12, 'Enquanto ela continuava a orar diante do Senhor, Eli observava sua boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7225, 'nvi', '1 Samuel', '1sm', 1, 13, 'Como Ana orava silenciosamente, seus lábios se mexiam mas não se ouvia sua voz. Então Eli pensou que ela estivesse embriagada');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7226, 'nvi', '1 Samuel', '1sm', 1, 14, 'e lhe disse: "Até quando você continuará embriagada? Abandone o vinho! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7227, 'nvi', '1 Samuel', '1sm', 1, 15, 'Ana respondeu: "Não se trata disso, meu senhor. Sou uma mulher muito angustiada. Não bebi vinho nem bebida fermentada; eu estava derramando minha alma diante do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7228, 'nvi', '1 Samuel', '1sm', 1, 16, 'Não julgues tua serva uma mulher vadia; estou orando aqui até agora por causa de minha grande angústia e tristeza".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7229, 'nvi', '1 Samuel', '1sm', 1, 17, 'Eli respondeu: "Vá em paz, e que o Deus de Israel lhe conceda o que você pediu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7230, 'nvi', '1 Samuel', '1sm', 1, 18, 'Ela disse: "Espero que sejas benevolente para com tua serva! " Então ela seguiu seu caminho, comeu, e seu rosto já não estava mais abatido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7231, 'nvi', '1 Samuel', '1sm', 1, 19, 'Na manhã seguinte, eles se levantaram e adoraram ao Senhor; então voltaram para casa, em Ramá. Elcana teve relações com sua mulher Ana, e o Senhor se lembrou dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7232, 'nvi', '1 Samuel', '1sm', 1, 20, 'Assim Ana engravidou e, no devido tempo, deu à luz um filho. E deu-lhe o nome de Samuel, dizendo: "Eu o pedi ao Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7233, 'nvi', '1 Samuel', '1sm', 1, 21, 'Quando no ano seguinte Elcana subiu com toda a família para oferecer o sacrifício anual ao Senhor e para cumprir o seu voto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7234, 'nvi', '1 Samuel', '1sm', 1, 22, 'Ana não foi e disse a seu marido: "Depois que o menino for desmamado, eu o levarei e o apresentarei ao Senhor, e ele morará ali para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7235, 'nvi', '1 Samuel', '1sm', 1, 23, 'Disse Elcana, seu marido: "Faça o que lhe parecer melhor. Fique aqui até desmamá-lo; que o Senhor apenas confirme a palavra dele! " Então ela ficou em casa e amamentou seu filho até que o desmamou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7236, 'nvi', '1 Samuel', '1sm', 1, 24, 'Depois de desmamá-lo, levou o menino, ainda pequeno, à casa do Senhor, em Siló, com um novilho de três anos de idade, uma arroba de farinha e uma vasilha de couro cheia de vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7237, 'nvi', '1 Samuel', '1sm', 1, 25, 'Eles sacrificaram o novilho e levaram o menino a Eli,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7238, 'nvi', '1 Samuel', '1sm', 1, 26, 'e ela lhe disse: "Meu senhor, juro por tua vida que eu sou a mulher que esteve aqui a teu lado, orando ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7239, 'nvi', '1 Samuel', '1sm', 1, 27, 'Era este menino que eu pedia, e o Senhor concedeu-me o pedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7240, 'nvi', '1 Samuel', '1sm', 1, 28, 'Por isso, agora, eu o dedico ao Senhor. Por toda a sua vida será dedicado ao Senhor". E ali adorou o Senhor.');
