INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7419, 'nvi', '1 Samuel', '1sm', 10, 1, 'Então Samuel apanhou um jarro de óleo, derramou-o sobre a cabeça de Saul e o beijou, dizendo: "O Senhor o tem ungido como líder da herança dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7420, 'nvi', '1 Samuel', '1sm', 10, 2, 'Hoje, quando você partir, encontrará dois homens perto do túmulo de Raquel, em Zelza, na fronteira de Benjamim. Eles lhe dirão: ‘As jumentas que você foi procurar já foram encontradas. Agora seu pai deixou de se importar com elas e está preocupado com vocês. Ele está perguntando: "Como encontrarei meu filho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7421, 'nvi', '1 Samuel', '1sm', 10, 3, 'Então, dali, você prosseguirá para o carvalho de Tabor. Três homens virão subindo ao santuário de Deus em Betel, e encontrarão você ali. Um estará levando três cabritos, outro três pães, e outro uma vasilha de couro cheia de vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7422, 'nvi', '1 Samuel', '1sm', 10, 4, 'Eles o cumprimentarão e lhe oferecerão dois pães, que você deve aceitar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7423, 'nvi', '1 Samuel', '1sm', 10, 5, 'Depois você irá a Gibeá de Deus, onde há um destacamento filisteu. Ao chegar à cidade, você encontrará um grupo de profetas descendo do altar no monte tocando liras, tamborins, flautas e harpas; e eles estarão profetizando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7424, 'nvi', '1 Samuel', '1sm', 10, 6, 'O Espírito do Senhor se apossará de você, e com eles você profetizará em transe, e será um novo homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7425, 'nvi', '1 Samuel', '1sm', 10, 7, 'Assim que esses sinais tiverem se cumprido, faça o que achar melhor, pois Deus está com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7426, 'nvi', '1 Samuel', '1sm', 10, 8, 'Vá na minha frente até Gilgal. Depois eu irei também, para oferecer holocaustos e sacrifícios de comunhão, mas você deve esperar sete dias, até que eu chegue e lhe diga o que fazer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7427, 'nvi', '1 Samuel', '1sm', 10, 9, 'Quando se virou para afastar-se de Samuel, Deus mudou o coração de Saul, e todos esses sinais se cumpriram naquele dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7428, 'nvi', '1 Samuel', '1sm', 10, 10, 'Chegando em Gibeá, um grupo de profetas o encontrou; o Espírito de Deus se apossou dele, e ele profetizou em transe no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7429, 'nvi', '1 Samuel', '1sm', 10, 11, 'Quando os que já o conheciam viram-no profetizando com os profetas, perguntaram uns aos outros: "O que aconteceu ao filho de Quis? Saul também está entre os profetas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7430, 'nvi', '1 Samuel', '1sm', 10, 12, 'Um homem daquele lugar respondeu: "E quem é o pai deles? " De modo que isto se tornou um ditado: "Saul também está entre os profetas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7431, 'nvi', '1 Samuel', '1sm', 10, 13, 'Depois que Saul parou de profetizar, foi para o altar no monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7432, 'nvi', '1 Samuel', '1sm', 10, 14, 'Então o tio de Saul perguntou a ele e ao seu servo: "Aonde vocês foram? " Ele respondeu: "Procurar as jumentas. Quando, porém, vimos que não seriam encontradas, fomos falar com Samuel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7433, 'nvi', '1 Samuel', '1sm', 10, 15, '"O que Samuel lhe disse? ", perguntou o tio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7434, 'nvi', '1 Samuel', '1sm', 10, 16, 'Saul respondeu: "Ele nos garantiu que as jumentas tinham sido encontradas". Todavia, Saul não contou ao tio o que Samuel tinha dito sobre o reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7435, 'nvi', '1 Samuel', '1sm', 10, 17, 'Samuel convocou o povo de Israel ao Senhor, em Mispá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7436, 'nvi', '1 Samuel', '1sm', 10, 18, 'e lhes disse: "Assim diz o Senhor, o Deus de Israel: ‘Eu tirei Israel do Egito, e libertei vocês do poder do Egito e de todos os reinos que os oprimiam’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7437, 'nvi', '1 Samuel', '1sm', 10, 19, 'Mas vocês agora rejeitaram o Deus que os salva de todas as suas desgraças e angústias. E disseram: ‘Não! Escolhe um rei para nós’. Por isso, agora, apresentem-se perante o Senhor, de acordo com as suas tribos e clãs".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7438, 'nvi', '1 Samuel', '1sm', 10, 20, 'Tendo Samuel feito todas as tribos de Israel se aproximarem, a de Benjamim foi escolhida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7439, 'nvi', '1 Samuel', '1sm', 10, 21, 'Então fez ir à frente a tribo de Benjamim, clã por clã, e o clã de Matri foi escolhido. Finalmente foi escolhido Saul, filho de Quis. Quando, porém, o procuraram, ele não foi encontrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7440, 'nvi', '1 Samuel', '1sm', 10, 22, 'Então consultaram novamente o Senhor: "Ele já chegou? " E o Senhor disse: "Sim, ele está escondido no meio da bagagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7441, 'nvi', '1 Samuel', '1sm', 10, 23, 'Então correram e o tiraram de lá. Quando ficou de pé no meio do povo; os mais altos do povo batiam-lhe nos ombros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7442, 'nvi', '1 Samuel', '1sm', 10, 24, 'E Samuel disse a todos: "Vocês vêem o homem que o Senhor escolheu? Não há ninguém como ele entre todo o povo". Então todos gritaram: "Viva o rei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7443, 'nvi', '1 Samuel', '1sm', 10, 25, 'Samuel expôs ao povo as leis do reino. Ele as escreveu num livro e o pôs perante o Senhor. Então Samuel mandou o povo de volta para suas casas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7444, 'nvi', '1 Samuel', '1sm', 10, 26, 'Saul também foi para sua casa em Gibeá, acompanhado por guerreiros, cujos corações Deus tinha tocado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7445, 'nvi', '1 Samuel', '1sm', 10, 27, 'Alguns vadios, porém, disseram: "Como este homem pode nos salvar? " Desprezaram-no e não lhe trouxeram presente algum. Mas Saul ficou calado.');
