INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7486, 'nvi', '1 Samuel', '1sm', 13, 1, 'Saul tinha trinta anos de idade quando começou a reinar, e reinou sobre Israel quarenta e dois anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7487, 'nvi', '1 Samuel', '1sm', 13, 2, 'Saul escolheu três mil homens de Israel; dois mil ficaram com ele em Micmás e nos montes de Betel, e mil ficaram com Jônatas em Gibeá de Benjamim. O restante dos homens ele mandou de volta para suas tendas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7488, 'nvi', '1 Samuel', '1sm', 13, 3, 'Jônatas atacou os destacamentos dos filisteus em Gibeá, e os filisteus foram informados disso. Então Saul mandou tocar a trombeta por todo o país dizendo: "Que os hebreus fiquem sabendo disto! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7489, 'nvi', '1 Samuel', '1sm', 13, 4, 'E todo Israel ouviu a notícia de que Saul tinha atacado o destacamento dos filisteus atraindo o ódio dos filisteus sobre Israel. Então os homens foram convocados para se unirem a Saul em Gilgal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7490, 'nvi', '1 Samuel', '1sm', 13, 5, 'Os filisteus reuniram-se para lutar contra Israel, com três mil carros de guerra, seis mil condutores de carros e tantos soldados quanto a areia da praia. Eles foram a Micmás, a leste de Bete-Áven e lá acamparam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7491, 'nvi', '1 Samuel', '1sm', 13, 6, 'Quando os soldados de Israel viram que a situação era difícil e que seu exército estava sendo muito pressionado, esconderam-se em cavernas e buracos, entre as rochas e em poços e cisternas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7492, 'nvi', '1 Samuel', '1sm', 13, 7, 'Alguns hebreus até atravessaram o Jordão para chegar à terra de Gade e de Gileade. Saul ficou em Gilgal, e os soldados que estavam com ele tremiam de medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7493, 'nvi', '1 Samuel', '1sm', 13, 8, 'Ele esperou sete dias, o prazo estabelecido por Samuel; mas este não chegou a Gilgal, e os soldados de Saul começaram a se dispersar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7494, 'nvi', '1 Samuel', '1sm', 13, 9, 'Então ele ordenou: "Tragam-me o holocausto e os sacrifícios de comunhão". Saul ofereceu então o holocausto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7495, 'nvi', '1 Samuel', '1sm', 13, 10, 'e quando ele terminou de oferecê-lo, Samuel chegou, e Saul foi saudá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7496, 'nvi', '1 Samuel', '1sm', 13, 11, 'E perguntou-lhe Samuel: "O que você fez? " Saul respondeu: "Quando vi que os soldados estavam se dispersando e que você não tinha chegado no prazo estabelecido e que os filisteus estavam reunidos em Micmás,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7497, 'nvi', '1 Samuel', '1sm', 13, 12, 'pensei: ‘Agora, os filisteus me atacarão em Gilgal, e eu não busquei o Senhor’. Por isso senti-me obrigado a oferecer o holocausto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7498, 'nvi', '1 Samuel', '1sm', 13, 13, 'Disse Samuel: "Você agiu como tolo, desobedecendo ao mandamento que o Senhor seu Deus lhe deu; se você tivesse obedecido, ele teria estabelecido para sempre o seu reinado sobre Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7499, 'nvi', '1 Samuel', '1sm', 13, 14, 'Mas agora seu reinado não permanecerá; o Senhor procurou um homem segundo o seu coração e o designou líder de seu povo, pois você não obedeceu ao mandamento do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7500, 'nvi', '1 Samuel', '1sm', 13, 15, 'Então Samuel partiu de Gilgal e foi a Gibeá de Benjamim, e Saul contou soldados que estavam com ele. Eram cerca de seiscentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7501, 'nvi', '1 Samuel', '1sm', 13, 16, 'Saul e seu filho Jônatas, acompanhados de seus soldados, ficaram em Gibeá de Benjamim, enquanto os filisteus estavam acampados em Micmás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7502, 'nvi', '1 Samuel', '1sm', 13, 17, 'Uma tropa de ataque saiu do acampamento filisteu em três divisões. Uma foi em direção a Ofra, nos arredores de Sual,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7503, 'nvi', '1 Samuel', '1sm', 13, 18, 'outra para Bete-Horom, e a terceira para a região fronteiriça de onde se avista o vale de Zeboim, diante do deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7504, 'nvi', '1 Samuel', '1sm', 13, 19, 'Naquela época não havia nem mesmo um único ferreiro em toda a terra de Israel, pois os filisteus não queriam que os hebreus fizessem espadas e lanças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7505, 'nvi', '1 Samuel', '1sm', 13, 20, 'Assim, eles tinham que ir aos filisteus para afiar seus arados, enxadas, machados e foices.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7506, 'nvi', '1 Samuel', '1sm', 13, 21, 'O preço para afiar rastelos e enxadas era oito gramas de prata, e quatro gramas de prata para afiar tridentes, machados e pontas de aguilhadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7507, 'nvi', '1 Samuel', '1sm', 13, 22, 'Por isso no dia da batalha nenhum soldado de Saul e Jônatas tinha espada ou lança nas mãos, exceto o próprio Saul e seu filho Jônatas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7508, 'nvi', '1 Samuel', '1sm', 13, 23, 'Aconteceu que um destacamento filisteu foi para o desfiladeiro de Micmás.');
