INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7596, 'nvi', '1 Samuel', '1sm', 16, 1, 'O Senhor disse a Samuel: "Até quando você irá se entristecer por causa de Saul? Eu o rejeitei como rei de Israel. Encha um chifre com óleo e vá a Belém; eu o enviarei a Jessé. Escolhi um de seus filhos para ser rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7597, 'nvi', '1 Samuel', '1sm', 16, 2, 'Samuel, porém, disse: "Como poderei ir? Saul saberá disto e me matará". O Senhor disse: "Leve um novilho com você e diga que foi sacrificar ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7598, 'nvi', '1 Samuel', '1sm', 16, 3, 'Convide Jessé para o sacrifício, e eu lhe mostrarei o que fazer. Você irá ungir para mim aquele que eu indicar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7599, 'nvi', '1 Samuel', '1sm', 16, 4, 'Samuel fez o que o Senhor disse. Quando chegou a Belém, as autoridades da cidade foram encontrar-se com ele tremendo e perguntaram: "Você vem em paz? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7600, 'nvi', '1 Samuel', '1sm', 16, 5, 'Respondeu Samuel: "Sim, venho em paz; vim sacrificar ao Senhor. Consagrem-se e venham ao sacrifício comigo". Então ele consagrou Jessé e os filhos dele e os convidou para o sacrifício.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7601, 'nvi', '1 Samuel', '1sm', 16, 6, 'Quando chegaram, Samuel viu Eliabe e pensou: "Com certeza este aqui é o que o Senhor quer ungir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7602, 'nvi', '1 Samuel', '1sm', 16, 7, 'O Senhor, contudo, disse a Samuel: "Não considere a sua aparência nem sua altura, pois eu o rejeitei. O Senhor não vê como o homem: o homem vê a aparência, mas o Senhor vê o coração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7603, 'nvi', '1 Samuel', '1sm', 16, 8, 'Então Jessé chamou Abinadabe e o levou a Samuel. Ele, porém, disse: "O Senhor também não escolheu a este".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7604, 'nvi', '1 Samuel', '1sm', 16, 9, 'Então Jessé levou Samá a Samuel, mas este disse: "Também não foi este que o Senhor escolheu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7605, 'nvi', '1 Samuel', '1sm', 16, 10, 'Jessé levou a Samuel sete de seus filhos, mas Samuel lhe disse: "O Senhor não escolheu nenhum destes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7606, 'nvi', '1 Samuel', '1sm', 16, 11, 'Então perguntou a Jessé: "Estes são todos os filhos que você tem? " Jessé respondeu: "Ainda tenho o caçula, mas ele está cuidando das ovelhas". Samuel disse: "Traga-o aqui; não nos sentaremos para comer até que ele chegue".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7607, 'nvi', '1 Samuel', '1sm', 16, 12, 'Então Jessé mandou chamá-lo e ele veio. Ele era ruivo, de belos olhos e boa aparência. Então o Senhor disse a Samuel: "É este! Levante-se e unja-o".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7608, 'nvi', '1 Samuel', '1sm', 16, 13, 'Samuel então apanhou o chifre cheio de óleo e o ungiu na presença de seus irmãos, e a partir daquele dia o Espírito do Senhor apoderou-se de Davi. E Samuel voltou para Ramá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7609, 'nvi', '1 Samuel', '1sm', 16, 14, 'O Espírito do Senhor se retirou de Saul, e um espírito maligno, vindo da parte do Senhor, o atormentava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7610, 'nvi', '1 Samuel', '1sm', 16, 15, 'Os funcionários de Saul lhe disseram: "Há um espírito maligno mandado por Deus te atormentando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7611, 'nvi', '1 Samuel', '1sm', 16, 16, 'Que nosso soberano mande estes seus servos procurar um homem que saiba tocar a harpa. Quando o espírito maligno se apoderar de ti, o homem tocará a harpa e tu te sentirás melhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7612, 'nvi', '1 Samuel', '1sm', 16, 17, 'E Saul respondeu aos que o serviam: "Encontrem alguém que toque bem e o tragam até aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7613, 'nvi', '1 Samuel', '1sm', 16, 18, 'Um dos funcionários respondeu: "Conheço um dos filhos de Jessé, de Belém, que sabe tocar harpa. É um guerreiro valente, sabe falar bem, tem boa aparência e o Senhor está com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7614, 'nvi', '1 Samuel', '1sm', 16, 19, 'Então Saul mandou mensageiros a Jessé com a seguinte mensagem: "Envie-me seu filho Davi, que cuida das ovelhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7615, 'nvi', '1 Samuel', '1sm', 16, 20, 'Jessé apanhou um jumento e o carregou de pães, uma vasilha de couro cheia de vinho e um cabrito e o enviou a Saul por meio de Davi, seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7616, 'nvi', '1 Samuel', '1sm', 16, 21, 'Davi foi apresentar-se a Saul e passou a trabalhar para ele. Saul gostou muito dele, e Davi tornou-se seu escudeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7617, 'nvi', '1 Samuel', '1sm', 16, 22, 'Então Saul enviou a seguinte mensagem a Jessé: "Deixe que Davi continue trabalhando para mim, pois estou satisfeito com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7618, 'nvi', '1 Samuel', '1sm', 16, 23, 'E sempre que o espírito mandado por Deus se apoderava de Saul, Davi apanhava sua harpa e tocava. Então Saul sentia alívio e melhorava, e o espírito maligno o deixava.');
