INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7731, 'nvi', '1 Samuel', '1sm', 20, 1, 'Então Davi fugiu de Naiote, em Ramá, foi falar com Jônatas e perguntou: "O que foi que eu fiz? Qual é o meu crime? Qual foi o pecado que cometi contra seu pai para que ele queira tirar minha vida? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7732, 'nvi', '1 Samuel', '1sm', 20, 2, '"Nem pense nisso", respondeu Jônatas, "você não será morto! Meu pai não fará coisa alguma sem antes me avisar, seja importante ou não. Por que ele iria esconder isso de mim? Não é nada disso! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7733, 'nvi', '1 Samuel', '1sm', 20, 3, 'Davi, contudo, fez um juramento e disse: "Seu pai sabe muito bem que eu conto com a sua simpatia, e pensou: ‘Jônatas não deve saber disso para não se entristecer’. No entanto, eu juro pelo nome do Senhor e por sua vida que estou a um passo da morte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7734, 'nvi', '1 Samuel', '1sm', 20, 4, 'Jônatas disse a Davi: "Eu farei o que você achar necessário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7735, 'nvi', '1 Samuel', '1sm', 20, 5, 'Então disse Davi: "Amanhã é a festa da lua nova, e devo jantar com o rei; mas deixe-me ir esconder-me no campo até o final da tarde de depois de amanhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7736, 'nvi', '1 Samuel', '1sm', 20, 6, 'Se seu pai sentir minha falta, diga-lhe: ‘Davi insistiu comigo para que lhe permitisse ir a Belém, sua cidade natal, por causa do sacrifício anual que está sendo feito lá por todo o seu clã’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7737, 'nvi', '1 Samuel', '1sm', 20, 7, 'Se ele disser: ‘Está bem’, então seu servo estará seguro. Se ele, porém, ficar muito irado, você pode estar certo de que está decidido a me fazer mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7738, 'nvi', '1 Samuel', '1sm', 20, 8, 'Mas seja leal a seu servo, porque fizemos um acordo perante o Senhor. Se sou culpado, então mate-me você mesmo! Por que entregar-me a seu pai? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7739, 'nvi', '1 Samuel', '1sm', 20, 9, 'Disse Jônatas: "Nem pense nisso! Se eu tiver a menor suspeita de que meu pai está decidido a matá-lo, certamente eu o avisarei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7740, 'nvi', '1 Samuel', '1sm', 20, 10, 'Davi perguntou: "Quem irá contar-me, se seu pai lhe responder asperamente? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7741, 'nvi', '1 Samuel', '1sm', 20, 11, 'Jônatas disse: "Venha, vamos ao campo". Eles foram');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7742, 'nvi', '1 Samuel', '1sm', 20, 12, 'e Jônatas disse a Davi: "Pelo Senhor, o Deus de Israel, prometo que sondarei meu pai, a esta hora, depois de amanhã! Saberei se as suas intenções são boas ou não para com você, e lhe mandarei avisar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7743, 'nvi', '1 Samuel', '1sm', 20, 13, 'E, se meu pai quiser fazer-lhe mal, que o Senhor me castigue com todo rigor, se eu não lhe informar e não deixá-lo ir em segurança. O Senhor esteja com você assim como esteve com meu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7744, 'nvi', '1 Samuel', '1sm', 20, 14, 'Se eu continuar vivo, mostre a lealdade do Senhor a mim; mas se eu morrer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7745, 'nvi', '1 Samuel', '1sm', 20, 15, 'jamais deixe de mostrar a sua lealdade para com a minha família, inclusive quando o Senhor eliminar da face da terra todos os inimigos de Davi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7746, 'nvi', '1 Samuel', '1sm', 20, 16, 'Assim Jônatas fez uma aliança com a família de Davi, dizendo: "Que o Senhor chame os inimigos de Davi para prestarem contas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7747, 'nvi', '1 Samuel', '1sm', 20, 17, 'E Jônatas fez Davi reafirmar seu juramento, por causa de sua amizade por ele, pois ele havia se tornado seu amigo leal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7748, 'nvi', '1 Samuel', '1sm', 20, 18, 'Então Jônatas disse a Davi: "Amanhã é a festa da lua nova. Vão sentir sua falta, pois sua cadeira estará vazia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7749, 'nvi', '1 Samuel', '1sm', 20, 19, 'Depois de amanhã, vá ao lugar onde você se escondeu quando tudo isto começou, e espere junto à pedra de Ezel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7750, 'nvi', '1 Samuel', '1sm', 20, 20, 'Atirarei três flechas para o lado dela, como se estivesse atirando num alvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7751, 'nvi', '1 Samuel', '1sm', 20, 21, 'Então mandarei um menino procurar as flechas. Se eu gritar para ele: ‘As flechas estão mais para cá, traga-as aqui’, você poderá vir, pois, juro pelo nome do Senhor que você estará seguro; não haverá perigo algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7752, 'nvi', '1 Samuel', '1sm', 20, 22, 'Mas, se eu gritar para ele: ‘Olhe, as flechas estão mais para lá’, vá embora, pois o Senhor o manda ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7753, 'nvi', '1 Samuel', '1sm', 20, 23, 'Quanto ao nosso acordo, o Senhor é testemunha entre mim e você para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7754, 'nvi', '1 Samuel', '1sm', 20, 24, 'Então Davi escondeu-se no campo. Quando chegou a festa da lua nova, o rei sentou-se à mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7755, 'nvi', '1 Samuel', '1sm', 20, 25, 'Ele se assentou no lugar de costume, junto à parede, em frente de Jônatas, e Abner sentou-se ao lado de Saul, mas o lugar de Davi ficou vazio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7756, 'nvi', '1 Samuel', '1sm', 20, 26, 'Saul não disse nada naquele dia, pois pensou: "Algo deve ter acontecido a Davi, deixando-o cerimonialmente impuro. Com certeza ele está impuro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7757, 'nvi', '1 Samuel', '1sm', 20, 27, 'No dia seguinte, o segundo dia da festa da lua nova, o lugar de Davi continuou vazio. Então Saul perguntou a seu filho Jônatas: "Por que o filho de Jessé não veio para a refeição, nem ontem nem hoje? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7758, 'nvi', '1 Samuel', '1sm', 20, 28, 'Jônatas respondeu: "Davi me pediu, com insistência, permissão para ir a Belém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7759, 'nvi', '1 Samuel', '1sm', 20, 29, 'dizendo: ‘Deixe-me ir, pois nossa família oferecerá um sacrifício na cidade, e meu irmão ordenou que eu estivesse lá. Se conto com a sua simpatia, deixe-me ir ver meus irmãos’. Por isso ele não veio à mesa do rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7760, 'nvi', '1 Samuel', '1sm', 20, 30, 'A ira de Saul se acendeu contra Jônatas, e ele lhe disse: "Filho de uma mulher perversa e rebelde! Será que eu não sei que você tem apoiado o filho de Jessé para sua própria vergonha e para vergonha daquela que o deu à luz?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7761, 'nvi', '1 Samuel', '1sm', 20, 31, 'Enquanto o filho de Jessé viver, nem você nem seu reino serão estabelecidos. Agora mande chamá-lo e traga-o a mim, pois ele deve morrer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7762, 'nvi', '1 Samuel', '1sm', 20, 32, 'Jônatas perguntou a seu pai: "Por que ele deve morrer? O que ele fez? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7763, 'nvi', '1 Samuel', '1sm', 20, 33, 'Então Saul atirou sua lança contra Jônatas para matá-lo. E assim Jônatas percebeu que seu pai estava decidido a matar Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7764, 'nvi', '1 Samuel', '1sm', 20, 34, 'Jônatas levantou-se da mesa muito irado; naquele segundo dia da festa da lua nova ele não comeu, pois estava triste porque seu pai havia humilhado a Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7765, 'nvi', '1 Samuel', '1sm', 20, 35, 'Pela manhã, Jônatas saiu ao campo para encontrar Davi. Levava consigo um menino');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7766, 'nvi', '1 Samuel', '1sm', 20, 36, 'e lhe disse: "Corra e ache as flechas que eu atirar". O menino correu e Jônatas atirou uma flecha para além dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7767, 'nvi', '1 Samuel', '1sm', 20, 37, 'Quando o menino chegou ao lugar onde a flecha havia caído, Jônatas gritou: "A flecha não está mais para lá?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7768, 'nvi', '1 Samuel', '1sm', 20, 38, 'Vamos! Rápido! Não pare! " O menino apanhou a flecha e voltou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7769, 'nvi', '1 Samuel', '1sm', 20, 39, 'sem saber de nada, pois somente Jônatas e Davi sabiam do que tinham combinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7770, 'nvi', '1 Samuel', '1sm', 20, 40, 'Então Jônatas deu suas armas ao menino e disse: "Vá, leve-as de volta à cidade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7771, 'nvi', '1 Samuel', '1sm', 20, 41, 'Depois que o menino se foi, Davi saiu do lado sul da pedra e inclinou-se três vezes perante Jônatas, com o rosto no chão. Então despediram-se beijando um ao outro e chorando; Davi chorou ainda mais do que Jônatas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7772, 'nvi', '1 Samuel', '1sm', 20, 42, 'E ele disse a Davi: "Vá em paz, pois temos jurado um ao outro, em nome do Senhor, dizendo: ‘O Senhor para sempre é testemunha entre nós e entre os nossos descendentes’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7773, 'nvi', '1 Samuel', '1sm', 20, 43, 'Então Davi partiu, e Jônatas voltou à cidade.');
