INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7789, 'nvi', '1 Samuel', '1sm', 22, 1, 'Davi fugiu da cidade de Gate e foi para a caverna de Adulão. Quando seus irmãos e a família de seu pai souberam disso, foram até lá para encontrá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7790, 'nvi', '1 Samuel', '1sm', 22, 2, 'Também juntaram-se a ele todos os que estavam em dificuldades, os endividados e os descontentes; e ele se tornou o líder deles. Havia cerca de quatrocentos homens com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7791, 'nvi', '1 Samuel', '1sm', 22, 3, 'De lá Davi foi para Mispá, em Moabe, e disse ao rei de Moabe: "Posso deixar meu pai e minha mãe virem para cá e ficarem contigo até que eu saiba o que Deus fará comigo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7792, 'nvi', '1 Samuel', '1sm', 22, 4, 'E assim ele os deixou com o rei de Moabe, e lá eles ficaram enquanto Davi permaneceu na fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7793, 'nvi', '1 Samuel', '1sm', 22, 5, 'Contudo, o profeta Gade disse a Davi: "Não fique na fortaleza. Vá para Judá". Então Davi foi para a floresta de Herete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7794, 'nvi', '1 Samuel', '1sm', 22, 6, 'Saul ficou sabendo que Davi e seus homens tinham sido descobertos. Saul estava sentado, com a lança na mão, debaixo da tamargueira, na colina de Gibeá, com todos os seus oficiais ao redor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7795, 'nvi', '1 Samuel', '1sm', 22, 7, 'e ele lhes disse: "Ouçam, homens de Benjamim! Será que o filho de Jessé lhes dará a todos vocês terras e vinhas? Será que ele os fará todos comandantes de mil e comandantes de cem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7796, 'nvi', '1 Samuel', '1sm', 22, 8, 'É por isso que todos vocês têm conspirado contra mim? Ninguém me informa quando meu filho faz acordo com o filho de Jessé. Nenhum de vocês se preocupa comigo nem me avisa que meu filho incitou meu servo a ficar à minha espreita, como ele hoje faz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7797, 'nvi', '1 Samuel', '1sm', 22, 9, 'Entretanto, Doegue, o edomita, que estava com os oficiais de Saul, disse: "Vi o filho de Jessé chegar em Nobe e encontrar-se com Aimeleque, filho de Aitube.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7798, 'nvi', '1 Samuel', '1sm', 22, 10, 'Aimeleque consultou o Senhor em favor dele; também lhe deu provisões e a espada de Golias, o filisteu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7799, 'nvi', '1 Samuel', '1sm', 22, 11, 'Então o rei mandou chamar o sacerdote Aimeleque, filho de Aitube, e toda a família de seu pai, que eram os sacerdotes em Nobe, e todos foram falar com o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7800, 'nvi', '1 Samuel', '1sm', 22, 12, 'E Saul disse: "Ouça agora, filho de Aitube". Ele respondeu: "Sim, meu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7801, 'nvi', '1 Samuel', '1sm', 22, 13, 'Saul lhe disse: "Por que vocês conspiraram contra mim, você e o filho de Jessé? Porque você lhe deu comida e espada, e consultou a Deus em favor dele, para que se rebelasse contra mim e me armasse cilada, como ele está fazendo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7802, 'nvi', '1 Samuel', '1sm', 22, 14, 'Aimeleque respondeu ao rei: "Quem dentre todos os teus oficiais é tão leal quanto Davi, o genro do rei, capitão de sua guarda pessoal e altamente respeitado em sua casa?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7803, 'nvi', '1 Samuel', '1sm', 22, 15, 'Será que foi essa a primeira vez que consultei a Deus em favor dele? Certamente que não! Que o rei não acuse a mim, seu servo, nem qualquer um da família de meu pai, pois seu servo nada sabe acerca do que está acontecendo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7804, 'nvi', '1 Samuel', '1sm', 22, 16, 'O rei, porém, disse: "Com certeza você será morto, Aimeleque, você e toda a família de seu pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7805, 'nvi', '1 Samuel', '1sm', 22, 17, 'Então o rei ordenou aos guardas que estavam ao seu lado: "Matem os sacerdotes do Senhor, pois eles também apóiam Davi. Sabiam que ele estava fugindo, mas nada me informaram". Contudo, os oficiais do rei recusaram erguer as mãos para matar os sacerdotes do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7806, 'nvi', '1 Samuel', '1sm', 22, 18, 'Então o rei ordenou a Doegue: "Mate os sacerdotes", e ele os matou. E naquele dia, matou oitenta e cinco homens que vestiam túnica de linho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7807, 'nvi', '1 Samuel', '1sm', 22, 19, 'Além disso, Saul mandou matar os habitantes de Nobe, a cidade dos sacerdotes: homens, mulheres, crianças, recém-nascidos, bois, jumentos e ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7808, 'nvi', '1 Samuel', '1sm', 22, 20, 'Entretanto, Abiatar, filho de Aimeleque e neto de Aitube, escapou e fugiu para juntar-se a Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7809, 'nvi', '1 Samuel', '1sm', 22, 21, 'E contou a Davi que Saul havia matado os sacerdotes do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7810, 'nvi', '1 Samuel', '1sm', 22, 22, 'Então Davi disse a Abiatar: "Naquele dia, quando o edomita Doegue estava ali, eu sabia que ele não deixaria de informar a Saul. Sou responsável pela morte de toda a família de seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7811, 'nvi', '1 Samuel', '1sm', 22, 23, 'Fique comigo, não tenha medo; o homem que está atrás de sua vida também está atrás da minha. Mas você estará a salvo comigo".');
