INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7812, 'nvi', '1 Samuel', '1sm', 23, 1, 'Quando disseram a Davi que os filisteus estavam atacando a cidade de Queila e saqueando as eiras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7813, 'nvi', '1 Samuel', '1sm', 23, 2, 'ele perguntou ao Senhor: "Devo atacar esses filisteus? " O Senhor lhe respondeu: "Vá, ataque os filisteus e liberte Queila".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7814, 'nvi', '1 Samuel', '1sm', 23, 3, 'Os soldados de Davi, porém, lhe disseram: "Aqui em Judá estamos com medo. Quanto mais, então, se formos a Queila lutar contra as tropas dos filisteus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7815, 'nvi', '1 Samuel', '1sm', 23, 4, 'Davi consultou o Senhor novamente. "Levante-se", disse o Senhor, "vá à cidade de Queila, pois estou entregando os filisteus em suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7816, 'nvi', '1 Samuel', '1sm', 23, 5, 'Então Davi e seus homens foram a Queila, combateram os filisteus e se apoderaram de seus rebanhos, impondo-lhes grande derrota e libertando o povo de Queila.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7817, 'nvi', '1 Samuel', '1sm', 23, 6, 'Ora, Abiatar, filho de Aimeleque, tinha levado o colete sacerdotal, quando fugiu para se juntar a Davi, em Queila.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7818, 'nvi', '1 Samuel', '1sm', 23, 7, 'Foi dito a Saul que Davi tinha ido a Queila, e ele disse: "Deus o entregou nas minhas mãos, pois Davi se aprisionou ao entrar numa cidade com portas e trancas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7819, 'nvi', '1 Samuel', '1sm', 23, 8, 'E Saul convocou todo o seu exército para a batalha, para irem a Queila e cercarem Davi e os homens que o seguiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7820, 'nvi', '1 Samuel', '1sm', 23, 9, 'Quando Davi soube que Saul tramava atacá-lo, disse a Abiatar: "Traga o colete sacerdotal".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7821, 'nvi', '1 Samuel', '1sm', 23, 10, 'Então orou: "Ó Senhor, Deus de Israel, este teu servo ouviu claramente que Saul planeja vir a Queila e destruir a cidade por causa dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7822, 'nvi', '1 Samuel', '1sm', 23, 11, 'Será que os cidadãos de Queila me entregarão a ele? Saul virá de fato, conforme teu servo ouviu? Ó Senhor, Deus de Israel, responde". E o Senhor disse: "Ele virá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7823, 'nvi', '1 Samuel', '1sm', 23, 12, 'E Davi, novamente, perguntou: "Será que os cidadãos de Queila entregarão a mim e a meus soldados a Saul? " E o Senhor respondeu: "Entregarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7824, 'nvi', '1 Samuel', '1sm', 23, 13, 'Então Davi e seus soldados, que eram cerca de seiscentos, partiram de Queila, e ficaram andando sem direção definida. Quando informaram a Saul que Davi tinha fugido de Queila, ele interrompeu a marcha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7825, 'nvi', '1 Samuel', '1sm', 23, 14, 'Davi permaneceu nas fortalezas do deserto e nas colinas do deserto de Zife. Dia após dia, Saul o procurava, mas Deus não entregou Davi em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7826, 'nvi', '1 Samuel', '1sm', 23, 15, 'Quando Davi estava em Horesa, no deserto de Zife, soube que Saul tinha saído para matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7827, 'nvi', '1 Samuel', '1sm', 23, 16, 'E Jônatas, filho de Saul, foi falar com ele, em Horesa, e o ajudou a encontrar forças em Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7828, 'nvi', '1 Samuel', '1sm', 23, 17, '"Não tenha medo", disse ele, "meu pai não porá as mãos em você. Você será rei de Israel, e eu lhe serei o segundo em comando. Até meu pai sabe disso. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7829, 'nvi', '1 Samuel', '1sm', 23, 18, 'Os dois fizeram um acordo perante o Senhor. Então, Jônatas foi para casa, mas Davi ficou em Horesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7830, 'nvi', '1 Samuel', '1sm', 23, 19, 'Alguns zifeus foram informar a Saul, em Gibeá: "Davi está se escondendo entre nós nas fortalezas de Horesa, na colina de Haquilá, ao sul do Deserto de Jesimom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7831, 'nvi', '1 Samuel', '1sm', 23, 20, 'Agora, ó rei, vá quando quiser, e nós seremos responsáveis por entregá-lo em suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7832, 'nvi', '1 Samuel', '1sm', 23, 21, 'Saul respondeu: "O Senhor os abençoe por terem compaixão de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7833, 'nvi', '1 Samuel', '1sm', 23, 22, 'Vão e façam mais preparativos. Descubram aonde Davi geralmente vai e quem o tem visto ali. Dizem-me que ele é muito astuto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7834, 'nvi', '1 Samuel', '1sm', 23, 23, 'Descubram todos os esconderijos dele e voltem aqui com informações exatas. Então irei com vocês; se ele estiver na região, eu o procurarei entre todos os clãs de Judá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7835, 'nvi', '1 Samuel', '1sm', 23, 24, 'E eles voltaram para Zife, à frente de Saul. Davi e seus soldados estavam no deserto de Maom, na Arabá, ao sul do deserto de Jesimom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7836, 'nvi', '1 Samuel', '1sm', 23, 25, 'Saul e seus soldados começaram a busca, e, ao ser informado disso, Davi desceu à rocha e permaneceu no deserto de Maom. Sabendo disso, Saul foi para lá em perseguição a Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7837, 'nvi', '1 Samuel', '1sm', 23, 26, 'Saul ia por um lado da montanha, e Davi e seus soldados pelo outro, fugiam depressa para escapar de Saul. Quando Saul e suas tropas estavam cercando Davi e seus soldados para capturá-los,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7838, 'nvi', '1 Samuel', '1sm', 23, 27, 'um mensageiro veio dizer a Saul: "Venha depressa! Os filisteus estão atacando Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7839, 'nvi', '1 Samuel', '1sm', 23, 28, 'Então Saul interrompeu a perseguição a Davi e foi enfrentar os filisteus. Por isso chamam esse lugar Selá-Hamalecote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7840, 'nvi', '1 Samuel', '1sm', 23, 29, 'E Davi saiu daquele lugar e foi viver nas fortalezas de En-Gedi.');
