INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7863, 'nvi', '1 Samuel', '1sm', 25, 1, 'Samuel morreu, e todo o Israel se reuniu e o pranteou; e o sepultaram onde vivia, em Ramá. Depois Davi foi para o deserto de Maom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7864, 'nvi', '1 Samuel', '1sm', 25, 2, 'Certo homem de Maom, que tinha seus bens na cidade de Carmelo, era muito rico. Possuía mil cabras e três mil ovelhas, as quais estavam sendo tosquiadas em Carmelo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7865, 'nvi', '1 Samuel', '1sm', 25, 3, 'Seu nome era Nabal e o nome de sua mulher era Abigail, mulher inteligente e bonita; mas seu marido, descendente de Calebe, era rude e mau.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7866, 'nvi', '1 Samuel', '1sm', 25, 4, 'No deserto, Davi ficou sabendo que Nabal estava tosquiando as ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7867, 'nvi', '1 Samuel', '1sm', 25, 5, 'Por isso, enviou dez rapazes e lhes disse: "Levem minha mensagem a Nabal, em Carmelo, e o cumprimentem em meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7868, 'nvi', '1 Samuel', '1sm', 25, 6, 'Digam-lhe: ‘Longa vida para o senhor! Muita paz para o senhor e sua família! E muita prosperidade para tudo que é teu! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7869, 'nvi', '1 Samuel', '1sm', 25, 7, '‘Sei que estás tosquiando tuas ovelhas. Quando os teus pastores estavam conosco, nós não os maltratamos, e durante todo o tempo em que estiveram em Carmelo não se perdeu nada que fosse deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7870, 'nvi', '1 Samuel', '1sm', 25, 8, 'Pergunte a eles, e eles lhe dirão. Por isso, seja favorável, pois estamos vindo em época de festa. Por favor, dá a nós teus servos e a teu filho Davi o que puderes’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7871, 'nvi', '1 Samuel', '1sm', 25, 9, 'Os rapazes foram e deram a Nabal essa mensagem, em nome de Davi. E ficaram esperando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7872, 'nvi', '1 Samuel', '1sm', 25, 10, 'Nabal respondeu então aos servos de Davi: "Quem é Davi? Quem é esse filho de Jessé? Hoje em dia, muitos servos estão fugindo de seus senhores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7873, 'nvi', '1 Samuel', '1sm', 25, 11, 'Por que deveria eu pegar meu pão e minha água, e a carne do gado que abati para meus tosquiadores, e dá-los a homens que vêm não se sabe de onde? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7874, 'nvi', '1 Samuel', '1sm', 25, 12, 'Então, os mensageiros de Davi voltaram, e ao chegarem, relataram a ele cada uma dessas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7875, 'nvi', '1 Samuel', '1sm', 25, 13, 'Davi ordenou a seus homens: "Ponham suas espadas na cintura! " Assim eles fizeram e também Davi. Cerca de quatrocentos homens acompanharam Davi, enquanto duzentos permaneceram com a bagagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7876, 'nvi', '1 Samuel', '1sm', 25, 14, 'Um dos servos disse a Abigail, mulher de Nabal: "Do deserto, Davi enviou mensageiros para saudar o nosso senhor, mas ele os insultou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7877, 'nvi', '1 Samuel', '1sm', 25, 15, 'No entanto, aqueles homens foram muito bons para conosco. Não nos maltrataram, e, durante todo o tempo em que estivemos com eles nos campos, nada perdemos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7878, 'nvi', '1 Samuel', '1sm', 25, 16, 'Dia e noite eles eram como um muro ao nosso redor, durante todo o tempo em que estivemos com eles cuidando de nossas ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7879, 'nvi', '1 Samuel', '1sm', 25, 17, 'Agora, leve isso em consideração e veja o que a senhora pode fazer, pois a destruição paira sobre o nosso senhor e sobre toda a sua família. Ele é um homem tão mau que ninguém consegue conversar com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7880, 'nvi', '1 Samuel', '1sm', 25, 18, 'Imediatamente, Abigail pegou duzentos pães, duas vasilhas de couro cheias de vinho, cinco ovelhas preparadas, cinco medidas de grãos torrados, cem bolos de uvas passas e duzentos bolos de figos prensados, e os carregou em jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7881, 'nvi', '1 Samuel', '1sm', 25, 19, 'E disse a seus servos: "Vocês vão na frente; eu os seguirei". Ela, porém, nada disse a Nabal, seu marido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7882, 'nvi', '1 Samuel', '1sm', 25, 20, 'Enquanto ela ia montada num jumento, encoberta pela montanha, Davi e seus soldados estavam descendo em sua direção, e ela os encontrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7883, 'nvi', '1 Samuel', '1sm', 25, 21, 'Davi tinha dito: "De nada adiantou proteger os bens daquele homem no deserto, para que nada se perdesse. Ele me pagou o bem com o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7884, 'nvi', '1 Samuel', '1sm', 25, 22, 'Que Deus castigue a Davi, e o faça com muita severidade, caso até de manhã eu deixe vivo um só do sexo masculino de todos os que pertencem a Nabal! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7885, 'nvi', '1 Samuel', '1sm', 25, 23, 'Quando Abigail viu Davi, desceu depressa do jumento e prostrou-se perante Davi, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7886, 'nvi', '1 Samuel', '1sm', 25, 24, 'Ela caiu a seus pés e disse: "Meu senhor, a culpa é toda minha. Por favor, deixa a tua serva lhe falar; ouve o que ela tem a dizer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7887, 'nvi', '1 Samuel', '1sm', 25, 25, 'Meu senhor, não dês atenção àquele homem mau, Nabal. Ele é insensato, conforme o seu nome significa; e a insensatez o acompanha. Contudo, eu, tua serva, não vi os rapazes que meu senhor enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7888, 'nvi', '1 Samuel', '1sm', 25, 26, '"Agora, meu senhor, juro pelo nome do Senhor e por tua vida que foi o Senhor que o impediu de derramar sangue e de vingar-se com tuas próprias mãos. Que teus inimigos e todos os que pretendem fazer-te mal sejam castigados como Nabal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7889, 'nvi', '1 Samuel', '1sm', 25, 27, 'E que este presente que esta tua serva trouxe ao meu senhor seja dado aos homens que o seguem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7890, 'nvi', '1 Samuel', '1sm', 25, 28, 'Esqueça, eu te suplico, a ofensa de tua serva, pois o Senhor certamente fará um reino duradouro para ti, que travas os combates do Senhor. E em toda a tua vida, nenhuma culpa se ache em ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7891, 'nvi', '1 Samuel', '1sm', 25, 29, 'Mesmo que alguém te persiga para tirar-te a vida, a vida de meu senhor estará firmemente segura como a dos que são protegidos pelo Senhor teu Deus. Mas a vida de teus inimigos será atirada para longe como por uma atiradeira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7892, 'nvi', '1 Samuel', '1sm', 25, 30, 'Quando o Senhor tiver feito a meu senhor todo o bem que prometeu e te tiver nomeado líder sobre Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7893, 'nvi', '1 Samuel', '1sm', 25, 31, 'meu senhor não terá no coração o peso de ter derramado sangue desnecessariamente nem de ter feito justiça com as próprias mãos. E, quando o Senhor tiver abençoado a ti, lembra-te de tua serva".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7894, 'nvi', '1 Samuel', '1sm', 25, 32, 'Davi disse a Abigail: "Bendito seja o Senhor, o Deus de Israel, que hoje a enviou ao meu encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7895, 'nvi', '1 Samuel', '1sm', 25, 33, 'Seja você abençoada pelo seu bom senso e por evitar que eu hoje derrame sangue e me vingue com minhas próprias mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7896, 'nvi', '1 Samuel', '1sm', 25, 34, 'De outro modo, juro pelo nome do Senhor, o Deus de Israel, que evitou que eu lhe fizesse mal, se você não tivesse vindo depressa encontrar-me, nem um só do sexo masculino pertencente a Nabal teria sido deixado vivo ao romper do dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7897, 'nvi', '1 Samuel', '1sm', 25, 35, 'Então Davi aceitou o que ela havia lhe trazido e disse: "Vá para sua casa em paz. Ouvi o que você disse e atenderei o seu pedido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7898, 'nvi', '1 Samuel', '1sm', 25, 36, 'Quando Abigail retornou a Nabal, ele estava dando um banquete em casa, como um banquete de rei. Ele estava alegre e bastante bêbado, e ela nada lhe falou até o amanhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7899, 'nvi', '1 Samuel', '1sm', 25, 37, 'De manhã, quando Nabal estava sóbrio, sua mulher lhe contou todas essas coisas; ele sofreu um ataque e ficou paralisado como uma pedra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7900, 'nvi', '1 Samuel', '1sm', 25, 38, 'Cerca de dez dias depois, o Senhor feriu a Nabal, e ele morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7901, 'nvi', '1 Samuel', '1sm', 25, 39, 'Quando Davi soube que Nabal estava morto, disse: "Bendito seja o Senhor, que defendeu a minha causa contra Nabal, por ter me tratado com desprezo. O Senhor impediu seu servo de praticar o mal e fez com que a maldade de Nabal caísse sobre sua própria cabeça". Então Davi enviou uma mensagem a Abigail, pedindo-lhe que se tornasse sua mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7902, 'nvi', '1 Samuel', '1sm', 25, 40, 'Seus servos foram a Carmelo e disseram a Abigail: "Davi nos enviou a você para levá-la para tornar-se mulher dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7903, 'nvi', '1 Samuel', '1sm', 25, 41, 'Ela se levantou, depois inclinou-se rosto em terra e disse: "Aqui está a sua serva, pronta para servi-los e lavar os pés dos servos de meu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7904, 'nvi', '1 Samuel', '1sm', 25, 42, 'Abigail logo montou num jumento e, acompanhada por suas cinco servas, foi com os mensageiros de Davi e tornou-se sua mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7905, 'nvi', '1 Samuel', '1sm', 25, 43, 'Davi também casou-se com Ainoã de Jezreel; e as duas foram suas mulheres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7906, 'nvi', '1 Samuel', '1sm', 25, 44, 'Saul, porém, tinha dado sua filha Mical, mulher de Davi, a Paltiel, filho de Laís, de Galim.');
