INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7907, 'nvi', '1 Samuel', '1sm', 26, 1, 'Os zifeus foram falar com Saul, em Gibeá, e disseram: "Davi está escondido na colina de Haquilá, em frente do deserto de Jesimom"');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7908, 'nvi', '1 Samuel', '1sm', 26, 2, 'Então Saul desceu ao deserto de Zife, com três mil dos melhores soldados de Israel, em busca de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7909, 'nvi', '1 Samuel', '1sm', 26, 3, 'Saul acampou ao lado da estrada, na colina de Haquilá, em frente do deserto de Jesimom, mas Davi permaneceu no deserto. Quando viu que Saul o estava seguindo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7910, 'nvi', '1 Samuel', '1sm', 26, 4, 'enviou espiões e soube que Saul havia de fato chegado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7911, 'nvi', '1 Samuel', '1sm', 26, 5, 'Então Davi foi para o lugar onde Saul estava acampado. E viu o lugar onde Saul e Abner, filho de Ner, comandante de seu exército, haviam se deitado. Saul estava deitado no acampamento, com o exército acampado ao redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7912, 'nvi', '1 Samuel', '1sm', 26, 6, 'Davi então perguntou ao hitita Aimeleque e a Abisai, filho de Zeruia, irmão de Joabe: "Quem descerá comigo ao acampamento de Saul? " Disse Abisai: "Irei com você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7913, 'nvi', '1 Samuel', '1sm', 26, 7, 'Davi e Abisai entraram à noite no acampamento. Saul estava deitado, dormindo com sua lança fincada no chão, perto da cabeça. Abner e os soldados estavam deitados à sua volta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7914, 'nvi', '1 Samuel', '1sm', 26, 8, 'Abisai disse a Davi: "Hoje Deus entregou o seu inimigo nas suas mãos. Deixe-me, agora, cravar a lança nele até o chão com um só golpe; não precisarei de outro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7915, 'nvi', '1 Samuel', '1sm', 26, 9, 'Davi, contudo, disse a Abisai: "Não o mate! Quem pode levantar a mão contra o ungido do Senhor e permanecer inocente?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7916, 'nvi', '1 Samuel', '1sm', 26, 10, 'Juro pelo nome do Senhor", disse ele, "o Senhor mesmo o matará; ou chegará a sua hora e ele morrerá, ou ele irá para a batalha e perecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7917, 'nvi', '1 Samuel', '1sm', 26, 11, 'O Senhor me livre de levantar a mão contra seu ungido. Agora, vamos pegar a lança e o jarro com água que estão perto da cabeça dele, e vamos embora".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7918, 'nvi', '1 Samuel', '1sm', 26, 12, 'Então, Davi apanhou a lança e o jarro que estavam perto da cabeça de Saul, e eles foram embora. Ninguém os viu, ninguém percebeu e ninguém acordou. Estavam todos dormindo, pois um sono pesado vindo do Senhor havia caído sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7919, 'nvi', '1 Samuel', '1sm', 26, 13, 'Então Davi atravessou para o outro lado e colocou-se no topo da colina, ao longe, a uma boa distância deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7920, 'nvi', '1 Samuel', '1sm', 26, 14, 'E gritou para o exército e para Abner, filho de Ner: "Você não vai me responder, Abner? " Abner respondeu: "Quem é que está gritando para o rei? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7921, 'nvi', '1 Samuel', '1sm', 26, 15, 'Disse Davi: "Você é homem, não é? Quem é como você em Israel? Por que você não protegeu o rei, seu senhor? Alguém foi até aí para matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7922, 'nvi', '1 Samuel', '1sm', 26, 16, 'Não é bom isso que você fez! Juro pelo Senhor que todos vocês merecem morrer, pois não protegeram o seu rei, o ungido do Senhor. Agora, olhem! Onde estão a lança e o jarro de água do rei, que estavam perto da cabeça dele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7923, 'nvi', '1 Samuel', '1sm', 26, 17, 'Saul reconheceu a voz de Davi e disse: "É você, meu filho Davi? " Davi respondeu: "Sim, ó rei, meu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7924, 'nvi', '1 Samuel', '1sm', 26, 18, 'E acrescentou: "Por que meu senhor está perseguindo este seu servo? O que eu fiz e de que mal sou culpado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7925, 'nvi', '1 Samuel', '1sm', 26, 19, 'Que o rei, meu senhor, escute as palavras de seu servo. Se o Senhor o instigou contra mim, então que ele aceite uma oferta; se, porém, são homens que o fizeram, que sejam amaldiçoados perante o Senhor! Eles agora me afastaram de minha porção na herança do Senhor e disseram: ‘Vá, preste culto a outros deuses’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7926, 'nvi', '1 Samuel', '1sm', 26, 20, 'Agora, que meu sangue não caia sobre a terra, longe da presença do Senhor. O rei de Israel saiu à procura de uma pulga, como alguém que caça uma perdiz nos montes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7927, 'nvi', '1 Samuel', '1sm', 26, 21, 'Então Saul disse: "Pequei! Volte, meu filho Davi! Como hoje você considerou preciosa a minha vida, não lhe farei mal de novo. Tenho agido como um tolo e cometido um grande erro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7928, 'nvi', '1 Samuel', '1sm', 26, 22, 'Respondeu Davi: "Aqui está a lança do rei. Venha um de seus servos e a pegue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7929, 'nvi', '1 Samuel', '1sm', 26, 23, 'O Senhor recompensa a justiça e a fidelidade de cada um. Ele o entregou nas minhas mãos hoje, mas eu não levantaria a mão contra o ungido do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7930, 'nvi', '1 Samuel', '1sm', 26, 24, 'Assim como eu hoje considerei a sua vida de grande valor, que o Senhor também considere a minha vida e me livre de toda a angústia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7931, 'nvi', '1 Samuel', '1sm', 26, 25, 'Então Saul disse a Davi: "Seja você abençoado, meu filho Davi; você fará muitas coisas e em tudo será bem sucedido". Assim Davi prosseguiu seu caminho, e Saul voltou para casa.');
